    cylinder(h=height, r=radius, $fn=20);
}

// Body stripes
module stripe(height, radius, position) {
    translate([0, 0, position])
    color(stripe_color)
    cylinder(h=height, r=radius+0.5, $fn=20);
}

// Spike
module spike(length, angle, position) {
    translate([position[0], position[1], position[2]])
    rotate([0, angle, 0])
    color(spike_color)
    cylinder(h=length, r1=1.5, r2=0.5, $fn=8);
}

// Eye
module eye() {
    translate([3.5, 2.5, 8])
    color(eye_color)
    sphere(r=1.5, $fn=16);
}

// Horn
module horn() {
    translate([0, 0, 14])
    color(spike_color)
    cylinder(h=6, r1=2, r2=0.5, $fn=8);
}

// Leg
module leg(x_pos, y_pos, angle) {
    translate([x_pos, y_pos, 0])
    rotate([0, 0, angle])
    color(body_color)
    union() {
        // Upper leg
        translate([0, 0, 0])
        cylinder(h=4, r=1.2, $fn=8);

        // Lower leg
        translate([0, 0, -3.5])
        cylinder(h=3.5, r=0.8, $fn=8);

        // Foot
        translate([0, 0, -6.5])
        sphere(r=1, $fn=8);
    }
}

// Tail spike
module tail_spike() {
    translate([0, -6, 0])
    rotate([30, 0, 0])
    color(spike_color)
    cylinder(h=4, r1=2, r2=0.5, $fn=8);
}

// Main model
union() {
    // Body segments - front to back
    body_segment(4, 5, 10);
    body_segment(4, 5.5, 6);
    body_segment(4, 6, 2);
    body_segment(4, 6, -2);
    body_segment(3, 5.5, -5.5);
    body_segment(2, 4.5, -8.5);

    // Stripes
    stripe(1, 5.5, 9.5);
    stripe(1, 6.5, 5.5);
    stripe(1, 6.5, 1.5);
    stripe(1, 6, -2.5);

    // Spikes on back
    spike(3, -30, [3, 0, 10]);
    spike(3, -30, [0, 0, 6]);
    spike(3, -30, [-3, 0, 2]);
    spike(2.5, -30, [-2, 0, -2]);

    // Head features
    horn();

    // Eyes (both sides)
    eye();
    mirror([1, 0, 0])
    eye();

    // Legs - 3 pairs
    leg(3.5, 2, 45);
    leg(-3.5, 2, -45);

    leg(4, 0, 0);
    leg(-4, 0, 0);

    leg(3.5, -2, -45);
    leg(-3.5, -2, 45);

    // Tail spike
    tail_spike();
}

