module silcoon() {
    // Main body - cocoon shape using rotated spheres
    color("tan") {
        difference() {
            union() {
                // Central cocoon body
                rotate([0, 0, 0]) 
                    scale([1.2, 1, 1.1]) 
                        sphere(d=45);

                // Back extension for cocoon shape
                translate([15, 0, 0])
                    scale([1.5, 0.9, 0.9])
                        sphere(d=35);
            }

            // Hollow interior for 3D printing
            translate([5, 0, 0])
                scale([1.1, 0.9, 0.9])
                    sphere(d=40);
        }
    }

    // Silk wrapping bands
    color("white") {
        // Horizontal bands around cocoon
        for(i = [-15:10:15]) {
            rotate([90, 0, 0])
                translate([i, 0, 0])
                    scale([0.8, 1, 0.15])
                        cylinder(h=55, d=50);
        }

        // Diagonal crossing bands
        for(angle = [45, 135]) {
            rotate([0, angle, 0])
                translate([0, 0, -25])
                    scale([0.8, 0.15, 1])
                        cylinder(h=50, d=50);
        }
    }

    // Eye section
    color("red") {
        // Left eye
        translate([-10, 15, 12])
            rotate([0, -30, 0])
                scale([1, 1.2, 0.3])
                    sphere(d=8);

        // Right eye
        translate([-10, -15, 12])
            rotate([0, 30, 0])
                scale([1, 1.2, 0.3])
                    sphere(d=8);

        // Eye highlights
        color("white") {
            translate([-12, 15, 14])
                sphere(d=3);
            translate([-12, -15, 14])
                sphere(d=3);
        }
    }

    // Horn-like projections
    color("tan") {
        // Top horn
        translate([-5, 0, 25])
            rotate([0, 0, 0])
                cylinder(h=15, d1=8, d2=5);

        // Side horns
        for(side = [-1, 1]) {
            translate([0, 18*side, 15])
                rotate([0, 30*side, 0])
                    cylinder(h=12, d1=6, d2=3);
        }
    }

    // Leg stubs (visible parts of legs inside cocoon)
    color("tan") {
        for(pos = [[-15, 12, -15], [-15, -12, -15], [10, 8, -15], [10, -8, -15]]) {
            translate(pos)
                rotate([90, 0, 0])
                    cylinder(h=8, d=6);
        }
    }

    // Silk thread attachments
    color("white") {
        for(i = [0:45:315]) {
            rotate([0, 0, i])
                translate([0, 25, 0])
                    cylinder(h=3, d=2);
        }
    }

    // Subtle texture dots
    color("brown") {
        for(i = [0:30:330]) {
            for(j = [0:30:150]) {
                rotate([0, i, j])
                    translate([22, 0, 0])
                        sphere(d=1.5);
            }
        }
    }
}

