// Cascoon 3D Model - Realistic Style, Medium Complexity
// Scale: ~70mm diameter

// Colors
$fn = 50;

// Main body dimensions
BODY_RADIUS = 35;
SHELL_THICKNESS = 3;
SEGMENTS = 12;

// Module for shell segment
module shell_segment() {
    difference() {
        // Outer curve
        translate([0, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = BODY_RADIUS * 2, r1 = BODY_RADIUS, r2 = BODY_RADIUS * 0.9, center = true);

        // Inner hollow
        translate([0, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = BODY_RADIUS * 2 + 2, r1 = BODY_RADIUS - SHELL_THICKNESS, r2 = BODY_RADIUS * 0.9 - SHELL_THICKNESS, center = true);
    }
}

// Module for shell texture lines
module shell_lines() {
    for(i = [0:15]) {
        rotate([0, 0, i * 22.5]) {
            translate([0, 0, -BODY_RADIUS])
                rotate([90, 0, 0])
                    cylinder(h = BODY_RADIUS * 2, r1 = 0.5, r2 = 0.2);
        }
    }
}

// Main body shell
color([0.4, 0.3, 0.2]) {
    difference() {
        union() {
            // Create segmented shell
            for(i = [0:SEGMENTS-1]) {
                rotate([0, 0, i * 360/SEGMENTS]) {
                    rotate([0, 0, 0])
                        shell_segment();
                }
            }
        }

        // Front opening
        translate([0, 0, BODY_RADIUS - 5])
            sphere(r = BODY_RADIUS * 0.6);
    }
}

// Shell texture
color([0.3, 0.2, 0.15]) {
    shell_lines();
}

// Eyes
color([0.8, 0.1, 0.1]) {
    // Left eye
    translate([-10, -20, 25])
        sphere(r = 4);

    // Right eye
    translate([10, -20, 25])
        sphere(r = 4);
}

// Eye highlights
