    translate([-9, -19, 27])
        sphere(r = 1);
    translate([11, -19, 27])
        sphere(r = 1);
}

// Back spikes
color([0.35, 0.25, 0.15]) {
    for(i = [0:5]) {
        rotate([0, 0, i * 60]) {
            translate([0, -BODY_RADIUS + 5, 0])
                rotate([90, 0, 0])
                    cylinder(h = 8, r1 = 3, r2 = 0.5);
        }
    }
}

// Side spikes
color([0.35, 0.25, 0.15]) {
    for(i = [0:5]) {
        rotate([0, 0, 30 + i * 60]) {
            translate([0, -BODY_RADIUS * 0.7, -BODY_RADIUS * 0.5])
                rotate([45, 0, 0])
                    cylinder(h = 6, r1 = 2.5, r2 = 0.5);
        }
    }
}

// Top swirl pattern
