    for(i = [0:3]) {
        rotate([0, 0, i * 90]) {
            difference() {
                translate([0, 0, 30])
                    rotate([0, 0, 45])
                        cube([15, 3, 2], center = true);
                translate([0, 0, 31])
                    cube([20, 5, 4], center = true);
            }
        }
    }
}

