// Color definitions
$fa = 2;
$fs = 0.5;

// Main body color
body_color = "#BBA26B";
// Darker body accents
accent_color = "#8C7A4D";
// Nose and mouth color
nose_color = "#654321";
// Eye color
eye_color = "#2E4057";
// Eye highlight
highlight_color = "#FFFFFF";

// Scale factor for 3D printing - roughly 80mm tall
scale_factor = 1.5;

// Main body module
module slakoth_body() {
    color(body_color) {
        // Central torso
        translate([0, 0, 0])
            scale([1.2, 1.0, 1.3])
            sphere(r=15);

        // Belly bulge
        translate([0, -10, -5])
            scale([0.9, 0.7, 0.8])
            sphere(r=15);
    }
}

// Head module with facial features
module slakoth_head() {
    color(body_color) {
        // Main head shape
        translate([0, 20, 15])
            scale([1.0, 0.9, 0.8])
            sphere(r=18);

        // Snout/nose area
        translate([0, 30, 10])
            scale([0.7, 0.6, 0.5])
            sphere(r=15);
    }

    // Nose
    color(nose_color)
        translate([0, 35, 12])
            scale([1, 1.5, 0.8])
            sphere(r=3);

    // Eyes
    color(eye_color) {
        translate([-10, 25, 20])
            sphere(r=4);
        translate([10, 25, 20])
            sphere(r=4);
    }

    // Eye highlights
    color(highlight_color) {
        translate([-9, 26, 22])
            scale([0.5, 0.5, 0.5])
            sphere(r=2);
        translate([11, 26, 22])
            scale([0.5, 0.5, 0.5])
            sphere(r=2);
    }

    // Mouth line
    color(nose_color)
        translate([0, 32, 8])
            rotate([90, 0, 0])
            cylinder(h=0.5, r1=3, r2=3);
}

// Ear module
module slakoth_ear(side) {
    color(body_color) {
        translate([side * 15, 15, 25])
            rotate([0, side * -20, 0])
            scale([0.3, 0.3, 1.2])
            cylinder(h=20, r1=8, r2=5);
    }

    // Inner ear
    color(accent_color)
        translate([side * 15, 15, 25])
            rotate([0, side * -20, 0])
            scale([0.2, 0.2, 1.0])
            cylinder(h=18, r1=6, r2=3);
}

// Arm module
module slakoth_arm(side) {
    color(body_color) {
        // Upper arm
        translate([side * 20, 0, 0])
            rotate([90, 0, side * 15])
            scale([0.8, 0.8, 1])
            cylinder(h=20, r1=6, r2=4);

        // Forearm
        translate([side * 25, -15, -5])
            rotate([90, 0, side * 30])
            scale([0.6, 0.6, 1])
            cylinder(h=15, r1=5, r2=4);

        // Hand
        translate([side * 28, -25, -10])
            sphere(r=6);
    }

    // Claws
    color(nose_color)
    for(i = [-1, 0, 1]) {
        translate([side * (28 + i * 2), -28, -10])
            scale([0.5, 0.5, 1.5])
            sphere(r=2);
    }
}

// Leg module
module slakoth_leg(side) {
    color(body_color) {
        // Upper leg
        translate([side * 12, -15, -15])
            rotate([0, side * -15, 0])
            scale([0.9, 0.9, 1])
            cylinder(h=18, r1=7, r2=5);

        // Lower leg
        translate([side * 10, -15, -30])
            rotate([0, side * -15, 0])
            scale([0.7, 0.7, 1])
            cylinder(h=15, r1=5, r2=4);

        // Foot
        translate([side * 8, -15, -42])
            scale([1.5, 0.8, 0.5])
            sphere(r=8);
    }
}

// Back stripe pattern
module slakoth_stripes() {
    color(accent_color) {
        for(i = [1, 2, 3]) {
            translate([0, 10 - i * 8, 10 - i * 5])
                scale([0.8, 0.1, 0.3])
                sphere(r=15);
        }
    }
}

// Main assembly
scale([scale_factor, scale_factor, scale_factor]) {
    union() {
        // Core body parts
        slakoth_body();
        slakoth_head();

        // Ears
        slakoth_ear(-1);  // Left ear
        slakoth_ear(1);   // Right ear

        // Arms
        slakoth_arm(-1);  // Left arm
        slakoth_arm(1);   // Right arm

        // Legs
        slakoth_leg(-1);  // Left leg
        slakoth_leg(1);   // Right leg

        // Back details
        slakoth_stripes();
    }
}

