color_wings = [0.9, 0.9, 0.9];    // White wings
color_eyes = [0.2, 0.2, 0.8];     // Blue eyes
color_markings = [0.4, 0.4, 0.4]; // Dark gray markings

// Overall scale
scale_factor = 0.8;

module ninjask_body() {
    color(color_body)
    union() {
        // Main thorax/abdomen
        translate([0, 0, 5])
            scale([1, 0.8, 1.5])
            sphere(d=20);

        // Head segment
        translate([0, 0, 25])
            scale([0.9, 0.9, 1.1])
            sphere(d=15);

        // Neck connector
        translate([0, 0, 18])
            cylinder(h=7, r1=6, r2=5);
    }
}

module ninjask_wings() {
    color(color_wings)
    for(i = [0, 1]) {
        rotate([0, 0, i*180])
        translate([0, 12, 15]) {
            // Upper wing
            rotate([10, 0, 0])
            difference() {
                scale([4, 0.5, 2])
                sphere(d=10);
                translate([0, -2, 0])
                scale([3.5, 1, 1.8])
                sphere(d=9);
            }

            // Lower wing
            translate([0, 4, -6])
            rotate([-10, 0, 0])
            difference() {
                scale([3.5, 0.4, 1.8])
                sphere(d=8);
                translate([0, -1.5, 0])
                scale([3, 0.8, 1.5])
                sphere(d=7);
            }
        }
    }
}

module ninjask_legs() {
    color(color_body)
    for(i = [0:3]) {
        angle = i * 90;
        rotate([0, 0, angle])
        translate([8, 0, 8]) {
            // Upper leg
            rotate([30, 0, 0])
            cylinder(h=8, r=2);

            // Lower leg
            translate([0, 0, -8])
            rotate([45, 0, 0])
            cylinder(h=6, r=1.5);

            // Foot
            translate([0, 0, -14])
            sphere(d=3);
        }
    }
}

module ninjask_eyes() {
    color(color_eyes)
    for(side = [-1, 1]) {
        translate([side * 5, 7, 24]) {
            // Eye sphere
            sphere(d=4);

            // Eye highlight
            color([1,1,1])
            translate([1, 0.5, 1])
            sphere(d=1.2);
        }
    }
}

module ninjask_markings() {
    color(color_markings)
    // Back markings
    translate([0, -6, 15])
    rotate([0, 0, 0])
    scale([0.8, 0.2, 2])
    cylinder(h=12, r=8);

    // Side markings
    for(side = [-1, 1]) {
        translate([side * 7, 0, 18])
        rotate([0, side * 15, 0])
        scale([0.3, 0.8, 1.2])
        cylinder(h=8, r=3);
    }
}

module ninjask_complete() {
    scale([scale_factor, scale_factor, scale_factor])
    union() {
        ninjask_body();
        ninjask_wings();
        ninjask_legs();
        ninjask_eyes();
        ninjask_markings();
    }
}

