// Shedinja - Realistic Style, Medium Complexity
// 3D printable model with proper wall thickness and no overhangs

$fn = 50; // Smooth curves
wall_thickness = 2;

// Main body shell
module body_shell() {
    color("saddlebrown")
    difference() {
        // Outer shell
        sphere(d = 60);

        // Hollow interior
        translate([0, 0, -wall_thickness])
        sphere(d = 60 - 2*wall_thickness);

        // Front opening
        translate([0, 0, 15])
        rotate([90, 0, 0])
        cylinder(h = 30, d1 = 35, d2 = 30, center = true);
    }
}

// Back shell with wings
module back_shell() {
    color("saddlebrown")
    union() {
        // Main back
        difference() {
            translate([0, 0, -30])
            sphere(d = 60);

            // Hollow interior
            translate([0, 0, -30])
            sphere(d = 60 - 2*wall_thickness);

            // Front cut for body connection
            translate([0, 0, -45])
            cube([80, 80, 30], center = true);
        }

        // Wings (3 wings)
        for(i = [0:2]) {
            rotate([0, 0, i*120])
            translate([30, 0, -15])
            rotate([0, 90, -30])
            color("burlywood")
            difference() {
                // Wing shape
                scale([1, 0.3, 1])
                sphere(d = 25);

                // Wing hollow
                scale([0.7, 0.3, 0.7])
                sphere(d = 25);
            }
        }
    }
}

// Halo/crown
module halo() {
    color("gold")
    rotate([90, 0, 0])
    translate([0, 0, 30])
    difference() {
        // Outer halo
        torus(20, 3);

        // Inner cut
        torus(19, 2.5);
    }
}

// Eye holes (4 holes)
module eye_holes() {
    color("black")
    for(pos = [[-12, 10, 25], [12, 10, 25], [-8, -5, 25], [8, -5, 25]]) {
        translate(pos)
        sphere(d = 4);
    }
}

// Back pattern (3 circles)
module back_pattern() {
    color("burlywood")
    for(pos = [[0, -20, -25], [-15, 5, -25], [15, 5, -25]]) {
        translate(pos)
        sphere(d = 8);
    }
}

// Leg stumps (3 stumps)
module leg_stumps() {
    color("saddlebrown")
    for(i = [0:2]) {
        rotate([0, 0, i*120 + 60])
        translate([18, 0, -25])
        rotate([90, 0, 0])
        cylinder(h = 10, d1 = 8, d2 = 6);
    }
}

// Assembly
union() {
    // Main body
    body_shell();

    // Back shell
    back_shell();

    // Halo
    halo();

    // Eyes
    eye_holes();

    // Back pattern
    back_pattern();

    // Leg stumps
    leg_stumps();
}

