        for (x = [-15, 15]) {
            translate([x * scale_factor, 0, -18 * scale_factor])
                rotate([30, 0, 0])
                    cylinder(h = 10 * scale_factor, r1 = 8 * scale_factor, r2 = 6 * scale_factor, $fn = 16);
        }

        // Arms
        for (x = [-20, 20]) {
            translate([x * scale_factor, 0, 5 * scale_factor])
                rotate([0, 90, 0])
                    cylinder(h = 12 * scale_factor, r1 = 6 * scale_factor, r2 = 4 * scale_factor, $fn = 16);
        }
