// Nosepass OpenSCAD Model - Realistic Style, Medium Complexity
// Based on the Rock-type Pokemon with magnetic nose

// Main parameters
nosepass_height = 70;
wall_thickness = 2;

// Colors
body_color = [0.5, 0.5, 0.5];  // Gray stone
nose_color = [0.8, 0.4, 0.4];  // Red magnetic nose
eye_color = [0.9, 0.9, 0.9];   // White
pupil_color = [0.1, 0.1, 0.1]; // Black
detail_color = [0.4, 0.4, 0.4]; // Darker gray details

// Main body - hexagonal prism shape
module body() {
    color(body_color)
    difference() {
        // Main hexagonal prism
        cylinder(h=nosepass_height * 0.7, r1=nosepass_height * 0.35, r2=nosepass_height * 0.3, $fn=6);

        // Hollow out center
        translate([0, 0, -1])
            cylinder(h=nosepass_height * 0.7 + 2, r1=nosepass_height * 0.35 - wall_thickness, r2=nosepass_height * 0.3 - wall_thickness, $fn=6);
    }
}

// Magnetic nose - protruding rectangular shape
module magnetic_nose() {
    color(nose_color)
    translate([0, nosepass_height * 0.32, nosepass_height * 0.4])
    rotate([0, 10, 0])
    difference() {
        // Main nose shape
        cube([nosepass_height * 0.5, nosepass_height * 0.15, nosepass_height * 0.25], center=true);

        // Add bevel edges
        translate([0, 0, nosepass_height * 0.12])
        cube([nosepass_height * 0.45, nosepass_height * 0.1, nosepass_height * 0.1], center=true);
    }
}

// Eye module
module eye(x_pos, y_pos, z_pos) {
    translate([x_pos, y_pos, z_pos]) {
        // Eye white
        color(eye_color)
        sphere(r=nosepass_height * 0.06);

        // Pupil
        color(pupil_color)
        translate([nosepass_height * 0.02, 0, nosepass_height * 0.04])
        sphere(r=nosepass_height * 0.03);
    }
}

// Ear-like protrusions (mini magnets)
module ear(x_pos, angle) {
    color(body_color)
    translate([x_pos, nosepass_height * 0.25, nosepass_height * 0.6])
    rotate([0, angle, 0])
    difference() {
        // Main ear shape
        cube([nosepass_height * 0.12, nosepass_height * 0.08, nosepass_height * 0.25]);

        // Hollow
        translate([wall_thickness, wall_thickness, wall_thickness])
        cube([nosepass_height * 0.12 - wall_thickness*2, nosepass_height * 0.08 - wall_thickness*2, nosepass_height * 0.25 - wall_thickness]);
    }
}

// Rock texture details
module rock_texture() {
    color(detail_color)
    for(i = [0:5]) {
        rotate([0, 0, i * 60])
        translate([nosepass_height * 0.32, 0, nosepass_height * 0.35])
        cube([nosepass_height * 0.08, wall_thickness, nosepass_height * 0.05]);
    }
}

// Base platform
module base() {
    color(body_color)
    difference() {
        cylinder(h=nosepass_height * 0.1, r=nosepass_height * 0.4, $fn=6);
        translate([0, 0, -1])
        cylinder(h=nosepass_height * 0.1 + 2, r=nosepass_height * 0.4 - wall_thickness, $fn=6);
    }
}

// Main assembly
union() {
    // Base
    base();

    // Body
    translate([0, 0, nosepass_height * 0.1])
    body();

    // Magnetic nose
    magnetic_nose();

    // Eyes
    eye(-nosepass_height * 0.12, nosepass_height * 0.2, nosepass_height * 0.5);
    eye(nosepass_height * 0.12, nosepass_height * 0.2, nosepass_height * 0.5);

    // Ears
    ear(-nosepass_height * 0.25, 30);
    ear(nosepass_height * 0.25, 30);

    // Rock texture details
    rock_texture();
}

