// Torkoal OpenSCAD Model - Realistic Style, Medium Complexity
// Size: ~70mm shell diameter

// Main colors
shell_color = [0.4, 0.2, 0.1];  // Dark brown
body_color = [0.3, 0.25, 0.2];  // Dark gray
smoke_color = [0.2, 0.2, 0.2];  // Dark gray smoke
eye_color = [0.9, 0.9, 0.1];    // Yellow
highlight_color = [1, 1, 1];    // White

// Shell segments
module shell_segment(radius, height, angle) {
    rotate([0, 0, angle])
    translate([0, radius * 0.8, 0])
    rotate([0, 90, 0])
    cylinder(h=height * 1.2, r1=height * 0.6, r2=height * 0.4, $fn=6);
}

// Main shell
module main_shell() {
    color(shell_color) {
        difference() {
            sphere(d=70, $fn=48);

            // Shell segments pattern
            for(i = [0:30:359]) {
                shell_segment(35, 8, i);
            }

            // Bottom opening
            translate([0, 0, -40])
            cylinder(h=80, r=25, $fn=32);
        }

        // Shell ridges
        for(i = [0:45:359]) {
            rotate([0, 0, i])
            translate([0, 30, 10])
            rotate([90, 0, 0])
            cylinder(h=20, r1=8, r2=4, $fn=6);
        }
    }
}

// Head
module head() {
    color(body_color) {
        translate([0, -40, 10])
        rotate([0, 0, 0])
        scale([1, 1.2, 0.8])
        sphere(d=25, $fn=24);

        // Snout
        translate([0, -50, 5])
        rotate([0, 0, 0])
        scale([0.8, 1, 0.6])
        cylinder(h=15, r=8, $fn=16);

        // Eyes
        color(eye_color) {
            translate([-8, -45, 12])
            sphere(d=6, $fn=16);
            translate([8, -45, 12])
            sphere(d=6, $fn=16);

            color(highlight_color) {
                translate([-7, -46, 13.5])
                sphere(d=2, $fn=8);
                translate([9, -46, 13.5])
                sphere(d=2, $fn=8);
            }
        }

        // Nostrils
        color([0.1, 0.1, 0.1])
        for(x = [-3, 3]) {
            translate([x, -52, 6])
            rotate([90, 0, 0])
            cylinder(h=3, r=1.5, $fn=8);
        }
    }
}

// Legs
module leg(pos_x, pos_y, angle) {
    color(body_color) {
        translate([pos_x, pos_y, -35])
        rotate([0, 0, angle])
        union() {
            // Upper leg
            translate([0, 0, 5])
            cylinder(h=15, r=5, $fn=8);

            // Lower leg
            translate([0, 0, -5])
            cylinder(h=15, r=4, $fn=8);

            // Foot
            translate([0, 0, -15])
            scale([1.5, 1, 0.5])
            sphere(d=10, $fn=8);
        }
    }
}

// Smoke stack
module smoke_stack() {
    color(smoke_color) {
        translate([0, -10, 45])
        union() {
            // Base
            cylinder(h=10, r=8, $fn=16);

            // Middle section
            translate([0, 0, 10])
            cylinder(h=15, r=6, $fn=16);

            // Top opening
            translate([0, 0, 25])
            cylinder(h=5, r=7, $fn=16);

            // Smoke rings
            for(i = [1:3]) {
                translate([0, 0, 30 + i * 8])
                rotate([0, 0, i * 30])
                scale([1 + i * 0.2, 1 + i * 0.2, 0.3])
                torus(3, 0.5);
            }
        }
    }
}

// Helper for smoke rings
module torus(outer, inner) {
    rotate_extrude($fn=32)
    translate([outer, 0, 0])
    circle(r=inner, $fn=16);
}

// Tail
module tail() {
    color(shell_color) {
        translate([0, 45, -5])
        rotate([0, 25, 0])
        union() {
            // Base
            cylinder(h=15, r=8, $fn=12);

            // Segments
            for(i = [1:3]) {
                translate([0, 0, i * 8])
                rotate([0, 10 * i, 0])
                scale([0.9, 0.9, 1])
                cylinder(h=8, r=6, $fn=12);
            }

            // Tip
            translate([0, 0, 35])
            sphere(d=10, $fn=12);
        }
    }
}

// Assembly
translate([0, 0, 35])
union() {
    main_shell();
    head();

    // Legs
    leg(-20, -20, -30);
    leg(20, -20, 30);
    leg(-25, 20, -30);
    leg(25, 20, 30);

    smoke_stack();
    tail();
}

