// Vibrava - Realistic Style, Medium Complexity
// Approximately 80mm tall when printed

// Colors
$vibrava_green = [34/255,139/255,34/255];
$vibrava_light_green = [144/255,238/255,144/255];
$vibrava_yellow = [255/255,215/255,0/255];
$vibrava_red = [220/255,20/255,60/255];
$vibrava_black = [25/255,25/255,25/255];

// Overall scale factor
scale_factor = 0.8;

module vibrava() {
    scale([scale_factor, scale_factor, scale_factor]) {
        union() {
            // Main body
            color($vibrava_green) body();

            // Head features
            color($vibrava_green) head();
            color($vibrava_light_green) eyes();
            color($vibrava_black) mouth();

            // Wings
            color($vibrava_light_green) wings();

            // Legs
            color($vibrava_green) legs();

            // Tail segments
            color($vibrava_green) tail();
        }
    }
}

module body() {
    // Main thorax
    translate([0, 0, 25]) {
        difference() {
            scale([1.2, 1, 1.5]) sphere(d=30, $fn=32);
            translate([0, 0, -20]) cube([40, 40, 40], center=true);
        }
    }

    // Abdomen segments
    for(i = [1:3]) {
        translate([0, 0, 15 - i*8]) {
            scale([1.1 - i*0.1, 1 - i*0.05, 0.8]) 
                sphere(d=25 - i*2, $fn=24);
        }
    }
}

module head() {
    // Main head
    translate([0, 20, 35]) {
        rotate([15, 0, 0]) {
            scale([1.1, 1.3, 1]) sphere(d=25, $fn=32);
        }
    }

    // Antennae
    for(side = [-1, 1]) {
        translate([side*12, 28, 42]) {
            rotate([0, 30, side*15]) {
                cylinder(h=15, r1=2, r2=1, $fn=8);
            }
        }
    }

    // Head ridges
    translate([0, 22, 45]) {
        for(i = [-1, 0, 1]) {
            translate([i*5, 0, 0]) {
                scale([2, 1, 3]) cube([3, 2, 6], center=true);
            }
        }
    }
}

module eyes() {
    // Large compound eyes
    for(side = [-1, 1]) {
        translate([side*10, 25, 38]) {
            scale([1.2, 1.2, 1]) sphere(d=12, $fn=20);

            // Eye details
            translate([0, 0, 5]) {
                color($vibrava_red) sphere(d=6, $fn=16);
            }
        }
    }
}

module mouth() {
    // Mandibles
    translate([0, 35, 30]) {
        rotate([0, 25, 0]) {
            cube([15, 3, 8], center=true);
        }
    }

    // Lower jaw
    translate([0, 32, 25]) {
        rotate([15, 0, 0]) {
            cube([12, 4, 6], center=true);
        }
    }
}

module wings() {
    // Elytra (wing covers)
    for(side = [-1, 1]) {
        translate([side*15, 0, 35]) {
            rotate([0, 0, side*20]) {
                // Main wing cover
                scale([1.5, 3, 0.5]) {
                    difference() {
                        cube([8, 20, 2], center=true);
                        // Wing texture lines
                        for(i = [1:4]) {
                            translate([0, -10 + i*5, 0]) {
                                cube([7.5, 0.5, 2.1], center=true);
                            }
                        }
                    }
                }

                // Wing membrane (simplified)
                translate([0, -25, -3]) {
                    rotate([10, 0, 0]) {
                        scale([1.2, 1.5, 0.1]) 
                            sphere(d=15, $fn=16);
                    }
                }
            }
        }
    }
}

module legs() {
    // Front legs
    for(side = [-1, 1]) {
        translate([side*12, 10, 20]) {
            rotate([0, 0, side*30]) {
                cylinder(h=15, r=2, $fn=8);
                translate([0, 0, -15]) {
                    rotate([90, 0, 0]) {
                        cylinder(h=10, r=1.5, $fn=6);
                    }
                }
            }
        }
    }

    // Middle legs
    for(side = [-1, 1]) {
        translate([side*15, 0, 15]) {
            rotate([0, 0, side*45]) {
                cylinder(h=18, r=2, $fn=8);
                translate([0, 0, -18]) {
                    rotate([90, 0, 0]) {
                        cylinder(h=12, r=1.5, $fn=6);
                    }
                }
            }
        }
    }

    // Rear legs
    for(side = [-1, 1]) {
        translate([side*12, -10, 10]) {
            rotate([0, 0, side*60]) {
                cylinder(h=20, r=2.5, $fn=8);
                translate([0, 0, -20]) {
                    rotate([90, 0, 0]) {
                        cylinder(h=15, r=2, $fn=6);
                    }
                }
            }
        }
    }
}

module tail() {
    // Tail segments with increasing detail
    for(i = [1:5]) {
        translate([0, -15 - i*8, 25 - i*3]) {
            rotate([0, 10, 0]) {
                scale([1 - i*0.15, 1 - i*0.1, 1 - i*0.1]) {
                    sphere(d=20 - i*3, $fn=24);

                    // Tail spikes
                    if(i <= 3) {
                        for(side = [-1, 1]) {
                            translate([side*(8 - i), 0, 0]) {
                                rotate([0, side*20, 0]) {
                                    cylinder(h=5 + i, r1=2, r2=0.5, $fn=6);
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

