color_cloud = [1, 1, 1];
color_eyes = [0, 0, 0];
color_beak = [1, 0.8, 0.2];

module cloud_puff(radius, height) {
    difference() {
        sphere(radius);
        translate([0, 0, -radius*0.8])
            cube([radius*2.2, radius*2.2, radius*1.2], center=true);
    }
}

module altaria_body() {
    color(color_body)
    union() {
        // Main body
        scale([1, 1.2, 1.3])
            sphere(12 * scale_factor);

        // Neck
        translate([0, 8 * scale_factor, 8 * scale_factor])
            rotate([0, -30, 0])
                cylinder(h=15 * scale_factor, r1=6 * scale_factor, r2=4 * scale_factor);

        // Head
        translate([0, 15 * scale_factor, 18 * scale_factor])
            sphere(7 * scale_factor);
    }
}

module altaria_wings() {
    color(color_wings)
    union() {
        // Left wing
        translate([12 * scale_factor, 0, 8 * scale_factor])
            rotate([0, 0, -30])
                scale([1, 2.5, 0.3])
                    sphere(8 * scale_factor);

        // Right wing
        translate([-12 * scale_factor, 0, 8 * scale_factor])
            rotate([0, 0, 30])
                scale([1, 2.5, 0.3])
                    sphere(8 * scale_factor);
    }
}

module altaria_tail() {
    color(color_body)
    union() {
        // Tail base
        translate([0, -15 * scale_factor, -5 * scale_factor])
            rotate([-20, 0, 0])
                cylinder(h=20 * scale_factor, r1=5 * scale_factor, r2=3 * scale_factor);

        // Tail feathers
        for(i = [0:3]) {
            rotate([0, 0, i * 90])
                translate([0, -30 * scale_factor, -8 * scale_factor])
                    rotate([30, 0, 0])
                        scale([0.4, 1.5, 0.1])
                            sphere(8 * scale_factor);
        }
    }
}

module altaria_clouds() {
    color(color_cloud)
    union() {
        // Front cloud puffs
        translate([8 * scale_factor, 5 * scale_factor, -10 * scale_factor])
            cloud_puff(8 * scale_factor, 5 * scale_factor);
        translate([-8 * scale_factor, 5 * scale_factor, -10 * scale_factor])
            cloud_puff(8 * scale_factor, 5 * scale_factor);

        // Back cloud puffs
        translate([10 * scale_factor, -5 * scale_factor, -8 * scale_factor])
            cloud_puff(7 * scale_factor, 4 * scale_factor);
        translate([-10 * scale_factor, -5 * scale_factor, -8 * scale_factor])
            cloud_puff(7 * scale_factor, 4 * scale_factor);
    }
}

module altaria_face() {
    union() {
        // Eyes
        color(color_eyes)
        union() {
            translate([2.5 * scale_factor, 17 * scale_factor, 21 * scale_factor])
                sphere(1.5 * scale_factor);
            translate([-2.5 * scale_factor, 17 * scale_factor, 21 * scale_factor])
                sphere(1.5 * scale_factor);
        }

        // Beak
        color(color_beak)
        translate([0, 19 * scale_factor, 19 * scale_factor])
            rotate([30, 0, 0])
                cylinder(h=4 * scale_factor, r1=2.5 * scale_factor, r2=1 * scale_factor);

        // Ears
        color(color_body)
        union() {
            translate([3 * scale_factor, 13 * scale_factor, 23 * scale_factor])
                rotate([0, -30, 45])
                    cylinder(h=6 * scale_factor, r1=2 * scale_factor, r2=0.5 * scale_factor);
            translate([-3 * scale_factor, 13 * scale_factor, 23 * scale_factor])
                rotate([0, -30, -45])
                    cylinder(h=6 * scale_factor, r1=2 * scale_factor, r2=0.5 * scale_factor);
        }
    }
}

// Assembly
