// Main body with realistic rock texture
module lunatone_body() {
    color(rock_color) {
        difference() {
            // Main spherical body
            sphere(d = 80, $fn = 64);

            // Create rock-like surface with boolean differences
            for(i = [0:15]) {
                rotate([rand(i, 1) * 360, rand(i, 2) * 180, 0])
                    translate([0, 0, rand(i, 3) * 5 - 2.5])
                        sphere(d = rand(i, 4) * 8 + 5, $fn = 16);
            }
        }
    }
}

// Crater details on surface
module craters() {
    color(crater_color) {
        difference() {
            sphere(d = 80.5, $fn = 64);

            // Large craters
            translate([20, 15, 30]) rotate([0, 0, 45]) 
                cylinder(h = 10, d1 = 12, d2 = 8, $fn = 16);
            translate([-25, -10, 28]) rotate([0, 0, -30]) 
                cylinder(h = 8, d1 = 10, d2 = 6, $fn = 16);
            translate([0, -30, 25]) rotate([0, 0, 60]) 
                cylinder(h = 12, d1 = 15, d2 = 10, $fn = 16);

            // Small craters
            for(i = [0:8]) {
                rotate([rand(i, 5) * 360, rand(i, 6) * 180, 0])
                    translate([rand(i, 7) * 35 - 17.5, rand(i, 8) * 35 - 17.5, 38]) 
                        sphere(d = rand(i, 9) * 4 + 2, $fn = 8);
            }
        }
    }
}

// Eye module with realistic depth
module eye(x_pos, y_pos, z_pos) {
    // Main eye socket
    color(eye_color) {
        translate([x_pos, y_pos, z_pos]) {
            // Eye socket cavity
            difference() {
                sphere(d = 18, $fn = 32);
                translate([0, 0, 2]) sphere(d = 14, $fn = 32);
            }

            // Eye ball
            translate([0, 0, 0.5]) {
                sphere(d = 12, $fn = 32);

                // Eye highlight
                color(highlight_color) {
                    translate([3, 2, 4]) sphere(d = 3, $fn = 16);
                }
            }
        }
    }
}

// Crescent moon marking
module moon_marking() {
    color([0.8, 0.75, 0.7]) {
        translate([0, 0, 39]) {
            rotate([0, 0, 90]) {
                difference() {
                    // Crescent base
                    cylinder(h = 3, d = 30, $fn = 32);

                    // Cut out center
                    translate([5, 0, -1]) cylinder(h = 5, d = 25, $fn = 32);

                    // Taper ends
                    translate([0, 15, 1.5]) rotate([90, 0, 0]) 
                        cylinder(h = 8, d1 = 8, d2 = 4, $fn = 16);
                    translate([0, -15, 1.5]) rotate([-90, 0, 0]) 
                        cylinder(h = 8, d1 = 8, d2 = 4, $fn = 16);
                }
            }
        }
    }
}

// Random number generator for texture
function rand(seed, offset) = (seed * 9301 + 49297 * offset) % 233280 / 233280;

// Assembly
union() {
    lunatone_body();
    craters();
    eye(-15, 10, 35);
    eye(15, 10, 35);
    moon_marking();
}

