    union() {
        // Body segments
        for(i = [0:4]) {
            translate([i*8, 0, 0])
            scale([1.5 - i*0.2, 1.5 - i*0.2, 1.5 - i*0.2])
            sphere(10);
        }

        // Head - larger sphere
        translate([-8, 0, 0])
        scale([1.3, 1.4, 1.2])
        sphere(10);
    }

    // Whiskers - three on each side
    color([0.3, 0.2, 0.8])
    for(side = [-1, 1]) {
        // Top whisker
        translate([-5, side*8, 4])
        rotate([0, 90, side*20])
        cylinder(h = 12, r1 = 1.2, r2 = 0.5);

        // Middle whisker
        translate([-5, side*9, 2])
        rotate([0, 90, side*30])
        cylinder(h = 15, r1 = 1.2, r2 = 0.5);

        // Bottom whisker
        translate([-5, side*8, 0])
        rotate([0, 90, side*40])
        cylinder(h = 12, r1 = 1.2, r2 = 0.5);
    }

    // Tail fin - split into two parts
    color([0.5, 0.4, 1])
    translate([32, 0, -2])
    rotate([0, 0, 0])
    union() {
        // Upper tail lobe
        translate([0, 0, 2])
        rotate([20, 0, 0])
        scale([1.5, 0.3, 1])
        difference() {
            cube([12, 8, 6], center = true);
            translate([6, 0, 0])
            sphere(4);
        }

        // Lower tail lobe
        translate([0, 0, -2])
        rotate([-20, 0, 0])
        scale([1.5, 0.3, 1])
        difference() {
            cube([12, 8, 6], center = true);
            translate([6, 0, 0])
            sphere(4);
        }
    }

    // Dorsal fin - small triangles along back
    color([0.45, 0.35, 0.95])
    for(i = [0:3]) {
        translate([i*8 + 5, 0, 8])
        rotate([0, 0, 0])
        difference() {
            cube([4, 6, 4], center = true);
            translate([2, 0, 2])
            sphere(3);
        }
    }

    // Eyes - large round eyes
    color([1, 1, 1])
    translate([-12, 4, 2])
    sphere(3);

    translate([-12, -4, 2])
    sphere(3);

    // Eye pupils
    color([0, 0, 0])
    translate([-13, 4, 3])
    sphere(1.5);

    translate([-13, -4, 3])
    sphere(1.5);

    // Mouth - small curved line
    color([0.2, 0.1, 0.7])
    translate([-12, 0, -3])
    rotate([90, 0, 0])
    difference() {
        cylinder(h = 1, r = 2, center = true);
        translate([0, 1, 0])
        cube([4, 2, 2], center = true);
    }

    // Side fins - pectoral fins
    color([0.4, 0.3, 0.9])
    for(side = [-1, 1]) {
        translate([5, side*7, -2])
        rotate([0, side*30, 0])
        scale([1, 0.2, 0.8])
        sphere(5);
    }

    // Fin markings - dark blue stripes
    color([0.2, 0.1, 0.7])
    for(i = [1:3]) {
        translate([i*8, 0, 7])
        rotate([90, 0, 0])
        cylinder(h = 0.5, r = 6 - i*0.5);
    }
}

