// Realistic Crawdaunt OpenSCAD model
// Size: ~70mm tall, 3D printable

// Colors
color_body = [0.7, 0.1, 0.1];      // Dark red
color_shell = [0.4, 0.2, 0.2];     // Dark brown
color_claws = [0.9, 0.3, 0.2];     // Orange-red
color_eyes = [0.1, 0.1, 0.1];      // Black
color_markings = [1, 0.8, 0.2];    // Yellow

$fn = 32;

// Main body
module crawdaunt() {
    // Scale to 70mm height
    scale([0.7, 0.7, 0.7]) {

        // Main shell body
        color(color_shell)
        translate([0, 0, 10])
        scale([1.2, 1, 1])
        sphere(15);

        // Lower body segment
        color(color_body)
        translate([0, 0, 5])
        scale([0.8, 0.8, 0.6])
        sphere(12);

        // Head section
        color(color_shell)
        translate([0, 0, 25])
        scale([0.9, 0.9, 0.7])
        sphere(10);

        // Eyes (left and right)
        color(color_eyes)
        for(side = [-1, 1]) {
            translate([side * 6, 12, 27])
            sphere(2);
        }

        // Yellow star marking on forehead
        color(color_markings)
        translate([0, 8, 32])
        scale([1, 0.8, 0.2])
        cylinder(h=1, r1=4, r2=0);

        // Left claw
        color(color_claws)
        translate([-20, 0, 20])
        rotate([0, 0, 45])
        claw_assembly();

        // Right claw
        color(color_claws)
        translate([20, 0, 20])
        rotate([0, 0, -45])
        mirror([1, 0, 0])
        claw_assembly();

        // Legs
        color(color_body)
        for(side = [-1, 1]) {
            translate([side * 8, 0, 8])
            rotate([0, 0, side * 30])
            leg();
        }

        // Rear legs
        for(side = [-1, 1]) {
            translate([side * 6, -8, 5])
            rotate([0, 0, side * 45])
            leg();
        }

        // Tail segments
        tail_assembly();

        // Antennae
        color(color_shell)
        for(side = [-1, 1]) {
            translate([side * 5, 10, 30])
            rotate([side * 20, 0, 0])
            cylinder(h=8, r1=1, r2=0.5);
        }
    }
}

// Claw assembly
module claw_assembly() {
    // Upper claw
    translate([0, 0, 0])
    rotate([0, 90, 0])
    scale([1, 1, 0.6])
    cylinder(h=15, r1=3, r2=8);

    // Lower claw
    translate([0, 0, -5])
    rotate([0, 90, 0])
    scale([1, 1, 0.6])
    cylinder(h=12, r1=2.5, r2=6);

    // Claw joint
    sphere(4);
}

// Leg module
module leg() {
    translate([0, 0, 0])
    rotate([90, 0, 0])
    cylinder(h=8, r1=2, r2=1);
}

// Tail assembly
module tail_assembly() {
    color(color_body)
    translate([0, -15, 8])
    rotate([90, 0, 0])
    cylinder(h=20, r1=5, r2=2);

    // Tail segments
    for(i = [0:3]) {
        translate([0, -15 - i*5, 8])
        rotate([90, 0, 0])
        scale([1 - i*0.1, 1 - i*0.1, 1])
        cylinder(h=4, r1=5 - i, r2=4 - i);
    }

    // Tail fin
    color(color_shell)
    translate([0, -35, 8])
    rotate([90, 0, 0])
    scale([1.5, 0.3, 1])
    cylinder(h=5, r1=4, r2=0);
}

// Render the complete model
crawdaunt();

// Baltoy Pokemon - Realistic Style, Medium Complexity
// 3D printable, ~60mm height

// Parameters
scale_factor = 1;
height = 60 * scale_factor;
head_height = 15 * scale_factor;
body_height = 30 * scale_factor;
base_height = 15 * scale_factor;
wall_thickness = 2 * scale_factor;

module baltoy_head() {
    // Main head dome
    color("tan")
    union() {
        // Top dome
        translate([0, 0, head_height/2])
        sphere(d=head_height, $fn=32);

        // Bottom flat face
        translate([0, 0, 0])
        cylinder(h=wall_thickness, d=head_height, $fn=32);
    }

    // Eye markings
    color("red")
    difference() {
        translate([0, 0, head_height/2])
        sphere(d=head_height*0.9, $fn=32);

        // Inner cutout
        translate([0, 0, head_height/2-1])
        sphere(d=head_height*0.7, $fn=32);
    }

    // Eye details
    color("black")
    for(i=[-1, 1]) {
        translate([i*head_height/4, 0, head_height/2])
        cylinder(h=2, d=head_height/6, $fn=16);
    }
}

module baltoy_body() {
    color("tan")
    union() {
        // Main body cylinder
        cylinder(h=body_height, d=body_height*0.8, $fn=32);

        // Reinforcement rings
        for(i=[0.25, 0.5, 0.75]) {
            translate([0, 0, i*body_height])
            cylinder(h=wall_thickness, d=body_height*0.85, $fn=32);
        }
    }

    // Spiral markings
    color("red")
    for(i=[0:30:360]) {
        rotate([0, 0, i])
        translate([body_height*0.35, 0, (i/360)*body_height])
        cube([body_height*0.1, wall_thickness, body_height*0.05]);
    }
}

module baltoy_base() {
    color("tan")
    union() {
        // Main base
        cylinder(h=base_height, d=body_height*1.2, $fn=32);

        // Bottom platform
        translate([0, 0, -wall_thickness])
        cylinder(h=wall_thickness*2, d=body_height*1.3, $fn=32);
    }

    // Base markings
    color("red")
    difference() {
        cylinder(h=wall_thickness*3, d=body_height*1.1, $fn=32);
        cylinder(h=wall_thickness*4, d=body_height*0.9, $fn=32);
    }
}

module baltoy_arms() {
    color("tan")
    for(side=[-1, 1]) {
        translate([side*body_height*0.4, 0, body_height*0.7])
        rotate([0, 90, 0])
        cylinder(h=body_height*0.5, d=body_height*0.2, $fn=16);

        // Arm markings
        color("red")
        translate([side*body_height*0.55, 0, body_height*0.7])
        rotate([0, 90, 0])
        cylinder(h=body_height*0.4, d=body_height*0.15, $fn=16);
    }
}

// Main assembly
union() {
    // Head
    translate([0, 0, body_height+base_height+head_height/2])
    baltoy_head();

    // Body
    translate([0, 0, base_height+body_height/2])
    baltoy_body();

    // Base
    translate([0, 0, base_height/2])
    baltoy_base();

    // Arms
    baltoy_arms();
}

