    translate([0, 0, 3.5])
    scale([1, 0.8, 0.2])
    sphere(r=3, $fn=12);
}

module claydol_arms() {
    color([0.7, 0.65, 0.55]) // Slightly darker than body

    // Left arm
    translate([-20, 0, 5])
    rotate([0, 0, -30])
    union() {
        // Upper arm
        scale([1.5, 1.5, 4])
        rotate([90, 0, 0])
        cylinder(r=3, h=1, $fn=8);

