        translate([0, 0, -8])
        scale([1.2, 1.2, 3])
        rotate([90, 0, 0])
        cylinder(r=2.5, h=1, $fn=8);

        // Hand
        translate([0, -6, -12])
        sphere(r=4, $fn=16);
