    // Right arm (mirror of left)
    translate([20, 0, 5])
    mirror([1, 0, 0])
    rotate([0, 0, -30])
    union() {
        // Upper arm
        scale([1.5, 1.5, 4])
        rotate([90, 0, 0])
        cylinder(r=3, h=1, $fn=8);

        // Lower arm segment
        translate([0, 0, -8])
        scale([1.2, 1.2, 3])
        rotate([90, 0, 0])
        cylinder(r=2.5, h=1, $fn=8);

        // Hand
        translate([0, -6, -12])
        sphere(r=4, $fn=16);
    }
}

module claydol_legs() {
    color([0.7, 0.65, 0.55])

    // Front left leg
    translate([-8, -12, -16])
    rotate([15, 0, -10])
    union() {
        cylinder(r1=3, r2=2, h=12, $fn=8);
        translate([0, 0, -12])
        sphere(r=3, $fn=16);
    }

    // Front right leg
    translate([8, -12, -16])
    rotate([15, 0, 10])
    union() {
        cylinder(r1=3, r2=2, h=12, $fn=8);
        translate([0, 0, -12])
        sphere(r=3, $fn=16);
    }

    // Back left leg
    translate([-8, 12, -16])
    rotate([15, 0, 10])
    union() {
        cylinder(r1=3, r2=2, h=12, $fn=8);
        translate([0, 0, -12])
        sphere(r=3, $fn=16);
    }

    // Back right leg
    translate([8, 12, -16])
    rotate([15, 0, -10])
    union() {
        cylinder(r1=3, r2=2, h=12, $fn=8);
        translate([0, 0, -12])
        sphere(r=3, $fn=16);
    }
}

module claydol_markings() {
    color([0.5, 0.4, 0.3]) // Darker markings

    // Front marking
    translate([0, -16, 0])
    rotate([90, 0, 0])
    scale([1, 0.3, 1])
    cylinder(r=10, h=0.5, $fn=32);

    // Back marking
    translate([0, 16, 0])
    rotate([90, 0, 0])
    scale([1, 0.3, 1])
    cylinder(r=10, h=0.5, $fn=32);

    // Side markings
    for(i = [0:3]) {
        rotate([0, 0, i * 90])
        translate([12, 0, -5])
        rotate([0, 90, 0])
        scale([0.8, 0.2, 1])
        cylinder(r=8, h=0.5, $fn=32);
    }
}

module claydol_eyes() {
    // Front eyes
    translate([-8, -12, 8])
    rotate([15, 0, 0])
    claydol_eye();

    translate([8, -12, 8])
    rotate([15, 0, 0])
    claydol_eye();

    // Back eyes
    translate([-8, 12, 8])
    rotate([15, 180, 0])
    claydol_eye();

    translate([8, 12, 8])
    rotate([15, 180, 0])
    claydol_eye();
}

// Main model assembly
union() {
    claydol_body();
    claydol_arms();
    claydol_legs();
    claydol_markings();
    claydol_eyes();
