// Lileep - Rock/Grass type Pokemon
// Medium complexity realistic style

$fn = 32;

// Main colors
color_lileep_body = [0.5, 0.35, 0.25];
color_lileep_purple = [0.55, 0.3, 0.65];
color_lileep_yellow = [0.95, 0.8, 0.2];
color_lileep_eye = [0.1, 0.1, 0.1];

module lileep() {
    // Base stem
    color(color_lileep_body)
    translate([0, 0, 5])
    cylinder(h=20, r1=8, r2=10, center=false);

    // Bulb body
    color(color_lileep_purple)
    difference() {
        sphere(15);
        translate([0, 0, -18])
        sphere(15);
    }

    // Mouth opening
    color(color_lileep_purple)
    difference() {
        sphere(15);
        translate([0, 0, 5])
        cylinder(h=30, r=8, center=true);
    }

    // Eye stalks
    for (i = [0:1]) {
        translate([i*20-10, 0, 10]) {
            rotate([0, i*30-15, 0])
            color(color_lileep_purple)
            cylinder(h=15, r=3, center=false);

            translate([0, 0, 15])
            color(color_lileep_yellow)
            sphere(6);

            translate([0, 0, 18])
            color(color_lileep_eye)
            sphere(3);
        }
    }

    // Petal tentacles
    for (i = [0:7]) {
        rotate([0, 0, i*45])
        translate([0, 12, 0])
        rotate([0, 30, 0])
        color(color_lileep_purple)
        cylinder(h=20, r1=4, r2=2, center=false);
    }

    // Yellow ring pattern
    for (i = [0:7]) {
        rotate([0, 0, i*45])
        translate([0, 11, -8])
        rotate([0, 90, 0])
        color(color_lileep_yellow)
        cylinder(h=2, r=2, center=true);
    }

    // Base suction cups
    for (i = [0:5]) {
        rotate([0, 0, i*72])
        translate([6, 0, 0])
        color(color_lileep_body)
        translate([0, 0, 2])
        cylinder(h=3, r=4, center=false);
    }

    // Root tentacles
    for (i = [0:3]) {
        rotate([0, 0, i*120])
        translate([0, 8, -15])
        rotate([30, 0, 0])
        color(color_lileep_body)
        cylinder(h=15, r1=3, r2=1, center=false);
    }
}

