// Cradily OpenSCAD Model - Realistic Style, Medium Complexity
// Approx 80mm tall when printed

// Colors
neck_color = [0.4, 0.6, 0.3];
head_color = [0.5, 0.7, 0.4];
eye_color = [0.9, 0.1, 0.1];
stone_color = [0.6, 0.5, 0.4];
tentacle_color = [0.3, 0.5, 0.2];

// Base stone pedestal
module base() {
    color(stone_color) {
        difference() {
            cylinder(h = 15, r1 = 25, r2 = 20, $fn = 32);
            translate([0, 0, 10]) cylinder(h = 10, r1 = 20, r2 = 15, $fn = 32);
        }
    }
}

// Neck segment
module neck_segment(height, radius) {
    color(neck_color) {
        cylinder(h = height, r = radius, $fn = 24);
    }
}

// Curved neck
module neck() {
    color(neck_color) {
        translate([0, 0, 15]) {
            for(i = [0:5]) {
                rotate([0, i * 12, 0]) 
                translate([i * 3, 0, i * 8]) 
                neck_segment(8, 8 - i * 0.5);
            }
        }
    }
}

// Head bulb
module head() {
    color(head_color) {
        translate([18, 0, 65]) {
            // Main bulb
            sphere(r = 12, $fn = 32);

            // Eye sockets
            difference() {
                sphere(r = 12.5, $fn = 32);
                translate([6, -5, 3]) sphere(r = 3, $fn = 16);
                translate([6, 5, 3]) sphere(r = 3, $fn = 16);
            }

            // Eyes
            color(eye_color) {
                translate([8.5, -5, 3]) sphere(r = 1.5, $fn = 16);
                translate([8.5, 5, 3]) sphere(r = 1.5, $fn = 16);
            }

            // Mouth
            difference() {
                translate([7, 0, -3]) sphere(r = 4, $fn = 32);
                translate([7, 0, -6]) cube([10, 8, 5], center = true);
            }

            // Yellow ring pattern
            color([0.9, 0.8, 0.2]) {
                translate([0, 0, 8]) 
                rotate([90, 0, 0]) 
                cylinder(h = 1.5, r = 11, r1 = 9, $fn = 32);
            }
        }
    }
}

// Single tentacle
module tentacle(length, angle) {
    color(tentacle_color) {
        rotate([0, 0, angle]) {
            translate([0, 0, 15]) {
                for(i = [0:10]) {
                    rotate([i * 5, 0, 0]) 
                    translate([sin(i * 10) * 3, cos(i * 10) * 3, -i * 3]) 
                    sphere(r = 2 - i * 0.1, $fn = 16);
                }
            }
        }
    }
}

// All tentacles
module tentacles() {
    for(i = [0:60:359]) {
        tentacle(30, i);
    }
}

// Assembly
base();
neck();
head();
tentacles();

