// Scale factor for overall size
scale_factor = 0.7;

// Main body color - slate gray
color_body = [0.35, 0.35, 0.4];

// Shell color - dark brown
color_shell = [0.25, 0.15, 0.1];

// Eye color - bright red
color_eye = [0.9, 0.1, 0.1];

// Main module
module anorith() {
    scale([scale_factor, scale_factor, scale_factor])
    union() {
        // Main shell segments
        shell_segments();

        // Head with eyes
        head();

        // Six legs
        legs();

        // Tail
        tail();

        // Underside body
        body();
    }
}

// Shell segments
module shell_segments() {
    color(color_shell)
    union() {
        // Central shell
        for(i = [0:2]) {
            translate([0, -5 + i*5, 2 + i*2])
            rotate([0, 0, 0])
            scale([1.2, 1, 0.3])
            sphere(r=8);
        }

        // Side shell plates
        for(side = [-1, 1]) {
            translate([side * 10, 0, 5])
            rotate([0, side * 15, 0])
            scale([0.8, 1.2, 0.4])
            sphere(r=6);

            translate([side * 12, -8, 3])
            rotate([0, side * 20, 0])
            scale([0.7, 1, 0.3])
            sphere(r=5);
        }
    }
}

// Head with eyes and feelers
module head() {
    color(color_body)
    union() {
        // Main head
        translate([0, 15, 8])
        scale([0.8, 1, 0.7])
        sphere(r=7);

        // Eyes
        color(color_eye)
        for(side = [-1, 1]) {
            translate([side * 3, 17, 10])
            sphere(r=1.5);
        }

        // Feelers/antennae
        color(color_body)
        for(side = [-1, 1]) {
            translate([side * 4, 19, 9])
            rotate([0, 0, side * 20])
            cylinder(h=8, r1=1, r2=0.5);

            translate([side * 5, 22, 9])
            rotate([0, 0, side * 20])
            cylinder(h=6, r1=0.8, r2=0.3);
        }

        // Mouth area
        translate([0, 16, 6])
        scale([0.3, 0.2, 0.2])
        sphere(r=3);
    }
}

// Six articulated legs
module legs() {
    color(color_body)
    union() {
        // Front legs
        for(side = [-1, 1]) {
            for(pos = [0, 1]) {
                translate([side * 6, 8 - pos*5, 4])
                rotate([0, 0, side * 30])
                leg_segment();
            }
        }

        // Middle legs
        for(side = [-1, 1]) {
            translate([side * 8, -2, 3])
            rotate([0, 0, side * 45])
            scale([0.9, 0.9, 0.9])
            leg_segment();
        }

        // Rear legs
        for(side = [-1, 1]) {
            translate([side * 6, -10, 2])
            rotate([0, 0, side * 60])
            scale([0.8, 0.8, 0.8])
            leg_segment();
        }
    }
}

// Single leg segment
module leg_segment() {
    union() {
        // Upper segment
        cylinder(h=6, r=1.2);

        // Joint
        translate([0, 6, 0])
        sphere(r=1);

        // Lower segment
        translate([0, 6, 0])
        rotate([30, 0, 0])
        cylinder(h=8, r1=1, r2=0.8);

        // Foot
        translate([0, 14, 2])
        rotate([90, 0, 0])
        scale([1, 0.6, 0.3])
        cylinder(h=3, r1=1.5, r2=1);
    }
}

// Tail
module tail() {
    color(color_shell)
    translate([0, -18, 3])
    rotate([0, 0, 180])
    union() {
        // Tail segments
        for(i = [0:3]) {
            translate([0, i*3, -i*0.5])
            scale([1 - i*0.15, 1 - i*0.15, 0.4])
            sphere(r=5 - i*0.5);
        }

        // Tail fan
        translate([0, 12, -2])
        rotate([90, 0, 0])
        scale([1.5, 0.8, 0.1])
        cylinder(h=0.5, r=4);
    }
}

// Underside body
module body() {
    color(color_body)
    translate([0, 0, 1])
    scale([1.5, 1.8, 0.5])
    sphere(r=6);
}

// Generate the model
anorith();

// Armaldo - Realistic Style Medium Complexity
// 3D printable, ~80mm tall

$fa = 2;
$fs = 0.5;

// Color definitions
color_body = [0.3, 0.2, 0.1];
color_shell = [0.4, 0.3, 0.2];
color_accents = [0.8, 0.2, 0.1];
color_eyes = [0.9, 0.9, 0.1];

// Main body
module armaldo() {
    union() {
        // Central body
        color(color_body)
        translate([0, 0, 20])
        scale([1, 0.8, 1.2])
        sphere(20);

        // Shell segments
        shell_segments();

        // Head
        head();

        // Arms/claws
        arms();

        // Legs
        legs();

        // Tail
        tail();
    }
}

// Shell segments
module shell_segments() {
    for(i = [0:2]) {
        rotate([0, 0, i * 120])
        translate([0, 25, 25])
        scale([1.2, 1, 0.3])
        color(color_shell)
        sphere(15);
    }
}

// Head with features
module head() {
    translate([0, -15, 45])
    union() {
        // Main head
        color(color_body)
        scale([0.8, 1, 0.7])
        sphere(15);

        // Eyes
        for(side = [-1, 1]) {
            translate([side * 8, -12, 5])
            color(color_eyes)
            sphere(3);

            // Eye ridges
            translate([side * 8, -15, 8])
            color(color_shell)
            rotate([0, side * 20, 0])
            scale([0.5, 1, 0.3])
            sphere(5);
        }

        // Mandibles
        mandibles();
    }
}

// Mandibles
module mandibles() {
    for(side = [-1, 1]) {
        translate([side * 5, -20, 0])
        rotate([0, 0, side * 30])
        color(color_shell)
        scale([0.3, 1, 0.2])
        cylinder(h = 12, r1 = 3, r2 = 1);
    }
}

// Arms with claws
module arms() {
    for(side = [-1, 1]) {
        translate([side * 25, 0, 30])
        rotate([0, 0, side * 30])
        union() {
            // Upper arm
            color(color_body)
            rotate([0, 90, 0])
            cylinder(h = 20, r = 5);

            // Claw
            translate([20, 0, 0])
            rotate([0, 90, 0])
            claw();
        }
    }
}

// Claw assembly
module claw() {
    union() {
        // Main claw body
        color(color_shell)
        scale([2, 0.8, 0.8])
        sphere(6);

        // Individual claw tips
        for(i = [0:2]) {
            rotate([0, 0, i * 120 - 60])
            translate([8, 0, 0])
            color(color_accents)
            scale([2, 0.5, 0.5])
            sphere(3);
        }
    }
}

// Legs
module legs() {
    for(side = [-1, 1]) {
        translate([side * 15, 5, 10])
        union() {
            // Upper leg
            color(color_body)
            rotate([0, 0, side * -20])
            cylinder(h = 15, r = 4);

            // Lower leg
            translate([side * 12, 8, -5])
            rotate([0, 0, side * 20])
            color(color_shell)
            cylinder(h = 12, r = 3);

            // Foot
            translate([side * 20, 15, -10])
            color(color_body)
            scale([1.5, 1, 0.5])
            sphere(5);
        }
    }
}

// Tail
module tail() {
    translate([0, 20, 15])
    rotate([0, 0, 180])
    union() {
        // Tail segments
        for(i = [0:3]) {
            translate([0, i * 8, -i * 3])
            scale([1 - i * 0.15, 1, 1 - i * 0.15])
            color(color_shell)
            sphere(8 - i * 2);
        }

        // Tail tip
        translate([0, 32, -12])
        color(color_accents)
        scale([0.5, 2, 0.5])
        sphere(4);
    }
}

// Generate the model
armaldo();

// Feebas - OpenSCAD model
// Basic primitives only, 3D printable, realistic style, medium complexity

module fin(x, y, z, rx, ry, rz) {
    translate([x, y, z])
        rotate([rx, ry, rz])
        color("dodgerblue")
        difference() {
            union() {
                scale([1, 0.4, 1.5])
                    sphere(d=6);
                translate([2, 0, 0])
                    scale([1.3, 0.3, 0.8])
                    sphere(d=5);
            }
            translate([0, -3, 0])
                cube([6, 6, 6], center=true);
        }
}

module body() {
    color("lightsalmon")
    union() {
        // Main body
        translate([0, 0, 0])
            scale([1, 0.7, 0.5])
            sphere(d=50);

        // Tail segment
        translate([-30, 0, 0])
            scale([0.7, 0.6, 0.4])
            sphere(d=40);

        // Tail end
        translate([-50, 0, 0])
            scale([0.3, 0.3, 0.2])
            sphere(d=30);
    }
}

module scales() {
    color("firebrick")
    for(i=[-20:10:20]) {
        for(j=[-15:10:15]) {
            translate([i, j, 15])
                scale([1, 1, 0.3])
                sphere(d=8);
        }
    }
}

module eye(x) {
    translate([x, 12, 5]) {
        // Eye white
        color("white")
        sphere(d=8);

        // Pupil
        translate([0, 0, 4])
            color("black")
            sphere(d=4);
    }
}

module mouth() {
    translate([0, 20, -5])
    rotate([0, 90, 0])
    color("darkred")
    difference() {
        cylinder(h=15, d1=8, d2=3);
        translate([0, 0, -1])
            cube([15, 15, 15], center=true);
    }
}

module whiskers() {
    color("dodgerblue")
    for(side=[-1, 1]) {
        for(i=[-1, 0, 1]) {
            translate([5*side, 15+i*3, -3])
            rotate([0, 0, side*30+i*10])
            cylinder(h=12, d1=2, d2=1);
        }
    }
}

module tail_fin() {
    translate([-55, 0, 0])
    rotate([0, 0, 90])
    color("dodgerblue")
    difference() {
        scale([1.2, 0.3, 1])
            sphere(d=25);
        translate([0, -15, 0])
            cube([30, 30, 30], center=true);
    }
}

// Main assembly
union() {
    body();
    scales();
    eye(10);
    eye(-10);
    mouth();
    whiskers();
    tail_fin();

    // Top fin
    fin(0, 0, 20, 0, 0, 0);

    // Side fins
    fin(20, 0, -5, 0, 30, 45);
    fin(-20, 0, -5, 0, 30, -45);
}

// Milotic Pokemon - Realistic Style - Medium Complexity
// 3D printable with no overhangs

$fn = 32;

// Main colors
milotic_cream = [1, 0.85, 0.75];
milotic_red = [0.8, 0.2, 0.2];
milotic_pink = [0.95, 0.6, 0.7];
milotic_blue = [0.6, 0.7, 0.95];

// Scale factor for 3D printing (80mm length)
scale_factor = 0.8;

module milotic_body() {
    color(milotic_cream)
    union() {
        // Main serpentine body segments
        for(i = [0:15]) {
            translate([i*5 - 30, 0, 0])
            rotate([0, 0, i*5])
            scale([1 - i*0.03, 1 - i*0.03, 1 - i*0.02])
            sphere(d = 15 - i*0.5);
        }

        // Tail fin
        translate([50, 0, 0])
        rotate([0, 0, 20])
        scale([3, 1, 0.5])
        sphere(d = 15);

        // Side fins
        translate([30, 10, 0])
        rotate([0, 0, 45])
        scale([2, 1, 0.3])
        sphere(d = 8);

        translate([30, -10, 0])
        rotate([0, 0, -45])
        scale([2, 1, 0.3])
        sphere(d = 8);
    }
}

module milotic_head() {
    color(milotic_cream)
    union() {
        // Head base
        translate([-35, 0, 5])
        rotate([0, 0, -10])
        scale([1.2, 1, 1.5])
        sphere(d = 18);

        // Snout
        translate([-45, 0, 7])
        scale([1.5, 0.8, 0.8])
        cylinder(h = 10, d1 = 12, d2 = 8);

        // Eyebrow fins
        color(milotic_red)
        translate([-40, 8, 12])
        rotate([0, 30, -20])
        scale([0.5, 2, 0.2])
        sphere(d = 8);

        translate([-40, -8, 12])
        rotate([0, -30, 20])
        scale([0.5, 2, 0.2])
        sphere(d = 8);

        // Crest
        color(milotic_pink)
        translate([-42, 0, 20])
        for(i = [0:3]) {
            translate([i*2, 0, i*3])
            rotate([0, 0, -5 - i*2])
            scale([0.3, 0.3, 1 + i*0.2])
            sphere(d = 5);
        }
    }
}

module milotic_eyes() {
    // Eyes
    color([0, 0, 0])
    union() {
        translate([-45, 4, 9])
        sphere(d = 3);

        translate([-45, -4, 9])
        sphere(d = 3);
    }

    // Eye highlights
    color([1, 1, 1])
    union() {
        translate([-44, 4.5, 10])
        sphere(d = 1);

        translate([-44, -3.5, 10])
        sphere(d = 1);
    }
}

module milotic_scales() {
    color(milotic_blue)
    // Scale pattern along body
    for(i = [0:5]) {
        translate([i*10 - 25, 0, 8])
        rotate([0, 0, i*3])
        scale([1.5, 0.5, 0.2])
        sphere(d = 4);
    }

    for(i = [0:4]) {
        translate([i*10 - 20, 0, -8])
        rotate([0, 0, i*3])
        scale([1.5, 0.5, 0.2])
        sphere(d = 4);
    }
}

// Main assembly
scale(scale_factor)
union() {
    milotic_body();
    milotic_head();
    milotic_eyes();
    milotic_scales();
}

