// Kecleon Realistic Style - Medium Complexity
// Based on Generation III sprite proportions

$fn = 32;

// Main colors
color_green = [34/255, 139/255, 34/255];
color_red = [220/255, 20/255, 60/255];
color_yellow = [255/255, 215/255, 0/255];
color_black = [25/255, 25/255, 25/255];
color_white = [245/255, 245/255, 245/255];

module kecleon_body() {
    color(color_green)
    union() {
        // Main torso - rounded rectangular shape
        translate([0, 0, 10])
        scale([1.2, 1, 0.8])
        sphere(15);

        // Belly bulge
        translate([0, 0, 8])
        scale([0.9, 0.7, 0.6])
        sphere(16);
    }
}

module kecleon_head() {
    color(color_green)
    union() {
        // Main head shape
        translate([0, 20, 22])
        scale([1.1, 1.3, 1])
        sphere(12);

        // Snout extension
        translate([0, 32, 22])
        scale([0.7, 0.8, 0.6])
        sphere(10);
    }

    // Red zigzag stripe
    color(color_red)
    for(i = [-1, 1]) {
        translate([i * 6, 28, 29])
        rotate([0, 0, i * 15])
        scale([0.3, 2, 0.3])
        cube([5, 3, 3], center=true);
    }
}

module kecleon_eyes() {
    color(color_yellow)
    union() {
        // Eye sockets
        translate([-8, 20, 28])
        sphere(4);
        translate([8, 20, 28])
        sphere(4);

        // Eyelids
        color(color_green)
        translate([-8, 20, 31])
        scale([1.2, 1, 0.3])
        sphere(4);
        translate([8, 20, 31])
        scale([1.2, 1, 0.3])
        sphere(4);
    }

    // Pupils
    color(color_black)
    union() {
        translate([-8, 22, 28])
        sphere(2);
        translate([8, 22, 28])
        sphere(2);
    }

    // Eye ridges
    color(color_green)
    for(i = [-1, 1]) {
        translate([i * 12, 15, 35])
        rotate([0, -20, 0])
        cylinder(h=8, r1=3, r2=2);
    }
}

module kecleon_limbs() {
    // Arms
    for(side = [-1, 1]) {
        color(color_green) {
            // Upper arm
            translate([side * 15, 8, 15])
            rotate([0, 0, side * 30])
            cylinder(h=12, r=4);

            // Hand
            translate([side * 20, 8, 15])
            sphere(5);

            // Fingers
            for(f = [0, 1, 2]) {
                translate([side * (22 + f*2), 8, 15])
                rotate([0, 0, side * (f-1) * 20])
                cylinder(h=6, r1=2, r2=1);
            }
        }
    }

    // Legs
    for(side = [-1, 1]) {
        color(color_green) {
            // Upper leg
            translate([side * 8, -5, 5])
            rotate([30, 0, 0])
            cylinder(h=10, r=5);

            // Foot
            translate([side * 8, -10, 0])
            scale([1.5, 2, 0.8])
            sphere(5);
        }
    }
}

module kecleon_tail() {
    color(color_red)
    union() {
        // Tail base
        translate([0, -18, 15])
        rotate([90, 0, 0])
        cylinder(h=20, r1=5, r2=3);

        // Curl
        for(i = [0:5]) {
            rotate([0, 0, i * 30])
            translate([0, -25 - i*3, 15])
            rotate([90, 0, 0])
            cylinder(h=4, r=3);
        }

        // Tail tip
        translate([0, -35, 15])
        sphere(4);
    }
}

module kecleon_pattern() {
    // Yellow belly stripe
    color(color_yellow)
    translate([0, 0, 5])
    scale([0.6, 1.4, 0.3])
    sphere(15);

    // Yellow zigzag on back
    color(color_yellow)
    for(i = [0:3]) {
        translate([0, -5 - i*8, 22])
        rotate([0, 0, (i%2) * 180])
        scale([0.5, 1, 0.3])
        cube([20, 6, 2], center=true);
    }
}

// Assembly
union() {
    kecleon_body();
    kecleon_head();
    kecleon_eyes();
    kecleon_limbs();
    kecleon_tail();
    kecleon_pattern();
}

