            }
        }
    }

    translate([15, 0, 35]) {
        rotate([0, 30, 15]) {
            color(color_eye) {
                sphere(d = 2);
            }
        }
    }

    // Shadow base for stability
    translate([0, 0, -3]) {
        color(color_shadow) {
            cylinder(h = 3, d = 30);
        }
    }
