fin_width = 8;
fin_thickness = 2;

// Eye parameters
eye_size = 3;

// Mouth parameters
mouth_length = 5;
mouth_diameter = 2;

// Gorebyss body
module gorebyss_body() {
    // Main body - elongated pink tube
    cylinder(h=body_length, d1=body_diameter, d2=body_diameter*0.8, center=false);

    // Head
    translate([0, 0, body_length]) {
        cylinder(h=head_length, d1=head_diameter, d2=head_diameter*0.7, center=false);
    }

    // Tail
    translate([0, 0, -tail_length]) {
        cylinder(h=tail_length, d1=tail_diameter*0.8, d2=tail_diameter, center=false);
    }
}

// Gorebyss fins
module gorebyss_fins() {
    // Left fin
    translate([body_diameter/2 + fin_width/2, 0, body_length*0.7]) {
        rotate([0, 90, 0]) {
            cube([fin_length, fin_width, fin_thickness], center=true);
        }
    }

    // Right fin
    translate([-body_diameter/2 - fin_width/2, 0, body_length*0.7]) {
        rotate([0, -90, 0]) {
            cube([fin_length, fin_width, fin_thickness], center=true);
        }
    }

    // Dorsal fin
    translate([0, body_diameter/2 + fin_width/2, body_length*0.6]) {
        rotate([-90, 0, 0]) {
            cube([fin_length*0.7, fin_width*0.7, fin_thickness], center=true);
        }
    }
}

// Gorebyss face
module gorebyss_face() {
    // Eyes - blue
    color([0.2, 0.6, 0.9]) {
        // Left eye
        translate([head_diameter/3, head_diameter/3, body_length + head_length - 5]) {
            sphere(d=eye_size);
        }

        // Right eye
        translate([-head_diameter/3, head_diameter/3, body_length + head_length - 5]) {
            sphere(d=eye_size);
        }
    }

    // Mouth - small tube
    translate([0, head_diameter/2, body_length + head_length - 3]) {
        rotate([90, 0, 0]) {
            cylinder(h=mouth_length, d=mouth_diameter, center=false);
        }
    }
}

// Gorebyss tail details
module gorebyss_tail_details() {
    // Tail segments
    for(i=[1:3]) {
        translate([0, 0, -i*tail_length/4]) {
            rotate([0, 0, i*30]) {
                scale([0.8, 0.8, 0.2]) {
                    sphere(d=tail_diameter*1.2);
                }
            }
        }
    }

    // Tail tip
    translate([0, 0, -tail_length - 5]) {
        sphere(d=tail_diameter*1.2);
    }
}

