        for(i=[1:4]) {
            translate([0, 0, i*body_length/5]) {
                scale([1.1, 1.1, 0.3]) {
                    sphere(d=body_diameter*1.1);
                }
            }
        }

        // Head pattern
        translate([0, 0, body_length + head_length/2]) {
            scale([1, 1, 0.5]) {
                sphere(d=head_diameter*1.1);
            }
        }
    }
}

