    color(blue)
    difference() {
        // Main body - two connected spheres
        hull() {
            translate([-body_size/3, 0, 0])
                sphere(d=body_size);
            translate([body_size/3, 0, 0])
                sphere(d=body_size);
        }

        // Hollow out
        translate([0, 0, -wall])
            scale([0.8, 0.8, 0.8])
            hull() {
                translate([-body_size/3, 0, 0])
                    sphere(d=body_size);
                translate([body_size/3, 0, 0])
                    sphere(d=body_size);
            }
    }

    // Cross pattern on body
    color(dark_silver)
    for(i = [-1, 1]) {
        rotate([90, 0, 90*i])
            translate([0, 0, -body_size/2])
                cylinder(h=body_size, d=wall*2, center=true);
    }
}

// Create metallic arm
module arm(position=[0, 0, 0], rotation=[0, 0, 0]) {
    translate(position)
    rotate(rotation)
    union() {
        // Upper arm segment
        color(silver)
        translate([0, 0, -arm_length/4])
            cylinder(h=arm_length/2, d=arm_width, center=true);

        // Lower arm segment
        translate([0, 0, -arm_length*3/4])
            cylinder(h=arm_length/2, d=arm_width*0.8, center=true);

        // Claw hand
        color(silver)
        translate([0, 0, -arm_length])
        union() {
            // Central claw base
            sphere(d=claw_size);

            // Three claw prongs
            for(i = [0, 120, 240]) {
                rotate([0, 0, i])
                translate([claw_size/2, 0, 0])
                    rotate([90, 0, 0])
                        cylinder(h=claw_size*1.5, d=claw_size/2);
            }
        }
    }
}

// Create eye
module eye(position=[0, 0, 0]) {
    translate(position)
    union() {
        // Eye socket
        color(dark_silver)
        cylinder(h=eye_size/2, d=eye_size*1.2);

        // Eye
        color(red)
        translate([0, 0, eye_size/4])
            sphere(d=eye_size);
    }
}

// Create the complete Metang
module metang() {
    // Main body
    metang_body();

    // Arms - positioned at 45 degree angles
    arm([body_size/2, 0, body_size/4], [0, 45, 0]);
    arm([-body_size/2, 0, body_size/4], [0, -45, 180]);

