        translate([-25, -35, 25]) {
            // Main head shape
            cube([20, 25, 18], center=true);

            // Snout
            translate([-12, 0, 0]) cube([12, 15, 10], center=true);

            // Jaw
            translate([-8, -15, -8]) cube([16, 8, 6], center=true);
        }
    }

    // Eyes
    color([0.9, 0.9, 0.9]) {
        translate([-35, -30, 30]) sphere(d=4, $fn=8);
        translate([-35, -40, 30]) sphere(d=4, $fn=8);
    }
    color([0, 0, 0]) {
        translate([-37, -30, 30]) sphere(d=2, $fn=6);
        translate([-37, -40, 30]) sphere(d=2, $fn=6);
    }
}

module tail() {
    color([0.3, 0.25, 0.15]) {
        translate([0, 40, 20]) {
            // Tail base
            cylinder(h=12, r1=6, r2=4, center=false, $fn=8);
            // Tail tip
            translate([0, 0, 12]) rotate([0, 0, 15]) 
                cube([8, 15, 6], center=true);
        }
    }
}

module shell_spikes() {
    // Spikes on shell
    color([0.15, 0.25, 0.05]) {
        positions = [[-5, -20, 30], [-5, 0, 35], [-5, 20, 30]];
        for(pos = positions) {
            translate(pos) {
                for(i = [0:45:315]) {
                    rotate([0, 0, i]) {
                        translate([0, 10, 0]) {
                            cylinder(h=8, r1=3, r2=0.5, center=false, $fn=6);
                        }
                    }
                }
            }
        }
    }
}

// Main assembly
union() {
    // Base plate for stability
    color([0.1, 0.1, 0.1]) {
        translate([0, 0, -2]) cube([60, 80, 2], center=true);
    }

    // Body components
    translate([0, 0, 5]) {
        body_shell();
        shell_spikes();
        tree_trunk();
        tree_leaves();
    }

    // Limbs and head
    legs();
    head();
    tail();
}

