face_color = "#FFE0B2";
flame_color = "#FF9800";
eye_color = "#212121";
inner_ear_color = "#FFCCBC";

// Main body
module chimchar_body() {
    color(body_color)
    union() {
        // Torso
        translate([0, 0, 15])
            scale([1.2, 1, 1.3])
            sphere(r=12);

        // Chest
        translate([0, 8, 12])
            scale([0.8, 0.6, 1])
            sphere(r=10);
    }
}

// Head
module chimchar_head() {
    color(body_color)
    union() {
        // Main head shape
        translate([0, 0, 35])
            scale([1.1, 1, 1.2])
            sphere(r=10);

        // Snout
        translate([0, 10, 33])
            scale([0.7, 0.9, 0.6])
            sphere(r=8);
    }
}

// Face details
module chimchar_face() {
    color(face_color)
    difference() {
        // Face patch
        translate([0, 7, 32])
            scale([0.9, 1.1, 0.8])
            sphere(r=9);

        // Eye holes
        translate([-4, 11, 35])
            sphere(r=2.5);
        translate([4, 11, 35])
            sphere(r=2.5);
    }

    // Eyes
    color(eye_color)
    union() {
        translate([-3.5, 11, 36])
            sphere(r=1.5);
        translate([3.5, 11, 36])
            sphere(r=1.5);
    }

    // Nose
    color(eye_color)
    translate([0, 13, 34])
        sphere(r=1);

    // Mouth
    color("#424242")
    translate([0, 11, 30])
        rotate([90, 0, 0])
        cylinder(h=0.5, r1=2, r2=0.5);
}

// Ears
module chimchar_ears() {
    // Left ear
    color(body_color)
    translate([-8, -2, 40])
        rotate([0, -15, -30])
        scale([0.8, 0.3, 2])
        sphere(r=5);

    color(inner_ear_color)
    translate([-8, -1.5, 40])
        rotate([0, -15, -30])
        scale([0.5, 0.2, 1.5])
        sphere(r=4);

    // Right ear
    color(body_color)
    translate([8, -2, 40])
        rotate([0, -15, 30])
        scale([0.8, 0.3, 2])
        sphere(r=5);

    color(inner_ear_color)
    translate([8, -1.5, 40])
        rotate([0, -15, 30])
        scale([0.5, 0.2, 1.5])
        sphere(r=4);
}

// Arms
module chimchar_arms() {
    // Left arm
    color(body_color)
    union() {
        translate([-13, 0, 25])
            rotate([0, 0, 30])
            scale([0.5, 0.5, 1.2])
            cylinder(h=10, r=4);

        // Hand
        translate([-15, 2, 15])
            sphere(r=4);
    }

    // Right arm
    color(body_color)
    union() {
        translate([13, 0, 25])
            rotate([0, 0, -30])
            scale([0.5, 0.5, 1.2])
            cylinder(h=10, r=4);

        // Hand
        translate([15, 2, 15])
            sphere(r=4);
    }
}

// Legs
module chimchar_legs() {
    // Left leg
    color(body_color)
    union() {
        translate([-5, 0, 5])
            scale([0.6, 0.6, 1])
            cylinder(h=10, r=5);

        // Foot
        translate([-5, 3, 0])
            scale([1.2, 1.5, 0.8])
            sphere(r=5);
    }

    // Right leg
    color(body_color)
    union() {
        translate([5, 0, 5])
            scale([0.6, 0.6, 1])
