    color(body_color)
    union() {
        translate([0, -15, 20])
            rotate([0, 30, 0])
            scale([0.5, 0.5, 2])
            cylinder(h=15, r=3);

        // Flame base
        translate([0, -20, 30])
            sphere(r=4);
    }

    // Flame
    color(flame_color)
    union() {
        translate([0, -22, 32])
            scale([1, 1, 1.5])
