// Monferno OpenSCAD Model - Realistic Style, Medium Complexity
// 3D printable Pokemon figure with recognizable features

$fn = 24; // Smooth curves for realistic style

// Main colors
skin_color = "#FF8C00";
chest_color = "#FFE4B5";
face_color = "#FFD700";
flame_color = "#FF4500";

// Scale factor for 3D printing (80mm height)
scale_factor = 80/100;

// Main assembly
module monferno() {
    scale([scale_factor, scale_factor, scale_factor]) {
        union() {
            // Body
            translate([0, 0, 30]) {
                difference() {
                    // Torso
                    color(skin_color) 
                    scale([1.2, 0.8, 1.5]) 
                    sphere(d = 40);

                    // Chest area
                    color(chest_color)
                    translate([0, 15, -5])
                    scale([0.8, 0.6, 0.8])
                    sphere(d = 30);
                }
            }

            // Head
            translate([0, -15, 55]) {
                union() {
                    // Main head shape
                    color(face_color)
                    scale([1, 1.2, 1.1])
                    sphere(d = 35);

                    // Snout
                    color(face_color)
                    translate([0, -12, -5])
                    scale([0.7, 0.8, 0.6])
                    sphere(d = 25);

                    // Ears (2 pointed)
                    for(side = [-1, 1]) {
                        translate([side * 15, 5, 10]) {
                            rotate([0, side * 20, 0])
                            color(skin_color)
                            scale([0.3, 1.5, 0.3])
                            cylinder(h = 20, d1 = 8, d2 = 2);
                        }
                    }

                    // Eyes
                    for(side = [-1, 1]) {
                        translate([side * 10, -10, 5]) {
                            color("#000080")
                            sphere(d = 6);
                            translate([0, 0, 3])
                            color("white")
                            sphere(d = 2);
                        }
                    }

                    // Nose
                    color("#8B4513")
                    translate([0, -18, -2])
                    sphere(d = 4);
                }
            }

            // Arms
            for(side = [-1, 1]) {
                translate([side * 25, 0, 40]) {
                    rotate([0, 0, side * 30])
                    color(skin_color)
                    cylinder(h = 30, d = 12);

                    // Hands
                    translate([0, 0, -35])
                    color(face_color)
                    sphere(d = 15);

                    // Fingers
                    for(f = [0:2]) {
                        rotate([0, 0, f * 30 - 30])
                        translate([8, 0, -5])
                        color(face_color)
                        cylinder(h = 8, d = 4);
                    }
                }
            }

            // Legs
            for(side = [-1, 1]) {
                translate([side * 12, 0, 15]) {
                    color(skin_color)
                    cylinder(h = 25, d = 15);

                    // Feet
                    translate([0, 5, -25])
                    color("#8B4513")
                    scale([1.5, 2, 0.8])
                    sphere(d = 20);
                }
            }

            // Tail with flame
            translate([0, 25, 35]) {
                // Tail base
                color(skin_color)
                rotate([90, 0, 0])
                cylinder(h = 40, d1 = 15, d2 = 8);

                // Flame
                translate([0, 15, 35])
                flame();
            }

            // Characteristic blue and red markings
            // Blue face marking
            color("#4169E1")
            translate([0, -15, 50])
            scale([0.3, 0.1, 0.05])
            cube([30, 40, 5]);

            // Red markings on body
            color("#DC143C")
            translate([0, 0, 45])
            scale([1.4, 0.3, 0.1])
            cube([40, 15, 5]);
        }
    }
}

// Flame module for tail
module flame() {
    color(flame_color) {
        // Main flame body
        scale([1, 1, 2])
        sphere(d = 20);

        // Flame details
        for(i = [1:5]) {
            rotate([0, 0, i * 72])
            translate([8, 0, 5])
            scale([0.3, 0.3, 1.5])
            sphere(d = 15);
        }

        // Inner flame
        color("#FFD700")
        translate([0, 0, 10])
        scale([0.6, 0.6, 0.8])
        sphere(d = 15);
    }
}

// Generate the model
monferno();

// Infernape realistic style - medium complexity
// Based on Pokemon Diamond/Pearl design
// Scale: 1 unit = 1mm, total height ~80mm

// Colors
$fa = 8;
$fs = 0.5;

module body() {
    color([0.8, 0.2, 0.1]) {
        // Torso
        translate([0, 0, 30]) 
            scale([1.2, 1, 1.5]) 
                sphere(15);

        // Waist
        translate([0, 0, 20])
            cylinder(h=10, r1=12, r2=10);
    }
}

module head() {
    color([0.8, 0.2, 0.1]) {
        // Main head
        translate([0, -5, 45])
            scale([0.9, 1.1, 1.2])
                sphere(12);

        // Muzzle
        translate([0, -12, 42])
            scale([0.7, 1.2, 0.8])
                sphere(8);

        // Snout detail
        translate([0, -16, 40])
            rotate([25, 0, 0])
                cylinder(h=8, r1=6, r2=4);
    }

    // Gold crown
    color([0.9, 0.7, 0.1]) {
        translate([0, -5, 55])
            scale([1.3, 0.3, 0.3])
                sphere(10);

        // Flame pattern
        for(i=[-1, 1]) {
            translate([i*8, -3, 58])
                rotate([0, 0, i*15])
                    scale([0.5, 0.2, 1])
                        cylinder(h=8, r1=3, r2=1);
        }
    }

    // White face marking
    color([0.95, 0.95, 0.9]) {
        translate([0, -12, 42])
            scale([0.5, 1, 0.6])
                sphere(8);
    }
}

module ears() {
    for(i=[-1, 1]) {
        color([0.8, 0.2, 0.1]) {
            // Outer ear
            translate([i*12, -5, 52])
                rotate([0, 0, i*20])
                    scale([0.3, 1, 1.5])
                        cylinder(h=15, r1=5, r2=2);

            // Inner ear
            color([0.95, 0.7, 0.7]) {
                translate([i*12, -5, 52])
                    rotate([0, 0, i*20])
                        translate([0, 0, 5])
                            scale([0.2, 0.8, 1])
                                cylinder(h=10, r1=4, r2=1);
            }
        }
    }
}

module eyes() {
    for(i=[-1, 1]) {
        // Eye white
        color([1, 1, 1]) {
            translate([i*5, -12, 48])
                sphere(3);
        }

        // Pupil
        color([0.1, 0.1, 0.1]) {
            translate([i*5.5, -12, 49])
                sphere(1.5);
        }

        // Eyebrow ridge
        color([0.6, 0.1, 0.1]) {
            translate([i*5, -8, 52])
                rotate([0, 0, i*10])
                    scale([0.5, 0.2, 1])
                        cylinder(h=6, r1=2, r2=1);
        }
    }
}

module arms() {
    for(i=[-1, 1]) {
        // Upper arm
        color([0.8, 0.2, 0.1]) {
            translate([i*15, 0, 35])
                rotate([0, 0, i*20])
                    cylinder(h=15, r1=5, r2=4);

            // Forearm
            translate([i*20, -3, 23])
                rotate([0, 0, i*45])
                    cylinder(h=12, r1=4, r2=3);

            // Hand
            translate([i*25, -8, 18])
                sphere(4);
        }

        // Gold wrist band
        color([0.9, 0.7, 0.1]) {
            translate([i*22, -5, 20])
                cylinder(h=3, r=5, $fn=16);
        }
    }
}

module legs() {
    for(i=[-1, 1]) {
        // Thigh
        color([0.8, 0.2, 0.1]) {
            translate([i*8, 0, 20])
                rotate([0, 0, i*10])
                    cylinder(h=15, r1=6, r2=5);

            // Lower leg
            translate([i*10, 0, 8])
                rotate([0, 0, i*5])
                    cylinder(h=12, r1=5, r2=4);

            // Foot
            translate([i*10, 3, 2])
                scale([1.2, 2, 0.8])
                    sphere(5);
        }
    }
}

module tail() {
    color([0.8, 0.2, 0.1]) {
        // Tail base
        translate([0, 8, 25])
            rotate([0, 15, 0])
                cylinder(h=20, r1=5, r2=3);

        // Flame
        color([1, 0.8, 0]) {
            translate([0, 12, 42])
                for(i=[0:30:359]) {
                    rotate([0, 0, i])
                        translate([0, 0, 0])
                            rotate([90, 0, 0])
                                cylinder(h=8+sin(i*3)*3, r1=2, r2=0);
                }
        }
    }
}

module chest_detail() {
    // Gold chest pattern
    color([0.9, 0.7, 0.1]) {
        translate([0, 0, 40])
            scale([0.8, 0.6, 0.3])
                sphere(8);

        // Spikes
        for(i=[-1, 1]) {
            translate([i*5, 0, 38])
                rotate([0, 0, i*20])
                    cylinder(h=5, r1=2, r2=0);
        }
    }
}

// Assembly
union() {
    body();
    head();
    ears();
    eyes();
    arms();
    legs();
    tail();
    chest_detail();
}

// Piplup Pokemon - Realistic Style, Medium Complexity
// Size: 60mm height, 3D printable

// Colors
$fn = 32;

module piplup_body() {
    color([0.2, 0.4, 0.8]) {
        // Main body - egg shape
        scale([1, 0.8, 1.2]) sphere(10);

        // Belly
        translate([0, -7, -2]) color([0.9, 0.9, 0.9]) 
            scale([1.1, 0.7, 1.1]) sphere(8);
    }
}

module piplup_head() {
    color([0.2, 0.4, 0.8]) {
        // Head sphere
        translate([0, 0, 15]) sphere(8);

        // Beak
        translate([0, -8, 14]) rotate([0, 0, 0]) color([1, 0.7, 0]) {
            scale([1, 0.8, 0.5]) sphere(4);
            translate([0, -2, -0.5]) cube([6, 3, 3], center=true);
        }

        // Eyes
        for(x = [-3.5, 3.5]) {
            translate([x, 2, 18]) {
                color([0, 0, 0]) sphere(1.5);
                translate([0, 0, 1]) color([1, 1, 1]) sphere(0.7);
            }
        }

        // White face markings
        translate([0, 0, 17]) color([0.9, 0.9, 0.9]) 
            scale([1.3, 1.1, 0.5]) sphere(6);
    }
}

module piplup_cape() {
    color([0.15, 0.3, 0.7]) {
        // Cape base
        translate([0, 0, 8]) {
            rotate([0, 90, 0]) cylinder(h=12, r1=10, r2=8);
            translate([0, -6, 0]) rotate([0, 90, 0]) cylinder(h=12, r1=8, r2=6);
        }

        // Cape points
        for(angle = [0:120:240]) {
            rotate([0, 0, angle]) {
                translate([0, 8, 8]) {
                    rotate([0, 90, 0]) scale([0.7, 1, 1]) 
                        cylinder(h=5, r1=3, r2=0);
                }
            }
        }
    }
}

module piplup_wings() {
    color([0.2, 0.4, 0.8]) {
        // Left wing
        translate([-8, 0, 10]) rotate([0, 45, -30]) {
            scale([1.5, 0.5, 2]) sphere(3);
        }

        // Right wing
        translate([8, 0, 10]) rotate([0, 45, 30]) {
            scale([1.5, 0.5, 2]) sphere(3);
        }

        // Wing tips - white
        color([0.9, 0.9, 0.9]) {
            translate([-10, 0, 13]) sphere(1);
            translate([10, 0, 13]) sphere(1);
        }
    }
}

module piplup_feet() {
    color([1, 0.7, 0]) {
        // Left foot
        translate([-4, -5, -10]) {
            scale([1.2, 1, 0.5]) sphere(3);
            translate([0, 0, -1.5]) cube([3, 4, 1], center=true);
        }

        // Right foot
        translate([4, -5, -10]) {
            scale([1.2, 1, 0.5]) sphere(3);
            translate([0, 0, -1.5]) cube([3, 4, 1], center=true);
        }
    }
}

module piplup_tail() {
    color([0.2, 0.4, 0.8]) {
        // Tail base
        translate([0, 8, 2]) rotate([0, 90, 0]) {
            cylinder(h=8, r1=3, r2=2);
        }

        // Tail tip
        translate([0, 15, 2]) {
            color([0.15, 0.3, 0.7]) sphere(2);
            color([0.9, 0.9, 0.9]) translate([0, 1, 0]) sphere(1);
        }
    }
}

// Assembly
union() {
    // Main body parts
    piplup_body();
    piplup_head();
    piplup_cape();
    piplup_wings();
    piplup_feet();
    piplup_tail();

    // Optional: Add support base for 3D printing
    color([0.1, 0.1, 0.1]) 
        translate([0, 0, -12]) 
            cylinder(h=2, r=12, $fn=64);
}

// Prinplup Pokemon - Realistic Style (Medium Complexity)
// Height: 65mm (scaled for 3D printing)

module prinplup() {
    // Main body color - dark blue
    color([0.1, 0.2, 0.6]) {
        // Body - oval shape
        translate([0, 0, 30]) {
            scale([1.2, 1, 1.5]) sphere(15);
        }

        // Belly - white oval
        color([1, 1, 1]) {
            translate([0, -14.5, 30]) {
                scale([0.8, 0.3, 1.2]) sphere(12);
            }
        }
    }

    // Head
    color([0.1, 0.2, 0.6]) {
        translate([0, 0, 50]) {
            sphere(12);
        }
    }

    // Beak
    color([0.8, 0.6, 0.2]) {
        translate([0, 12, 48]) {
            rotate([0, 90, 0]) {
                cylinder(h=15, r1=6, r2=3);
            }
        }

        // Beak detail
        color([0.6, 0.4, 0.1]) {
            translate([0, 18, 48]) {
                rotate([0, 90, 0]) {
                    cylinder(h=8, r1=3, r2=2);
                }
            }
        }
    }

    // Eyes
    module eye() {
        color([1, 1, 1]) sphere(3);
        color([0, 0, 0]) translate([0, 0, 2]) sphere(1.5);
    }

    translate([-6, 5, 53]) eye();
    translate([6, 5, 53]) eye();

    // Crown
    color([0.8, 0.6, 0.2]) {
        translate([0, -5, 62]) {
            difference() {
                cylinder(h=8, r=8);
                translate([0, 0, -1]) cylinder(h=10, r=6);
            }
        }

        // Crown prongs
        for(i = [-1, 0, 1]) {
            translate([i * 6, -5, 70]) {
                rotate([0, 0, 0]) cylinder(h=6, r=2);
            }
        }
    }

    // Wings
    module wing() {
        color([0.1, 0.2, 0.6]) {
            translate([0, 0, 10]) {
                scale([0.3, 1, 0.8]) sphere(15);
            }
        }
    }

    translate([18, 0, 35]) rotate([0, 0, 30]) wing();
    translate([-18, 0, 35]) rotate([0, 0, -30]) mirror([1, 0, 0]) wing();

    // Feet
    module foot() {
        color([0.8, 0.6, 0.2]) {
            translate([0, 0, 5]) {
                scale([1, 1.5, 0.5]) sphere(5);
            }

            // Toes
            for(i = [-1, 0, 1]) {
                translate([i * 3, 3, 2]) {
                    scale([0.5, 0.5, 1]) sphere(3);
                }
            }
        }
    }

    translate([8, 10, 10]) foot();
    translate([-8, 10, 10]) foot();

    // Tail
    color([0.1, 0.2, 0.6]) {
        translate([0, -18, 25]) {
            rotate([0, 15, 0]) {
                scale([0.8, 0.3, 1.5]) sphere(10);
            }
        }
    }

    // Markings
    color([0.3, 0.4, 0.8]) {
        // Wing markings
        translate([15, 0, 35]) {
            scale([0.2, 0.8, 0.6]) sphere(12);
        }
        translate([-15, 0, 35]) {
            scale([0.2, 0.8, 0.6]) sphere(12);
        }
    }
}

// Create the model
prinplup();

// Empoleon OpenSCAD Model - Medium Complexity Realistic Style
// Overall dimensions approximately 80mm height

// Color definitions
blue = [0.2, 0.4, 0.8];
black = [0.1, 0.1, 0.1];
white = [0.95, 0.95, 0.95];
yellow = [0.9, 0.7, 0.1];
orange = [0.9, 0.5, 0.1];

// Body proportions
body_height = 40;
body_width = 30;
body_depth = 25;

// Main body module
module empoleon_body() {
    color(blue)
    union() {
        // Torso - slightly tapered
        hull() {
            translate([0, 0, body_height/2])
                sphere(d=body_width, $fn=16);
            translate([0, 0, body_height/4])
                sphere(d=body_width*1.2, $fn=16);
        }

        // Lower body
        translate([0, 0, -body_height/4])
            cylinder(h=body_height/2, d1=body_width*1.2, d2=body_width*0.8, $fn=16);
    }
}

// Head with crown
module empoleon_head() {
    color(blue)
    translate([0, body_depth/2 + 8, body_height/2 + 10])
    union() {
        // Main head sphere
        sphere(d=25, $fn=16);

        // Crown - three prongs
        color(orange)
        for(i=[-1, 0, 1]) {
            translate([i*8, 0, 12])
                rotate([0, 0, i*10])
                cylinder(h=15, d1=3, d2=1, $fn=8);
        }

        // Beak
        color(yellow)
        translate([0, 15, -2])
            rotate([30, 0, 0])
            difference() {
                cube([8, 15, 8], center=true);
                translate([0, 6, 0])
                    cube([7, 12, 7], center=true);
            }
    }
}

// Wings
module empoleon_wings() {
    module wing() {
        color(blue)
        union() {
            // Upper wing section
            translate([0, 0, 0])
                scale([2, 1, 0.3])
                sphere(d=25, $fn=16);

            // Lower wing section
            translate([0, -10, -8])
                scale([1.5, 0.8, 0.2])
                sphere(d=20, $fn=16);

            // Wing tips
            color(yellow)
            translate([15, -5, 0])
                sphere(d=3, $fn=8);
            translate([12, -12, -8])
                sphere(d=2.5, $fn=8);
        }
    }

    // Left wing
    translate([-body_width/2 - 15, 0, body_height/2])
        rotate([0, 20, 30])
        wing();

    // Right wing
    translate([body_width/2 + 15, 0, body_height/2])
        rotate([0, 20, -30])
        mirror([1, 0, 0])
        wing();
}

// Arms/fins
module empoleon_arms() {
    module arm() {
        color(blue)
        union() {
            // Upper arm
            cylinder(h=12, d1=8, d2=6, $fn=8);

            // Forearm with fin
            translate([0, 0, -8])
                union() {
                    cylinder(h=10, d1=6, d2=5, $fn=8);

                    // Fin blade
                    color(orange)
                    translate([0, 8, -5])
                        rotate([90, 0, 0])
                        scale([1, 0.1, 2])
                        cylinder(h=15, d=6, $fn=3);
                }
        }
    }

    // Left arm
    translate([-body_width/2 - 5, body_depth/4, body_height/3])
        rotate([20, 0, -30])
        arm();

    // Right arm
    translate([body_width/2 + 5, body_depth/4, body_height/3])
        rotate([20, 0, 30])
        mirror([1, 0, 0])
        arm();
}

// Feet
module empoleon_feet() {
    module foot() {
        color(yellow)
        union() {
            // Main foot
            translate([0, 5, 0])
                scale([1, 1.5, 0.3])
                sphere(d=12, $fn=8);

            // Toes
            for(i=[-1, 0, 1]) {
                translate([i*3, 8, -2])
                    sphere(d=3, $fn=6);
            }
        }
    }

    // Left foot
    translate([-body_width/4, -body_depth/2 - 8, -body_height/2])
        rotate([10, 0, 0])
        foot();

    // Right foot
    translate([body_width/4, -body_depth/2 - 8, -body_height/2])
        rotate([10, 0, 0])
        foot();
}

// Tail
module empoleon_tail() {
    color(blue)
    translate([0, -body_depth/2 - 5, body_height/4])
    union() {
        // Base
        cylinder(h=15, d1=10, d2=8, $fn=8);

        // Tail flippers
        for(angle=[0, 120, 240]) {
            rotate([0, 0, angle])
            translate([0, -8, 0])
                color(orange)
                scale([0.5, 1, 0.1])
                sphere(d=15, $fn=8);
        }
    }
}

// Eyes
module empoleon_eyes() {
    module eye() {
        color(white)
        sphere(d=4, $fn=8);
        color(black)
        translate([0, 2.1, 0])
            sphere(d=2, $fn=8);
    }

    // Left eye
    translate([-6, body_depth/2 + 18, body_height/2 + 12])
        eye();

    // Right eye
    translate([6, body_depth/2 + 18, body_height/2 + 12])
        eye();
}

// Main assembly
union() {
    empoleon_body();
    empoleon_head();
    empoleon_wings();
    empoleon_arms();
    empoleon_feet();
    empoleon_tail();
    empoleon_eyes();
}

// Starly Pokemon - Realistic Style - Medium Complexity
// 3D printable model with no overhangs

// Colors
starly_gray = [0.4, 0.4, 0.4];
starly_brown = [0.5, 0.35, 0.25];
starly_yellow = [0.9, 0.8, 0.2];
starly_white = [0.9, 0.9, 0.9];
starly_black = [0.1, 0.1, 0.1];
starly_orange = [0.9, 0.5, 0.2];

// Main body
module body() {
    union() {
        // Main torso
        color(starly_gray)
        scale([1, 0.8, 0.9])
        sphere(15);

        // Chest patch
        color(starly_white)
        translate([0, -5, -8])
        scale([1.2, 0.8, 0.5])
        sphere(8);
    }
}

// Head with beak
module head() {
    translate([0, 20, 8]) {
        union() {
            // Head
            color(starly_gray)
            scale([0.9, 1.1, 1])
            sphere(12);

            // Beak
            color(starly_yellow)
            translate([0, 14, -2])
            rotate([0, 30, 0])
            scale([1, 2, 0.8])
            cylinder(10, 3, 1);

            // Forehead crest
            color(starly_gray)
            translate([0, 8, 8])
            scale([1, 0.3, 1])
            sphere(6);
        }
    }
}

// Eyes
module eyes() {
    for(side = [-1, 1]) {
        translate([side * 6, 25, 5]) {
            union() {
                // White of eye
                color(starly_white)
                sphere(3);

                // Pupil
                color(starly_black)
                translate([side * 1, 0, 1])
                sphere(1.5);
            }
        }
    }
}

// Wings
module wing(side) {
    translate([side * 18, 5, 5]) {
        rotate([0, 0, side * 20]) {
            color(starly_gray) {
                // Main wing
                scale([1.5, 0.2, 2])
                sphere(8);

                // Wing tip
                translate([side * 5, 0, 8])
                scale([0.8, 0.2, 1.2])
                sphere(6);

                // Wing feather pattern
                color(starly_white)
                for(i = [0:2]) {
                    translate([side * (2 + i * 2), 0, -5 + i * 5])
                    scale([0.3, 0.1, 0.8])
                    sphere(3);
                }
            }
        }
    }
}

// Tail feathers
module tail() {
    translate([0, -20, 0]) {
        for(side = [-1, 1]) {
            color(starly_gray) {
                rotate([0, 0, side * 30]) {
                    translate([side * 8, -8, 0])
                    scale([0.8, 0.2, 2.5])
                    sphere(6);

                    // White tips
                    color(starly_white)
                    translate([side * 8, -8, 8])
                    scale([0.5, 0.2, 0.8])
                    sphere(4);
                }
            }
        }
    }
}

// Legs and feet
module legs() {
    for(side = [-1, 1]) {
        translate([side * 6, -5, -15]) {
            union() {
                // Leg
                color(starly_orange)
                cylinder(8, 2, 2);

                // Foot
                translate([0, 0, -10]) {
                    union() {
                        // Main foot
                        scale([1.5, 2.5, 0.5])
                        sphere(3);

                        // Toes
                        for(toe = [-1, 0, 1]) {
                            color(starly_orange)
                            translate([toe * 2, -2, -1])
                            scale([0.8, 0.5, 0.3])
                            sphere(2);
                        }
                    }
                }
            }
        }
    }
}

// Ears/crown feathers
module ears() {
    for(side = [-1, 1]) {
        color(starly_gray)
        translate([side * 8, 18, 12]) {
            rotate([side * 20, 0, 0])
            scale([0.5, 0.5, 2])
            cylinder(8, 3, 1);

            // White spot
            color(starly_white)
            translate([0, 0, 7])
            sphere(1.5);
        }
    }
}

// Assembly
scale(2)
union() {
    body();
    head();
    eyes();
    wing(-1);
    wing(1);
    tail();
    legs();
    ears();
}

// Staravia 3D model - realistic style, medium complexity
// Overall dimensions: ~70mm wingspan, ~65mm length

// Colors
body_color = [0.2, 0.2, 0.2];      // Dark gray
wing_color = [0.3, 0.3, 0.3];      // Medium gray
chest_color = [0.95, 0.95, 0.9];   // Off-white
beak_color = [0.8, 0.4, 0.1];      // Orange
eye_color = [0.9, 0.1, 0.1];       // Red
accent_color = [0.1, 0.1, 0.1];    // Black

// Main body module
module body() {
    color(body_color)
    union() {
        // Main torso
        translate([0, 0, 10])
            scale([1.2, 0.8, 1])
            sphere(r=12, $fn=32);

        // Chest
        color(chest_color)
        translate([0, -5, 5])
            scale([0.8, 0.6, 0.8])
            sphere(r=10, $fn=32);

        // Neck
        translate([0, 0, 20])
            rotate([30, 0, 0])
            cylinder(h=8, r1=6, r2=4, $fn=16);
    }
}

// Head module
module head() {
    color(body_color)
    union() {
        // Main head shape
        translate([0, 0, 30])
            scale([1, 0.9, 1.1])
            sphere(r=10, $fn=32);

        // Beak
        color(beak_color)
        translate([0, 0, 37])
            rotate([20, 0, 0])
            cylinder(h=15, r1=3, r2=0.5, $fn=8);

        // Crest
        color(accent_color)
        for(i = [0:2]) {
            rotate([0, 0, i*120])
            translate([0, 0, 38])
                rotate([30, 0, 0])
                cylinder(h=8, r1=1.5, r2=0.5, $fn=6);
        }

        // Eyes
        color(eye_color)
        for(side = [-1, 1]) {
            translate([side*5, 5, 32])
                sphere(r=2.5, $fn=16);

            // Eye highlight
            color([1,1,1])
            translate([side*5.5, 5.5, 34])
                sphere(r=0.8, $fn=8);
        }

        // Eye mask
        color(accent_color)
        for(side = [-1, 1]) {
            translate([side*5, 5, 32])
                rotate([0, 90, 0])
                scale([1.5, 0.8, 0.5])
                cylinder(h=0.5, r=4, $fn=16);
        }
    }
}

// Wing module
module wing() {
    color(wing_color)
    difference() {
        // Main wing
        scale([1, 0.2, 1])
            rotate([0, 90, 0])
            cylinder(h=25, r1=15, r2=25, $fn=32);

        // Wing tip cutout
        translate([0, -1, 20])
            rotate([0, 90, 0])
            cylinder(h=2, r1=5, r2=8, $fn=16);
    }

    // Wing feathers
    color(accent_color)
    for(i = [0:2]) {
        translate([0, -1, i*6])
            rotate([0, 90, 0])
            cylinder(h=0.5, r1=12-i*2, r2=15-i*3, $fn=3);
    }
}

// Tail module
module tail() {
    color(wing_color)
    union() {
        // Central tail feather
        translate([0, 0, -20])
            rotate([30, 0, 0])
            cylinder(h=15, r1=4, r2=2, $fn=8);

        // Side tail feathers
        for(side = [-1, 1]) {
            translate([side*3, 0, -18])
                rotate([30, side*10, 0])
                cylinder(h=12, r1=3, r2=1.5, $fn=6);
        }
    }
}

// Leg module
module leg() {
    color(beak_color)
    union() {
        // Upper leg
        translate([0, 0, 0])
            cylinder(h=6, r1=2, r2=1.5, $fn=8);

        // Lower leg
        translate([0, 0, -6])
            rotate([20, 0, 0])
            cylinder(h=5, r1=1.5, r2=1, $fn=8);

        // Foot
        translate([0, 0, -11])
            rotate([90, 0, 0])
            scale([1.5, 0.5, 1])
            cylinder(h=3, r=2, $fn=16);
    }
}

// Main assembly
union() {
    // Body and head
    body();
    head();

    // Wings
    for(side = [-1, 1]) {
        translate([side*15, 0, 15])
            rotate([0, side*20, 0])
            mirror([0, side, 0])
            wing();
    }

    // Tail
    translate([0, 0, 10])
        tail();

    // Legs
    for(side = [-1, 1]) {
        translate([side*5, 0, 0])
            leg();
    }
}

// Staraptor OpenSCAD Model - Medium Complexity Realistic Style
// Dimensions: ~90mm wingspan, 50mm tall

// Main color definitions
color_body = [0.2, 0.2, 0.2];
color_wing = [0.1, 0.1, 0.1];
color_face = [0.15, 0.15, 0.15];
color_crest = [0.3, 0.3, 0.3];
color_beak = [1, 0.8, 0.2];
color_eye = [1, 1, 0];
color_markings = [0.05, 0.05, 0.05];

// Body
module body() {
    color(color_body)
    union() {
        // Main torso
        translate([0, 0, 25])
            scale([1.2, 0.8, 1])
            sphere(r=12, $fn=32);

        // Chest area
        translate([0, 8, 20])
            scale([0.8, 0.6, 1.2])
            sphere(r=10, $fn=32);
    }
}

// Head with crest
module head() {
    color(color_face)
    union() {
        // Main head
        translate([0, 15, 38])
            rotate([0, 20, 0])
            scale([0.9, 0.7, 1.1])
            sphere(r=10, $fn=32);

        // Crest
        color(color_crest)
        translate([0, 12, 48])
            rotate([0, 30, 0])
            scale([0.3, 0.8, 1.5])
            cylinder(r1=8, r2=2, h=15, $fn=32);
    }
}

// Beak
module beak() {
    color(color_beak)
    translate([0, 26, 36])
        rotate([0, -30, 0])
        scale([1, 2, 0.8])
        cylinder(r1=3, r2=1, h=10, $fn=16);
}

// Eye
module eye() {
    color(color_eye)
    translate([3, 20, 40])
        sphere(r=2, $fn=16);
}

// Wing module
module wing(is_left = true) {
    color(color_wing)
    mirror([is_left ? 0 : 1, 0, 0])
    translate([15, 0, 30])
        rotate([0, 0, is_left ? -10 : 10])
        union() {
            // Main wing
            translate([0, 0, 0])
                rotate([0, 20, 0])
                scale([1, 0.3, 0.1])
                cube([30, 15, 2], center=true);

            // Wing tip
            translate([25, 0, 2])
                rotate([0, 10, 0])
                scale([0.8, 0.2, 0.1])
                cube([15, 10, 2], center=true);

            // Wing feather details
            for(i = [0:2]) {
                translate([10 + i*8, 0, -1])
                    rotate([0, 30 - i*10, 0])
                    scale([0.6 - i*0.1, 0.15, 0.05])
                    cube([10, 8, 1], center=true);
            }
        }
}

// Tail
module tail() {
    color(color_wing)
    translate([0, -20, 25])
        rotate([30, 0, 0])
        union() {
            // Main tail
            scale([1, 0.3, 0.1])
            cube([20, 8, 2], center=true);

            // Tail feathers
            for(i = [-1:1]) {
                translate([i*5, -5, 0])
                    rotate([0, i*10, 0])
                    scale([0.8, 0.2, 0.1])
                    cube([12, 6, 1.5], center=true);
            }
        }
}

// Leg
module leg(is_left = true) {
    color(color_body)
    mirror([is_left ? 0 : 1, 0, 0])
    translate([5, 0, 15])
        union() {
            // Thigh
            translate([0, 0, 5])
                rotate([0, 0, 45])
                cylinder(r=2.5, h=8, $fn=16);

            // Lower leg
            translate([0, -5, 0])
                rotate([0, 0, 90])
                cylinder(r=2, h=10, $fn=16);

            // Foot
            translate([0, -8, -2])
                scale([1.5, 2, 0.5])
                cube([4, 6, 2], center=true);
        }
}

// Body markings
module markings() {
    color(color_markings)
    union() {
        // Chest marking
        translate([0, 12, 25])
            scale([0.6, 0.4, 0.1])
            sphere(r=8, $fn=32);

        // Wing markings
        for(side = [-1, 1]) {
            translate([side*20, 0, 30])
                rotate([0, 20, 0])
                scale([0.1, 0.2, 0.05])
                cube([20, 8, 1], center=true);
        }
    }
}

// Assemble the complete Staraptor
union() {
    body();
    head();
    beak();
    eye();
    mirror([-1, 0, 0]) eye();
    wing(true);
    wing(false);
    tail();
    leg(true);
    leg(false);
    markings();
}

// Bidoof Pokemon - Medium complexity, realistic style
// Overall dimensions: ~80mm long, ~50mm tall, ~45mm wide

$fn = 32;

module bidoof_body() {
    // Main brown body
    color([0.4, 0.25, 0.15]) {
        // Torso - elongated oval
        scale([1.2, 0.8, 0.7]) sphere(20);

        // Belly - lighter brown
        color([0.6, 0.4, 0.3]) {
            translate([0, -15, -13]) scale([0.7, 0.6, 0.4]) sphere(15);
        }
    }
}

module bidoof_head() {
    color([0.4, 0.25, 0.15]) {
        // Head - rounded rectangle shape
        translate([0, 25, 8]) scale([1.1, 0.9, 0.9]) sphere(15);

        // Snout
        translate([0, 35, 2]) scale([0.8, 0.6, 0.5]) sphere(12);

        // Teeth (two buck teeth)
        color([0.9, 0.9, 0.9]) {
            translate([-3, 42, 2]) cube([3, 4, 2]);
            translate([0, 42, 2]) cube([3, 4, 2]);
        }

        // Nose - small black oval
        color([0.1, 0.1, 0.1]) {
            translate([0, 40, 5]) scale([1, 0.7, 0.5]) sphere(3);
        }

        // Eyes
        color([0.1, 0.1, 0.1]) {
            translate([-8, 30, 10]) sphere(3);
            translate([8, 30, 10]) sphere(3);
        }

        // Eye whites
        color([1, 1, 1]) {
            translate([-8.5, 29.5, 11]) sphere(1.5);
            translate([7.5, 29.5, 11]) sphere(1.5);
        }
    }
}

module bidoof_ears() {
    color([0.4, 0.25, 0.15]) {
        // Left ear
        translate([-12, 20, 20]) rotate([30, 0, -20]) 
            scale([0.3, 0.2, 1]) cylinder(h=15, r1=5, r2=2);

        // Right ear
        translate([12, 20, 20]) rotate([30, 0, 20]) 
            scale([0.3, 0.2, 1]) cylinder(h=15, r1=5, r2=2);
    }
}

module bidoof_tail() {
    color([0.4, 0.25, 0.15]) {
        // Tail base
        translate([25, -10, 5]) rotate([0, 0, -30]) 
            scale([0.3, 2, 0.3]) cylinder(h=20, r1=4, r2=2);

        // Tail paddle
        translate([32, -8, 5]) rotate([0, 0, 0]) 
            scale([2, 0.2, 1.2]) sphere(8);
    }
}

module bidoof_legs() {
    color([0.4, 0.25, 0.15]) {
        // Front legs
        translate([-10, 15, -15]) rotate([0, 10, 10]) 
            cylinder(h=15, r=4);
        translate([10, 15, -15]) rotate([0, 10, -10]) 
            cylinder(h=15, r=4);

        // Back legs
        translate([-10, -15, -15]) rotate([0, -10, 5]) 
            cylinder(h=15, r=5);
        translate([10, -15, -15]) rotate([0, -10, -5]) 
            cylinder(h=15, r=5);

        // Paws - darker brown
        color([0.3, 0.2, 0.1]) {
            for(x = [-10, 10]) {
                for(y = [15, -15]) {
                    translate([x, y, -28]) sphere(4);
                }
            }
        }
    }
}

module bidoof_pattern() {
    // Distinctive tan pattern on face
    color([0.7, 0.5, 0.3]) {
        translate([0, 30, 2]) scale([1.5, 0.8, 0.3]) sphere(10);
    }

    // Tan patches on back
    color([0.7, 0.5, 0.3]) {
        translate([-8, 5, 12]) sphere(5);
        translate([8, 5, 12]) sphere(5);
    }
}

// Assemble the complete Bidoof
union() {
    bidoof_body();
    bidoof_head();
    bidoof_ears();
    bidoof_tail();
    bidoof_legs();
    bidoof_pattern();
}

// Bibarel - Realistic Style, Medium Complexity
// 3D printable model with proper wall thickness and no overhangs

// Color definitions
color_body = [0.75, 0.5, 0.35];      // Brown fur
color_cream = [0.95, 0.85, 0.75];    // Cream belly
color_black = [0.1, 0.1, 0.1];       // Black markings
color_red = [0.8, 0.2, 0.2];         // Red nose

// Main module
module bibarel() {
    // Body - main torso
    color(color_body)
    union() {
        // Main body
        translate([0, 0, 10])
        scale([1.2, 0.8, 1])
        sphere(d=40, $fn=32);

        // Belly - cream colored patch
        color(color_cream)
        translate([0, 0, 5])
        scale([1.1, 0.7, 0.6])
        sphere(d=35, $fn=32);

        // Head section
        translate([0, 25, 25])
        scale([1.3, 1, 1.1])
        sphere(d=35, $fn=32);

        // Tail base
        translate([0, -30, 12])
        scale([0.8, 1.2, 0.4])
        sphere(d=30, $fn=32);
    }

    // Ears
    color(color_body)
    for(x = [-15, 15]) {
        translate([x, 20, 45])
        rotate([0, -10, 0])
