// Main parameters
$fn = 32;
scale_factor = 1.5;
body_radius = 15 * scale_factor;
head_radius = 12 * scale_factor;
ear_length = 18 * scale_factor;
ear_width = 5 * scale_factor;
stem_length = 8 * scale_factor;
stem_radius = 2 * scale_factor;
eye_radius = 3 * scale_factor;
cheek_radius = 4 * scale_factor;
leaf_length = 12 * scale_factor;
leaf_width = 8 * scale_factor;

// Colors
red = [0.8, 0.2, 0.2];
green = [0.2, 0.6, 0.2];
white = [0.95, 0.95, 0.95];
black = [0.1, 0.1, 0.1];
pink = [0.95, 0.6, 0.7];

// Main body
module cherubi_body() {
    color(red) {
        // Main spherical body
        sphere(r = body_radius, center = true);

        // Head sphere
        translate([0, 0, body_radius * 0.8]) {
            sphere(r = head_radius, center = true);
        }
    }
}

// Ears module
module ears() {
    color(red) {
        // Left ear
        translate([-head_radius * 0.7, 0, body_radius + head_radius * 0.5]) {
            rotate([0, -20, -30]) {
                cylinder(h = ear_length, r1 = ear_width, r2 = ear_width * 0.7, center = true);
            }
        }

        // Right ear
        translate([head_radius * 0.7, 0, body_radius + head_radius * 0.5]) {
            rotate([0, -20, 30]) {
                cylinder(h = ear_length, r1 = ear_width, r2 = ear_width * 0.7, center = true);
            }
        }
    }
}

// Stem and leaves
module stem_and_leaves() {
    color(green) {
        // Main stem
        translate([0, 0, body_radius + head_radius + stem_length * 0.5]) {
            cylinder(h = stem_length, r = stem_radius, center = true);
        }

        // Leaves
        for (i = [0:1]) {
            rotate([0, 0, i * 180]) {
                translate([0, stem_radius + leaf_width * 0.5, body_radius + head_radius + stem_length]) {
                    rotate([45, 0, 0]) {
                        scale([1, 0.3, 1]) {
                            sphere(r = leaf_length, center = true);
                        }
                    }
                }
            }
        }
    }
}

// Face features
module face() {
    // Eyes
    color(white) {
        // Left eye
        translate([-head_radius * 0.4, -head_radius * 0.3, body_radius + head_radius * 0.8]) {
            sphere(r = eye_radius, center = true);
        }

        // Right eye
        translate([head_radius * 0.4, -head_radius * 0.3, body_radius + head_radius * 0.8]) {
            sphere(r = eye_radius, center = true);
        }
    }

    // Pupils
    color(black) {
        // Left pupil
        translate([-head_radius * 0.4, -head_radius * 0.2, body_radius + head_radius * 0.9]) {
            sphere(r = eye_radius * 0.5, center = true);
        }

        // Right pupil
        translate([head_radius * 0.4, -head_radius * 0.2, body_radius + head_radius * 0.9]) {
            sphere(r = eye_radius * 0.5, center = true);
        }
    }

    // Cheeks
    color(pink) {
        // Left cheek
        translate([-head_radius * 0.7, -head_radius * 0.1, body_radius + head_radius * 0.5]) {
            sphere(r = cheek_radius, center = true);
        }

        // Right cheek
        translate([head_radius * 0.7, -head_radius * 0.1, body_radius + head_radius * 0.5]) {
            sphere(r = cheek_radius, center = true);
        }
    }

    // Mouth
    color(black) {
        translate([0, -head_radius * 0.6, body_radius + head_radius * 0.3]) {
            scale([0.8, 0.3, 0.2]) {
                sphere(r = eye_radius, center = true);
            }
        }
    }
}

// Assembly
union() {
    cherubi_body();
    ears();
    stem_and_leaves();
    face();
}

