// Honchkrow - Realistic Style, Medium Complexity
// Basic dimensions
body_length = 40;
body_width = 25;
body_height = 30;
head_size = 18;
wing_span = 60;
wing_depth = 8;
tail_length = 35;
beak_length = 12;

// Colors
color_body = [0.1, 0.1, 0.1];      // Dark blue-black
color_red = [0.8, 0.1, 0.1];       // Red accents
color_white = [0.9, 0.9, 0.9];     // White feathers
color_yellow = [0.9, 0.7, 0.1];    // Beak
color_hat = [0.15, 0.15, 0.15];    // Dark hat color

module honchkrow() {
    // Main body
    color(color_body)
    union() {
        // Body
        translate([0, 0, 15])
        scale([1, 0.8, 1.2])
        sphere(d=body_length);

        // Head
        translate([0, -body_width/2, body_height])
        sphere(d=head_size);

        // Hat rim
        translate([0, -body_width/2-2, body_height+5])
        rotate([90, 0, 0])
        cylinder(h=3, r1=head_size/2+3, r2=head_size/2+8);

        // Hat top
        translate([0, -body_width/2-2, body_height+8])
        rotate([90, 0, 0])
        cylinder(h=12, r1=head_size/2+8, r2=0);

        // Beak
        color(color_yellow)
        translate([0, -body_width/2-8, body_height-2])
        rotate([0, 0, -20])
        cylinder(h=beak_length, r1=2.5, r2=0.5);

        // Eyes (white)
        color(color_white)
        for(i = [-1, 1]) {
            translate([i*5, -body_width/2-8, body_height+3])
            sphere(d=4);
        }

        // Eyes (red pupils)
        color(color_red)
        for(i = [-1, 1]) {
            translate([i*5.5, -body_width/2-9.5, body_height+3])
            sphere(d=2);
        }

        // Wings
        color(color_body)
        for(side = [-1, 1]) {
            translate([side*body_width/2, 0, 25])
            rotate([0, 0, side*20])
            rotate([30, 0, 0])
            scale([1, 2, 0.3])
            sphere(d=wing_span/2);
        }

        // Tail feathers
        color(color_body)
        translate([0, body_width/2+5, 20])
        rotate([30, 0, 0])
        scale([0.3, 3, 1])
        sphere(d=tail_length);

        // Red chest marking
        color(color_red)
        translate([0, -body_width/2, 10])
        scale([0.8, 0.3, 0.6])
        sphere(d=body_length/1.5);

        // Legs
        color(color_body)
        for(side = [-1, 1]) {
            translate([side*8, -body_width/2, 5])
            cylinder(h=10, r=2);

            // Feet
            translate([side*8, -body_width/2, 0])
            rotate([90, 0, 0])
            difference() {
                cylinder(h=8, r=3);
                translate([0, -4, 0])
                cube([8, 8, 3], center=true);
            }
        }

        // Feathers on hat
        color(color_red)
        translate([0, -body_width/2-2, body_height+18])
        rotate([0, 0, 45])
        cylinder(h=8, r1=1, r2=3);

        // Side feathers
        color(color_body)
        for(side = [-1, 1]) {
            translate([side*head_size/2, -body_width/2-2, body_height-5])
            rotate([0, 0, side*30])
            scale([0.5, 2, 0.2])
            sphere(d=8);
        }
    }
}

