    color(color_body) {
        // Front left leg
        translate([-8, 15, 0]) {
            rotate([0, 10, 0]) {
                cylinder(h=12, d1=6, d2=4, $fn=16);
            }
        }

        // Front right leg
        translate([8, 15, 0]) {
            rotate([0, 10, 0]) {
                cylinder(h=12, d1=6, d2=4, $fn=16);
            }
        }

        // Back left leg
        translate([-10, -10, 0]) {
            rotate([0, -10, 0]) {
                cylinder(h=15, d1=7, d2=5, $fn=16);
            }
        }

        // Back right leg
        translate([10, -10, 0]) {
            rotate([0, -10, 0]) {
                cylinder(h=15, d1=7, d2=5, $fn=16);
            }
        }
    }

    // Paws
    color(color_face) {
        for(x = [-8, 8]) {
            translate([x, 15, -1]) {
                sphere(d=6, $fn=16);
            }
            translate([x*1.25, -10, -1]) {
                sphere(d=7, $fn=16);
            }
        }
    }
}

// Tail module
module glameow_tail() {
    color(color_tail) {
        // Tail base
        translate([0, -18, 18]) {
            rotate([30, 0, 0]) {
                cylinder(h=8, d1=8, d2=6, $fn=16);
            }
        }

        // Tail curl - spiral segments
        for(i = [0:15:135]) {
            rotate([0, 0, i]) {
                translate([0, -18 - i/5, 18 - i/10]) {
                    rotate([30 - i/10, 0, 0]) {
                        sphere(d=5, $fn=16);
                    }
                }
            }
        }

        // Tail tip
        translate([4, -32, 10]) {
            sphere(d=4, $fn=16);
        }
    }
}

// Whiskers module
module glameow_whiskers() {
    color("#666666") {
        // Left whiskers
        for(angle = [-20, 0, 20]) {
            translate([-6, 33, 23]) {
                rotate([0, angle, -90]) {
                    cylinder(h=10, d=0.8, $fn=8);
                }
            }
        }

        // Right whiskers
        for(angle = [-20, 0, 20]) {
            translate([6, 33, 23]) {
                rotate([0, angle, -90]) {
                    cylinder(h=10, d=0.8, $fn=8);
                }
            }
        }
    }
}

// Collar module
module glameow_collar() {
    color(color_collar) {
        translate([0, 10, 20]) {
            rotate([90, 0, 0]) {
                difference() {
                    cylinder(h=3, d=28, $fn=32);
                    cylinder(h=4, d=26, $fn=32);
                }
            }
        }

        // Collar gem
        color("#FF69B4") {
            translate([0, 10, 21.5]) {
                sphere(d=4, $fn=16);
            }
        }
    }
}

// Assembly
scale([scale_factor, scale_factor, scale_factor]) {
    union() {
        glameow_body();
        glameow_head();
        glameow_ears();
        glameow_eyes();
        glameow_nose();
        glameow_legs();
        glameow_tail();
        glameow_whiskers();
        glameow_collar();
    }
}

// Realistic Purugly OpenSCAD Model - Medium Complexity
// 3D printable Pokemon figure with characteristic features

$fn = 32;

// Main colors
color_body = [0.7, 0.7, 0.7];      // Light gray
color_accents = [0.3, 0.3, 0.3];   // Dark gray
color_eyes = [0.9, 0.1, 0.1];      // Red
color_mouth = [0.5, 0.2, 0.2];     // Dark pink

// Scale factor for 3D printing (80mm width)
scale_factor = 1;

module body_main() {
    color(color_body)
    union() {
        // Main rounded body
        translate([0, 0, 15])
            scale([1.2, 1, 0.8])
            sphere(20);

        // Lower belly bulge
        translate([0, 0, 5])
            scale([1.1, 0.9, 0.6])
            sphere(18);
    }
}

module head() {
    color(color_body)
    union() {
        // Head shape
        translate([0, 22, 25])
            scale([0.9, 0.7, 0.7])
            sphere(15);

        // Snout extension
        translate([0, 32, 22])
            scale([0.6, 0.4, 0.3])
            sphere(12);
    }
}

module ears() {
    color(color_body)
    union() {
        // Left ear
        translate([-12, 15, 35])
            rotate([0, -20, -30])
            scale([0.4, 0.2, 1.2])
            cylinder(15, 5, 3);

        // Right ear
        translate([12, 15, 35])
            rotate([0, -20, 30])
            scale([0.4, 0.2, 1.2])
            cylinder(15, 5, 3);

        // Ear tips (dark gray)
        color(color_accents) {
            translate([-12, 15, 48])
                sphere(3);
            translate([12, 15, 48])
                sphere(3);
        }
    }
}

module tail() {
    color(color_body)
    union() {
        // Main tail base
        translate([0, -28, 18])
            rotate([0, 0, 0])
            scale([0.8, 0.3, 0.3])
            cylinder(20, 8, 5);

        // Tail curl
        translate([0, -38, 15])
            rotate([0, 0, 0])
            scale([0.9, 0.4, 0.4])
            sphere(12);

        // Tail stripes
        color(color_accents)
        for(i = [0:2]) {
            translate([0, -25 - i*5, 18])
                scale([0.85, 0.35, 0.35])
                cylinder(3, 7.5, 4.5);
        }
    }
}

module legs() {
    color(color_body)
    union() {
        // Front legs
        for(side = [-1, 1]) {
            translate([side * 12, 15, 8])
                rotate([0, side * 10, 0])
                scale([0.4, 0.4, 1])
                cylinder(12, 4, 3);

            // Paws
            translate([side * 12, 15, 0])
                sphere(4);
        }

        // Back legs
        for(side = [-1, 1]) {
            translate([side * 15, -18, 8])
                rotate([0, side * 15, 0])
                scale([0.5, 0.5, 1.1])
                cylinder(14, 5, 4);

            // Back paws
            translate([side * 15, -18, 0])
                sphere(5);
        }
    }
}

module face_features() {
    union() {
        // Eyes
        color(color_eyes)
        for(side = [-1, 1]) {
            translate([side * 6, 30, 28])
                scale([1, 1.2, 0.5])
                sphere(4);

            // Eye highlights
            color([1, 1, 1])
            translate([side * 5.5, 31, 30])
                sphere(1);
        }

        // Nose
        color(color_accents)
        translate([0, 36, 24])
            sphere(2);

        // Mouth
        color(color_mouth)
        translate([0, 35, 20])
            scale([1.5, 0.5, 0.5])
            sphere(2);

        // Whiskers
        color(color_accents)
        for(side = [-1, 1]) {
            for(i = [0:2]) {
                rotate([0, 0, side * (20 + i * 10)])
                translate([side * 8, 32, 22])
                    cylinder(12, 0.5, 0.5);
            }
        }
    }
}

module body_markings() {
    color(color_accents)
    union() {
        // Chest pattern
        translate([0, 10, 18])
            scale([0.8, 0.6, 0.1])
            sphere(12);

        // Side stripes
        for(side = [-1, 1]) {
            for(i = [0:1]) {
                translate([side * 18, -5 + i * 10, 15])
                    scale([0.3, 1.5, 0.2])
                    sphere(8);
            }
        }
    }
}

// Assembly
scale([scale_factor, scale_factor, scale_factor]) {
    body_main();
    head();
    ears();
    tail();
    legs();
    face_features();
    body_markings();
}

// Chingling - Realistic Style, Medium Complexity
// 3D printable model with proper wall thickness and no overhangs

// Overall scale
scale = 1.5;

// Main colors
color_yellow = [1, 0.8, 0];
color_red = [0.8, 0.2, 0.2];
color_black = [0.1, 0.1, 0.1];
color_white = [0.9, 0.9, 0.9];

// Main body module
module chingling_body() {
    color(color_yellow)
    union() {
        // Main spherical body
        sphere(15 * scale);

        // Bottom flattened
        translate([0, 0, -15 * scale])
        cylinder(h=5 * scale, r1=15 * scale, r2=12 * scale, $fn=32);
    }
}

// Ear module
module chingling_ear(side) {
    color(color_yellow)
    translate([side * 12 * scale, 0, 10 * scale]) {
        rotate([0, side * 15, 0])
        union() {
            // Main ear shape
            scale([1, 0.3, 2.5]) sphere(8 * scale);

            // Red marking
            color(color_red)
            translate([0, 2.5 * scale, 3 * scale])
            scale([0.6, 0.2, 0.8])
            sphere(8 * scale);

            // Black line
            color(color_black)
            translate([0, 3 * scale, -3 * scale])
            scale([0.8, 0.1, 0.05])
            cube([12 * scale, 1 * scale, 0.5 * scale]);
        }
    }
}

// Eye module
module chingling_eye(side) {
    color(color_black)
    translate([side * 6 * scale, -10 * scale, 5 * scale]) {
        // Eye socket
        difference() {
            sphere(4 * scale);
            translate([0, 1 * scale, 1 * scale])
            sphere(3 * scale);
        }

        // Eye highlight
        color(color_white)
        translate([1 * scale, 1 * scale, 2 * scale])
        sphere(1 * scale);
    }
}

// Mouth module
module chingling_mouth() {
    color(color_black)
    translate([0, -12 * scale, -5 * scale])
    rotate([0, 0, 0])
    scale([1, 0.3, 0.5])
    sphere(3 * scale);
}

// Ball appendage
module chingling_ball() {
    color(color_red)
    translate([0, 0, -20 * scale]) {
        // Main ball
        sphere(8 * scale);

        // Ring
        color(color_yellow)
        difference() {
            cylinder(h=3 * scale, r1=10 * scale, r2=10 * scale, $fn=32);
            cylinder(h=4 * scale, r1=8 * scale, r2=8 * scale, $fn=32);
        }

        // Connection to body
        color(color_yellow)
        translate([0, 0, 8 * scale])
        cylinder(h=2 * scale, r1=4 * scale, r2=6 * scale, $fn=16);
    }
}

// Assembly
chingling_body();

// Ears
chingling_ear(1);
chingling_ear(-1);

// Eyes
chingling_eye(1);
chingling_eye(-1);

// Mouth
chingling_mouth();

// Ball
chingling_ball();

// Stunky Pokemon - Realistic Medium Complexity
// Overall dimensions: ~80mm length, ~60mm height, ~50mm width

// Color definitions
color_purple = [0.5, 0.2, 0.6];
color_dark_purple = [0.3, 0.1, 0.4];
color_magenta = [0.7, 0.2, 0.5];
color_white = [0.9, 0.9, 0.9];
color_yellow = [0.9, 0.8, 0.2];
color_black = [0.1, 0.1, 0.1];

// Main module
module stunky() {
    // Body
    color(color_purple) {
        // Main torso
        translate([0, 0, 15]) {
            difference() {
                scale([1.2, 0.8, 1]) sphere(d=40);
                translate([0, 0, -20]) cube([80, 80, 40], center=true);
            }
        }

        // Back
        translate([5, 0, 20]) {
            scale([1.3, 1, 0.6]) sphere(d=35);
        }
    }

    // Head
    color(color_purple) {
        translate([-15, 0, 35]) {
            scale([0.8, 0.9, 0.8]) sphere(d=35);
        }
    }

    // Snout
    color(color_dark_purple) {
        translate([-30, 0, 35]) {
            rotate([0, 0, 0]) cylinder(h=15, d1=12, d2=8);
        }
    }

    // Ears
    module ear(x, y, z, rot) {
        color(color_purple) {
            translate([x, y, z]) {
                rotate([0, 0, rot]) {
                    difference() {
                        scale([0.4, 0.1, 1.2]) sphere(d=15);
                        translate([0, -10, 0]) cube([20, 20, 30], center=true);
                    }
                }
            }
        }
    }

    ear(-15, -15, 45, -30);
    ear(-15, 15, 45, 30);

    // Legs
    module leg(x, y, angle) {
        color(color_purple) {
            translate([x, y, 10]) {
                rotate([0, 0, angle]) {
                    union() {
                        // Upper leg
                        translate([0, 0, 5]) cylinder(h=15, d=8);
                        // Paw
                        translate([0, 0, -5]) {
                            scale([1.2, 1, 0.8]) sphere(d=10);
                        }
                    }
                }
            }
        }
    }

    leg(-5, -20, -10);
    leg(-5, 20, 10);
    leg(20, -15, -15);
    leg(20, 15, 15);

    // Tail
    color(color_purple) {
        translate([25, 0, 25]) {
            rotate([20, 0, 0]) {
                hull() {
                    sphere(d=12);
                    translate([0, 0, 25]) sphere(d=8);
                }
            }
        }
    }

    // Tail tuft
    color(color_magenta) {
        translate([25, 0, 50]) {
            rotate([20, 0, 0]) {
                for(i = [0:45:315]) {
                    rotate([0, 0, i]) {
                        translate([0, 0, 5]) {
                            scale([0.2, 0.1, 0.8]) sphere(d=8);
                        }
                    }
                }
            }
        }
    }

    // Face details
    // Eyes
    module eye(x, y) {
        color(color_white) {
            translate([x, y, 35]) {
                sphere(d=6);
            }
        }
        color(color_black) {
            translate([x-2, y, 37]) {
                sphere(d=3);
            }
        }
        color(color_yellow) {
            translate([x, y, 35]) {
                scale([1.2, 0.8, 0.2]) sphere(d=6);
            }
        }
    }

    eye(-25, -8);
    eye(-25, 8);

    // Nose
    color(color_black) {
        translate([-35, 0, 35]) {
            sphere(d=3);
        }
    }

    // Mouth
    color(color_black) {
        translate([-30, 0, 30]) {
            rotate([90, 0, 0]) {
                cylinder(h=1, d=8);
            }
        }
    }

    // Stripes
    color(color_dark_purple) {
        // Back stripes
        for(i = [1:3]) {
            translate([5 + i*5, 0, 20 + i*3]) {
                rotate([0, 90, 0]) {
                    scale([0.3, 1, 0.1]) cylinder(h=30, d=20);
                }
            }
        }

        // Head stripe
        translate([-20, 0, 45]) {
            rotate([0, 0, 0]) {
                scale([0.8, 0.1, 0.1]) cube([20, 25, 5], center=true);
            }
        }
    }

    // Belly marking
    color(color_white) {
        translate([0, 0, 12]) {
            difference() {
                scale([1, 0.6, 0.3]) sphere(d=35);
                translate([0, 0, -10]) cube([40, 30, 20], center=true);
            }
        }
    }
}

// Create the model
stunky();

// Skuntank Pokemon - Realistic Style, Medium Complexity
// Designed for 3D printing - approximately 80mm in length

// Color definitions
$fn = 32;

// Main body module
module skuntank_body() {
    // Main torso - elongated ellipsoid
    color("purple") {
        scale([1.2, 0.8, 0.9]) sphere(d=30);
    }

    // Thicker rear section
    color("purple") {
        translate([12, 0, 0]) scale([1.4, 1, 1.1]) sphere(d=25);
    }
}

// Head with snout
module skuntank_head() {
    color("purple") {
        // Main head sphere
        translate([-25, 0, 8]) sphere(d=20);

        // Snout extension
        translate([-35, 0, 6]) scale([1.5, 0.7, 0.7]) sphere(d=12);
    }
}

// Ears - two pointed ears
module skuntank_ears() {
    color("purple") {
        // Left ear
        translate([-28, 8, 18]) rotate([0, -20, -30]) cylinder(h=12, d1=6, d2=2);

        // Right ear
        translate([-28, -8, 18]) rotate([0, -20, 30]) cylinder(h=12, d1=6, d2=2);
    }
}

// Tail - split into two parts
module skuntank_tail() {
    color("purple") {
        // Main tail base
        translate([22, 0, 5]) rotate([0, 20, 0]) cylinder(h=20, d1=10, d2=8);

        // Left tail segment
        translate([32, 6, 8]) rotate([0, 10, -15]) cylinder(h=15, d1=8, d2=4);

        // Right tail segment
        translate([32, -6, 8]) rotate([0, 10, 15]) cylinder(h=15, d1=8, d2=4);
    }
}

// Legs - four legs with paws
module skuntank_legs() {
    color("purple") {
        // Front left leg
        translate([-15, 12, -12]) cylinder(h=15, d=6);
        translate([-15, 12, -20]) sphere(d=8);

        // Front right leg
        translate([-15, -12, -12]) cylinder(h=15, d=6);
        translate([-15, -12, -20]) sphere(d=8);

        // Rear left leg
        translate([15, 12, -12]) cylinder(h=15, d=6);
        translate([15, 12, -20]) sphere(d=8);

        // Rear right leg
        translate([15, -12, -12]) cylinder(h=15, d=6);
        translate([15, -12, -20]) sphere(d=8);
    }
}

// Face details
module skuntank_face() {
    // Eyes - yellow with black pupils
    color("yellow") {
        translate([-38, 5, 10]) sphere(d=4);
        translate([-38, -5, 10]) sphere(d=4);
    }

    color("black") {
        translate([-40, 5, 10]) sphere(d=2);
        translate([-40, -5, 10]) sphere(d=2);
    }

    // Nose
    color("black") {
        translate([-42, 0, 8]) sphere(d=3);
    }

