module skuntank_stripes() {
    color("black") {
        // Body stripes
        for(i = [-10, 0, 10]) {
            translate([i, 0, 10]) rotate([90, 0, 0]) cylinder(h=30, d=2);
        }

        // Tail stripes
        translate([30, 0, 8]) rotate([0, 110, 0]) cylinder(h=15, d=1.5);
    }
}

// Assembly
union() {
    skuntank_body();
    skuntank_head();
    skuntank_ears();
    skuntank_tail();
    skuntank_legs();
    skuntank_face();
    skuntank_stripes();
