// Realistic Bronzor Pokemon - Medium Complexity OpenSCAD Model
// 3D printable at ~80mm diameter

// Color definitions
color_bronze = "#B87333";
color_dark_bronze = "#8B5A2B";
color_blue = "#4169E1";
color_black = "#000000";
color_red = "#DC143C";

// Main parameters
diameter = 80;
thickness = 8;
edge_thickness = 3;

// Create the main circular body
module bronzor_body() {
    color(color_bronze) {
        // Main disc
        cylinder(h=thickness, d=diameter, $fn=100);

        // Raised edge ring
        translate([0, 0, thickness-0.1]) {
            cylinder(h=edge_thickness, d=diameter+2, $fn=100);
        }
    }
}

// Create decorative ring pattern around edge
module edge_pattern() {
    for (i = [0:7]) {
        rotate([0, 0, i * 45]) {
            translate([diameter/2 - 4, 0, thickness - 0.5]) {
                color(color_dark_bronze) {
                    cube([8, 3, 1.5], center=true);
                }
            }
        }
    }
}

// Create central eye pattern
module central_eye() {
    // Outer blue ring
    color(color_blue) {
        translate([0, 0, thickness + 0.1]) {
            cylinder(h=1, d=25, $fn=100);
        }
    }

    // Inner black circle
    color(color_black) {
        translate([0, 0, thickness + 0.2]) {
            cylinder(h=1, d=15, $fn=100);
        }
    }

    // Red pupil
    color(color_red) {
        translate([0, 0, thickness + 0.3]) {
            cylinder(h=1, d=8, $fn=100);
        }
    }
}

// Create ancient rune patterns
module rune_markings() {
    // Top rune (cross pattern)
    translate([0, 0, thickness + 0.1]) {
        color(color_dark_bronze) {
            intersection() {
                cylinder(h=1, d=35, $fn=4);
                cylinder(h=1, d=35, $fn=4, rotate=[0, 0, 45]);
            }
        }
    }

    // Side runes
    for (angle = [30, 150, 210, 330]) {
        rotate([0, 0, angle]) {
            translate([diameter/3, 0, thickness + 0.1]) {
                color(color_dark_bronze) {
                    rotate([0, 0, 45]) {
                        cube([8, 2, 1], center=true);
                    }
                }
            }
        }
    }
}

// Create decorative dots around edges
module decorative_dots() {
    for (i = [0:11]) {
        rotate([0, 0, i * 30]) {
            translate([diameter/2 - 8, 0, thickness + 0.1]) {
                color(color_dark_bronze) {
                    sphere(d=2, $fn=20);
                }
            }
        }
    }
}

// Create back details (hidden eye pattern)
module back_details() {
    // Hidden eye on back
    translate([0, 0, -0.1]) {
        color(color_blue) {
            cylinder(h=1, d=15, $fn=100);
        }
    }

    // Concentric rings on back
    for (r = [20:10:40]) {
        translate([0, 0, -0.1]) {
            color(color_dark_bronze) {
                difference() {
                    cylinder(h=0.5, d=r+2, $fn=100);
                    cylinder(h=1, d=r, $fn=100);
                }
            }
        }
    }
}

// Assembly
union() {
    bronzor_body();
    edge_pattern();
    central_eye();
    rune_markings();
    decorative_dots();
    back_details();
}

color("peru") union() {
    // Main body - bronze disc shape
    difference() {
        cylinder(h = 12, r = 25, $fn = 64);
        translate([0, 0, -1]) cylinder(h = 14, r = 20, $fn = 64);
    }

    // Top dome
    translate([0, 0, 12]) sphere(r = 25, $fn = 64);

    // Bottom dome
    translate([0, 0, 0]) mirror([0, 0, 1]) sphere(r = 25, $fn = 64);

    // Eye holes
    for (x = [-12, 12]) {
        translate([x, 0, 8]) {
            rotate([90, 0, 0]) cylinder(h = 10, r1 = 4, r2 = 6, $fn = 32);
            translate([0, 0, -1]) color("slateblue") sphere(r = 3.5);
        }
    }

    // Decorative ring around body
    translate([0, 0, 6]) cylinder(h = 1.5, r = 27, $fn = 128);
    translate([0, 0, 4.5]) cylinder(h = 1.5, r = 27, $fn = 128);

    // Arms/handles on sides
    module handle() {
        rotate([0, 90, 0]) difference() {
            union() {
                cylinder(h = 20, r = 4, $fn = 32);
                translate([0, 0, 20]) sphere(r = 4);
            }
            translate([0, -5, 10]) cube([10, 10, 20], center = true);
        }
    }

    translate([0, 25, 6]) handle();
    translate([0, -25, 6]) mirror([0, 1, 0]) handle();

    // Decorative markings on front
    for (i = [0:2]) {
        rotate([0, 0, i * 120]) {
            translate([0, 18, 6]) cube([3, 8, 0.5], center = true);
            translate([0, -18, 6]) cube([3, 8, 0.5], center = true);
        }
