color_green = [34/255, 139/255, 34/255];
color_beige = [245/255, 222/255, 179/255];
color_black = [20/255, 20/255, 20/255];

module bonsly() {
    // Main body (boulder-like pot shape)
    color(color_brown)
    difference() {
        cylinder(h = 45, r1 = 25, r2 = 20, center = false);
        translate([0, 0, -1])
        cylinder(h = 47, r1 = 20, r2 = 15, center = false);
    }

    // Top rim detail
    color(color_dark_brown)
    translate([0, 0, 44])
    cylinder(h = 3, r1 = 25.5, r2 = 25, center = false);

    // Head (cubic with rounded edges)
    color(color_brown)
    translate([0, 0, 47])
    minkowski() {
        cube([30, 25, 30], center = true);
        sphere(2);
    }

    // Face details - eyes
    color(color_black)
    for(x = [-8, 8]) {
        translate([x, 5, 60])
        sphere(3);
