    translate([0, -2, 54])
    rotate([90, 0, 0])
    cylinder(h = 1, r1 = 4, r2 = 2, center = true);

    // Branch-like antenna (3 branches)
    color(color_dark_brown)
    translate([0, 0, 62])
    cylinder(h = 15, r = 2, center = false);

    for(angle = [0, 120, 240]) {
