        translate([0, 0, 70])
        rotate([0, 0, angle])
        translate([8, 0, 0])
        rotate([0, 90, 0])
        scale([0.3, 0.3, 1])
        cylinder(h = 10, r = 3, center = false);
    }

    // Leaf clusters
    color(color_green)
    for(angle = [0, 120, 240]) {
        translate([0, 0, 70])
        rotate([0, 0, angle])
        translate([10, 0, 5])
        sphere(4);
    }

    // Side branches (arms)
    color(color_brown)
    for(side = [-1, 1]) {
        translate([side * 20, 0, 40])
        rotate([0, 0, side * 30])
        cylinder(h = 12, r = 3, center = false);

        // Hand-like end
        translate([side * 28, 0, 40])
        rotate([0, 0, side * 30])
        sphere(4);
    }

    // Base stabilizers
    color(color_dark_brown)
    for(angle = [45, 135, 225, 315]) {
        rotate([0, 0, angle])
        translate([20, 0, 0])
        cylinder(h = 8, r = 2, center = false);
