color_wing = [0.1, 0.1, 0.1]; // Black
color_face = [0.9, 0.9, 0.9]; // White
color_beak = [0.9, 0.7, 0.1]; // Yellow
color_music = [0.3, 0.6, 0.9]; // Blue notes
color_eye = [0.1, 0.1, 0.1]; // Black

// Main module
module chatot() {
    // Position the whole model
    translate([0, 0, 0]) {

        // Body
        color(color_body)
        union() {
            // Main body sphere
            sphere(d=35, $fn=32);

            // Belly bulge
            translate([0, 0, -5])
            scale([1.2, 1, 0.8])
            sphere(d=30, $fn=32);
        }

        // Face mask
        color(color_face)
        difference() {
            translate([0, 13, 5])
            sphere(d=25, $fn=32);

            // Cut sides for mask shape
            translate([15, 13, 5])
            cube([20, 20, 30], center=true);
            translate([-15, 13, 5])
            cube([20, 20, 30], center=true);
        }

        // Beak
        color(color_beak)
        union() {
            translate([0, 20, 0])
            rotate([90, 0, 0])
            cylinder(h=15, d1=8, d2=12, $fn=16);

            // Lower beak
            translate([0, 20, -3])
            rotate([90, 0, 0])
            cylinder(h=10, d1=12, d2=8, $fn=16);
        }

        // Eyes
        color(color_eye)
        for(side = [-1, 1]) {
            translate([side * 7, 15, 8])
            sphere(d=4, $fn=16);

            // Eye highlight
            color([1, 1, 1])
            translate([side * 7.5, 15.5, 9])
            sphere(d=1.5, $fn=8);
        }

        // Head feathers
        color(color_wing)
        union() {
            // Left crest
            translate([-5, -15, 15])
            rotate([45, -20, 0])
            scale([0.5, 2, 0.3])
            sphere(d=15, $fn=16);

            // Right crest
            translate([5, -15, 15])
            rotate([45, 20, 0])
            scale([0.5, 2, 0.3])
            sphere(d=15, $fn=16);

            // Middle crest
            translate([0, -18, 15])
            rotate([30, 0, 0])
            scale([0.4, 2.5, 0.4])
            sphere(d=15, $fn=16);
        }

        // Wings
        module wing() {
            color(color_wing)
            union() {
                // Main wing
                translate([0, 0, 0])
                scale([1.5, 1, 0.2])
                sphere(d=25, $fn=32);

                // Wing tip
                translate([0, 12, 0])
                rotate([0, 0, 30])
                scale([0.8, 1.2, 0.2])
                sphere(d=15, $fn=16);

                // Wing base
                translate([0, -8, 0])
                scale([0.6, 0.8, 0.3])
                sphere(d=15, $fn=16);
            }
        }

        // Left wing
        translate([-15, 0, 5])
        rotate([0, 0, 30])
        wing();

        // Right wing
        translate([15, 0, 5])
        rotate([0, 0, -30])
        mirror([1, 0, 0])
        wing();

        // Feet
        color(color_beak)
        for(side = [-1, 1]) {
            translate([side * 8, 0, -15])
            union() {
                // Upper leg
                cylinder(h=8, d=4, $fn=12);

                // Lower leg
                translate([0, 0, -8])
                rotate([20, 0, 0])
                cylinder(h=6, d=3, $fn=12);

                // Foot
                translate([0, 3, -13])
                cube([6, 8, 2], center=true);
            }
        }

        // Tail feathers
        color(color_wing)
        union() {
            // Main tail
            translate([0, -15, -5])
            rotate([20, 0, 0])
            scale([0.3, 2, 1])
            sphere(d=20, $fn=16);

            // Side tail feathers
            for(side = [-1, 1]) {
                translate([side * 5, -18, -3])
                rotate([25, side * 15, 0])
                scale([0.2, 1.5, 0.8])
                sphere(d=15, $fn=16);
            }
        }

        // Music note on belly
        color(color_music)
        union() {
            // Note head
            translate([0, 0, -12])
            sphere(d=5, $fn=16);

            // Note stem
            translate([2, 5, -12])
            cylinder(h=8, d=2, $fn=8);

            // Note flag
            translate([2, 11, -8])
            rotate([0, 0, 30])
            scale([0.5, 1, 0.2])
            sphere(d=8, $fn=8);
        }
    }
}

