color_dark_purple = [0.2, 0.1, 0.3];
color_gray = [0.4, 0.4, 0.4];
color_black = [0.1, 0.1, 0.1];
color_white = [0.9, 0.9, 0.9];

module skorupi() {
    union() {
        // Body segments
        translate([0, 0, 5]) color(color_purple) body_segment(20, 15, 8);
        translate([0, 0, 10]) color(color_purple) body_segment(18, 14, 7);
        translate([0, 0, 15]) color(color_purple) body_segment(16, 13, 6);
        translate([0, 0, 20]) color(color_purple) body_segment(14, 12, 5);

        // Head
        translate([0, 0, 0]) color(color_purple) head();

        // Claws
        translate([12, 8, 2]) rotate([0, 0, 30]) color(color_gray) claw();
        translate([-12, 8, 2]) rotate([0, 0, -30]) mirror([1, 0, 0]) color(color_gray) claw();

        // Legs
        for(i = [1:3]) {
            translate([8, 0, i*6]) rotate([0, 0, 45]) color(color_gray) leg();
            translate([-8, 0, i*6]) rotate([0, 0, -45]) mirror([1, 0, 0]) color(color_gray) leg();
        }

        // Tail segments
        translate([0, 0, 25]) color(color_purple) tail_segment([0, 0, 25], [0, 0, 30], 12, 4);
        translate([0, 0, 30]) color(color_purple) tail_segment([0, 0, 30], [0, 0, 35], 8, 3);
        translate([0, 0, 35]) color(color_purple) tail_segment([0, 0, 35], [0, 0, 40], 5, 2);

        // Stinger
        translate([0, 0, 40]) rotate([0, 0, 180]) color(color_gray) cylinder(h=10, r1=3, r2=0);
    }
}

module body_segment(w, l, h) {
    difference() {
        hull() {
            cube([w, l, h], center=true);
            translate([0, 0, h/2]) sphere(d=w*0.8);
        }

        // Texture pattern
        for(i = [-1:1]) {
            for(j = [-1:1]) {
                translate([i*4, j*3, h/2]) rotate([90, 0, 0]) 
                    cylinder(h=0.5, r=1.5);
            }
        }
    }
}

module head() {
    union() {
        // Main head
        difference() {
            hull() {
                sphere(d=22);
                translate([0, 0, -5]) sphere(d=20);
            }

            // Eye sockets
            translate([7, 8, 2]) sphere(d=5);
            translate([-7, 8, 2]) sphere(d=5);
        }

        // Eyes
        translate([7, 8, 2]) color(color_white) sphere(d=3);
        translate([-7, 8, 2]) color(color_white) sphere(d=3);
        translate([7, 8, 3]) color(color_black) sphere(d=1.5);
        translate([-7, 8, 3]) color(color_black) sphere(d=1.5);

        // Jaw
        translate([0, 10, -8]) color(color_dark_purple) 
            cube([12, 8, 6], center=true);

        // Pincers
        translate([10, 0, 0]) rotate([0, 0, 45]) color(color_gray) pincer();
        translate([-10, 0, 0]) rotate([0, 0, -45]) mirror([1, 0, 0]) color(color_gray) pincer();
    }
}

module claw() {
    union() {
        translate([0, 0, 5]) cylinder(h=10, r=2);
        translate([0, 0, 10]) rotate([0, 0, 30]) cylinder(h=8, r=1.5);
        translate([0, 0, 18]) rotate([0, 0, 60]) cylinder(h=6, r=1);
    }
}

module leg() {
    union() {
        cylinder(h=8, r=1.5);
        translate([0, 0, 8]) rotate([0, 0, 30]) cylinder(h=6, r=1);
        translate([0, 0, 14]) rotate([0, 0, 60]) cylinder(h=4, r=0.8);
    }
}

module tail_segment(pos1, pos2, r1, r2) {
    hull() {
        translate(pos1) sphere(r=r1);
        translate(pos2) sphere(r=r2);
    }
}

module pincer() {
    difference() {
        hull() {
            cube([8, 3, 2]);
            translate([8, 0, 0]) cube([3, 2, 1]);
        }

        // Teeth
        for(i = [0:2]) {
            translate([4 + i*2, 1.5, 0]) rotate([0, 90, 0]) 
                cylinder(h=1, r=0.5);
        }
    }
}

