// Main body module
module drapion_body() {
    // Thorax/head section
    translate([0, 0, 20]) {
        color(color_body) {
            // Main carapace
            difference() {
                scale([1.5, 1.2, 1]) sphere(15);
                translate([0, -10, 5]) scale([0.8, 0.8, 0.6]) sphere(15);
            }

            // Front horn
            translate([0, 15, 5]) rotate([0, -20, 0]) 
                cylinder(h=20, r1=4, r2=2);

            // Side horns
            for(angle = [-30, 30]) {
                rotate([0, 0, angle]) translate([12, 10, 8]) 
                    rotate([0, -30, 0]) cylinder(h=15, r1=3, r2=1);
            }
        }
    }

    // Abdomen segments
    for(i = [1:3]) {
        translate([0, -25*i, 15 - i*3]) {
            color(color_body) scale([1.4 - i*0.2, 1, 0.8]) 
                sphere(12 - i*2);
        }
    }
}

// Leg module
module drapion_leg(length, angle, z_offset) {
    color(color_body) {
        // Coxa
        rotate([0, 0, angle]) translate([18, 5, z_offset]) 
            rotate([0, 45, 0]) cylinder(h=8, r=3);

        // Femur
        translate([18 + cos(angle)*8, 5 + sin(angle)*8, z_offset + 5])
            rotate([0, 45 + angle, 0]) cylinder(h=length/2, r=2.5);

        // Tibia
        translate([18 + cos(angle)*(8 + length/2*cos(45)), 
                  5 + sin(angle)*(8 + length/2*cos(45)), 
                  z_offset + 5 + length/2*sin(45)])
            rotate([0, 90, 0]) cylinder(h=length/2, r=2);

        // Tarsus/claw
        translate([18 + cos(angle)*(8 + length*cos(45)), 
                  5 + sin(angle)*(8 + length*cos(45)), 
                  z_offset + 5 + length*sin(45)])
            rotate([0, 120, 0]) cylinder(h=5, r1=2, r2=0);
    }
}

// Claw module
module drapion_claw(side) {
    translate([side*15, 25, 15]) {
        color(color_accents) {
            // Base
            scale([1.5, 1, 1]) sphere(8);

            // Upper claw
            rotate([0, -30, side*20]) 
                translate([0, 0, 5]) cylinder(h=20, r1=6, r2=3);

            // Lower claw
            rotate([0, 30, side*-20]) 
                translate([0, 0, -5]) cylinder(h=18, r1=5, r2=2);
        }
    }
}

// Eye module
module drapion_eye() {
    color(color_eyes) {
        translate([5, 15, 20]) sphere(3);
        translate([-5, 15, 20]) sphere(3);
    }
}

// Teeth module
module drapion_teeth() {
    color(color_teeth) {
        for(i = [-2:2]) {
            translate([i*3, 18, 15]) rotate([0, -90, 0]) 
                cylinder(h=3, r1=1.5, r2=0);
        }
    }
}

// Tail stinger
module drapion_tail() {
    color(color_body) {
        translate([0, -80, 12]) {
            // Tail segments
            for(i = [0:3]) {
                translate([0, -i*8, -i*2]) 
                    scale([1 - i*0.2, 1 - i*0.2, 1 - i*0.1]) 
                    sphere(8 - i);
            }

            // Stinger
            translate([0, -32, -8]) rotate([0, 30, 0])
                cylinder(h=12, r1=3, r2=0);
        }
    }
}

// Assemble the Drapion
union() {
    drapion_body();

    // Legs - 4 pairs
    for(i = [0:3]) {
        drapion_leg(20 + i*3, 45 + i*15, 20 - i*5);
        drapion_leg(20 + i*3, -45 - i*15, 20 - i*5);
    }

    // Claws
    drapion_claw(1);
    drapion_claw(-1);

    // Eyes
    drapion_eye();

    // Teeth
    drapion_teeth();

    // Tail
    drapion_tail();
}

