color_blue = [70/255, 130/255, 180/255];
color_red = [220/255, 20/255, 60/255];
color_yellow = [255/255, 215/255, 0/255];

// Main body module
module magnezone_body() {
    color(color_silver) {
        // Central sphere body
        sphere(18, $fn=64);

        // Top dome
        translate([0,0,15])
            scale([1,1,0.5])
                sphere(10, $fn=64);

        // Bottom dome
        translate([0,0,-15])
            scale([1,1,0.5])
                sphere(10, $fn=64);
    }
}

// Eye module
module eye() {
    color(color_red) {
        // Eye sphere
        sphere(5, $fn=32);

        // Pupil
        color([0,0,0]) {
            translate([2,0,0])
                sphere(2, $fn=16);
        }
    }
}

// Magnet module
module magnet() {
    color(color_silver) {
        // Main cylinder
        cylinder(h=25, r1=6, r2=6, $fn=32);

        // Front cap
        translate([0,0,12.5])
            cylinder(h=5, r1=8, r2=6, $fn=32);

        // Back cap
        translate([0,0,-12.5])
            cylinder(h=5, r1=6, r2=8, $fn=32);
    }

    // Red/blue tips
    color(color_blue) {
        translate([0,0,17.5])
            sphere(4, $fn=16);
    }
    color(color_red) {
        translate([0,0,-17.5])
            sphere(4, $fn=16);
    }
}

// Side unit module
module side_unit() {
    color(color_silver) {
        // Main body sphere
        sphere(12, $fn=64);

        // Eye socket
        translate([10,0,0])
            difference() {
                sphere(8, $fn=32);
                translate([5,0,0])
                    sphere(8.5, $fn=32);
            }
    }

    // Eye placement
    translate([10,0,0])
        rotate([0,-90,0])
            eye();

    // Yellow marking
    color(color_yellow) {
        translate([0,0,-8])
            scale([1.5,0.3,0.1])
                sphere(10, $fn=32);
    }
}

// Antenna module
module antenna() {
    color(color_silver) {
        // Main pole
        cylinder(h=15, r1=1, r2=2, $fn=16);

        // Top sphere
        translate([0,0,15])
            sphere(2, $fn=16);

        // Side arms
        for(i=[-1,1]) {
            rotate([0,0,i*45])
                translate([0,10,5])
                    scale([1,0.5,0.5])
                        sphere(3, $fn=16);
        }
    }
}

// Main assembly
union() {
    // Central body
    magnezone_body();

    // Eyes
    translate([13,0,5])
        rotate([0,-30,0])
            eye();
    translate([-13,0,5])
        rotate([0,30,0])
            eye();

    // Magnets (3 arranged at 120 degrees)
    for(i=[0:120:240]) {
        rotate([0,0,i])
            translate([30,0,0])
                rotate([0,90,0])
                    magnet();
    }

    // Side units
    for(i=[0:120:240]) {
        rotate([0,0,i])
            translate([25,0,-10])
                side_unit();
    }

    // Antenna on top
    translate([0,0,25])
        antenna();
}

// Lickilicky - Realistic Style, Medium Complexity
// Overall dimensions: ~80mm height

$fn = 32;

module body() {
    color([0.8, 0.75, 0.7]) {
        // Main body sphere
        sphere(d = 60, center = true);
    }
}

module belly() {
    color([0.9, 0.85, 0.8]) {
        // Belly oval
        scale([1.3, 1.1, 1.1])
            sphere(d = 50);
    }
}

module head() {
    color([0.8, 0.75, 0.7]) {
        // Head
        translate([0, 28, 20])
            sphere(d = 35);

        // Snout
        translate([0, 40, 15])
            scale([1.2, 1.5, 1])
                sphere(d = 20);
    }
}

module ears() {
    // Left ear
    color([0.8, 0.75, 0.7]) {
        translate([-18, 15, 35]) {
            rotate([0, 20, -30])
                cylinder(h = 20, d1 = 12, d2 = 8);
            translate([0, 0, 20])
                sphere(d = 12);
        }
    }

    // Right ear (mirrored)
    mirror([1, 0, 0])
        ears();
}

module arms() {
    // Left arm
    color([0.8, 0.75, 0.7]) {
        translate([-30, 5, -5]) {
            rotate([0, 0, 90])
                cylinder(h = 25, d = 12);
            translate([25, 0, 0])
                sphere(d = 15);
        }
    }

    // Right arm (mirrored)
    mirror([1, 0, 0])
        arms();
}

module legs() {
    // Left leg
    color([0.8, 0.75, 0.7]) {
        translate([-18, -15, -25]) {
            cylinder(h = 20, d = 15);
            translate([0, 0, -20])
                sphere(d = 18);
        }
    }

    // Right leg (mirrored)
    mirror([1, 0, 0])
        legs();
}

module tail() {
    color([0.8, 0.75, 0.7]) {
        translate([0, -35, -15]) {
            // Tail curl segments
            for(i = [0:3]) {
                rotate([0, 0, i * 30])
                    translate([0, -8 - i * 5, 0])
                        sphere(d = 12 - i * 2);
            }
        }
    }
}

module tongue() {
    color([0.9, 0.3, 0.4]) {
        // Tongue base
        translate([0, 45, 8])
            rotate([0, 90, 0])
                cylinder(h = 40, d1 = 8, d2 = 4);

        // Tongue tip
        translate([0, 85, 8])
            sphere(d = 6);
    }
}

module eyes() {
    // Left eye
    color([0, 0, 0]) {
        translate([-8, 32, 25])
            sphere(d = 4);
    }

    // Right eye (mirrored)
    mirror([1, 0, 0])
        eyes();
}

module markings() {
    // Back markings
    color([0.6, 0.55, 0.5]) {
        translate([0, -10, 25])
            scale([1.5, 0.8, 0.3])
                sphere(d = 30);

        translate([0, -25, 15])
            scale([2, 0.8, 0.3])
                sphere(d = 25);
    }
}

// Assembly
union() {
    body();
    belly();
    head();
    ears();
    arms();
    legs();
    tail();
    tongue();
    eyes();
    markings();
}

// Rhyperior - Realistic Style, Medium Complexity
// Designed for 3D printing - approximately 75mm tall

$fn = 32;

// Main colors
color_orange = [0.9, 0.4, 0.1];
color_dark_gray = [0.3, 0.3, 0.3];
color_light_gray = [0.6, 0.6, 0.6];
color_yellow = [0.9, 0.8, 0.1];
color_black = [0.1, 0.1, 0.1];
color_white = [0.9, 0.9, 0.9];

// Body scale factor
body_scale = 0.8;

module rhyperior() {
    // Main body
    color(color_orange)
    translate([0, 0, 10])
    scale([body_scale, body_scale, body_scale])
    union() {
        // Torso
        difference() {
            sphere(15);
            translate([0, 0, -10])
            cube([30, 30, 20], center = true);
        }

        // Chest plate
        color(color_dark_gray)
        translate([0, -14, 5])
        rotate([90, 0, 0])
        cylinder(h = 3, r1 = 12, r2 = 8);

        // Back spikes
        for(i = [0:2]) {
            color(color_dark_gray)
            rotate([0, 0, i * 120])
            translate([0, 12, 5])
            rotate([20, 0, 0])
            cylinder(h = 8, r1 = 3, r2 = 1);
        }
    }

    // Head
    color(color_orange)
    translate([0, -20, 35])
    scale([0.9, 0.9, 0.9])
    union() {
        // Main head shape
        sphere(12);

        // Snout
        color(color_orange)
        translate([0, -12, -2])
        rotate([20, 0, 0])
        cylinder(h = 12, r1 = 8, r2 = 6);

        // Horns
        color(color_dark_gray)
        translate([-8, -5, 8])
        rotate([0, -20, -20])
        cylinder(h = 10, r1 = 3, r2 = 1);

        color(color_dark_gray)
        translate([8, -5, 8])
        rotate([0, -20, 20])
        cylinder(h = 10, r1 = 3, r2 = 1);

        // Eyes
        color(color_yellow)
        translate([-5, -8, 3])
        sphere(2.5);

        color(color_yellow)
        translate([5, -8, 3])
        sphere(2.5);

        // Pupils
        color(color_black)
        translate([-5, -9.5, 3.5])
        sphere(1);

        color(color_black)
        translate([5, -9.5, 3.5])
        sphere(1);

        // Mouth
        color(color_black)
        translate([0, -14, -3])
        rotate([20, 0, 0])
        cylinder(h = 0.5, r = 4);
    }

    // Arms
    // Left arm
    color(color_orange)
    translate([-18, 0, 20])
    rotate([0, 0, -30])
    union() {
        // Upper arm
        cylinder(h = 15, r = 6);

        // Lower arm
        translate([0, 0, 15])
        rotate([0, 25, 0])
        cylinder(h = 12, r = 5);

        // Hand
        color(color_dark_gray)
        translate([0, 0, 27])
        rotate([0, 45, 0])
        sphere(7);

        // Drill
        color(color_light_gray)
        translate([0, 0, 34])
        rotate([0, 45, 0])
        cylinder(h = 10, r1 = 3, r2 = 1);
    }

    // Right arm
    color(color_orange)
    translate([18, 0, 20])
    mirror([1, 0, 0])
    rotate([0, 0, -30])
    union() {
        // Upper arm
        cylinder(h = 15, r = 6);

        // Lower arm
        translate([0, 0, 15])
        rotate([0, 25, 0])
        cylinder(h = 12, r = 5);

        // Hand
        color(color_dark_gray)
        translate([0, 0, 27])
        rotate([0, 45, 0])
        sphere(7);

        // Drill
        color(color_light_gray)
        translate([0, 0, 34])
        rotate([0, 45, 0])
        cylinder(h = 10, r1 = 3, r2 = 1);
    }

    // Legs
    // Left leg
    color(color_orange)
    translate([-10, 10, 0])
    union() {
        // Thigh
        cylinder(h = 15, r = 7);

        // Lower leg
        translate([0, 0, 15])
        cylinder(h = 12, r = 6);

        // Foot
        color(color_dark_gray)
        translate([0, 5, 27])
        cube([12, 18, 4], center = true);
    }

    // Right leg
    color(color_orange)
    translate([10, 10, 0])
    union() {
        // Thigh
        cylinder(h = 15, r = 7);

        // Lower leg
        translate([0, 0, 15])
        cylinder(h = 12, r = 6);

        // Foot
        color(color_dark_gray)
        translate([0, 5, 27])
        cube([12, 18, 4], center = true);
    }

    // Tail
    color(color_orange)
    translate([0, 18, 15])
    rotate([30, 0, 0])
    union() {
        cylinder(h = 20, r1 = 6, r2 = 4);

        // Tail club
        color(color_dark_gray)
        translate([0, 0, 20])
        sphere(8);

        // Tail spikes
        for(i = [0:3]) {
            color(color_dark_gray)
            rotate([0, 0, i * 90])
            translate([0, 6, 18])
            rotate([20, 0, 0])
            cylinder(h = 5, r1 = 2, r2 = 1);
        }
    }

    // Shoulder guards
    color(color_dark_gray)
    translate([-18, 0, 25])
    rotate([0, 90, 0])
    cylinder(h = 8, r1 = 8, r2 = 6);

    color(color_dark_gray)
    translate([18, 0, 25])
    rotate([0, 90, 0])
    cylinder(h = 8, r1 = 8, r2 = 6);
}

// Render the model
rhyperior();

// Tangrowth - Realistic Style, Medium Complexity
// 3D printable Pokemon model

// Colors
body_color = [34/255,139/255,34/255];  // Dark green
vine_color = [0/255,100/255,0/255];    // Forest green
eye_color = [220/255,220/255,220/255]; // Light gray
mouth_color = [139/255,69/255,19/255]; // Brown

// Main module
module tangrowth() {
    // Body
    color(body_color) {
        // Central body mass
        translate([0,0,10]) {
            sphere(15);
            cylinder(h=20, r1=15, r2=10, center=true);
        }

        // Upper body extension
        translate([0,0,30]) {
            sphere(12);
        }
    }

    // Vines
    color(vine_color) {
        // Left arm vine
        translate([-15,0,20]) {
            rotate([0,0,-30]) {
                vine(20, 8);
            }
        }

        // Right arm vine
        translate([15,0,20]) {
            rotate([0,0,30]) {
                mirror([1,0,0]) {
                    vine(20, 8);
                }
            }
        }

        // Back vines
        for(i=[0:3]) {
            rotate([0,0,i*90]) {
                translate([0,-20,25]) {
                    rotate([20,0,0]) {
                        vine(15, 6);
                    }
                }
            }
        }

        // Head vines
        for(i=[0:4]) {
            rotate([0,0,i*72]) {
                translate([0,0,40]) {
                    rotate([30,0,0]) {
                        vine(12, 5);
                    }
                }
            }
        }
    }

    // Face
    color(eye_color) {
        // Eyes
        translate([-5,8,35]) {
            sphere(2);
        }
        translate([5,8,35]) {
            sphere(2);
        }

        // Mouth
        color(mouth_color) {
            translate([0,12,30]) {
                rotate([90,0,0]) {
                    cylinder(h=1, r1=3, r2=2);
                }
            }
        }
    }

    // Feet
    color(body_color) {
        translate([-8,-5,0]) {
            sphere(8);
            cylinder(h=10, r=6, center=true);
        }
        translate([8,-5,0]) {
            sphere(8);
            cylinder(h=10, r=6, center=true);
        }
    }
}

// Vine module with leaves
module vine(length, thickness) {
    union() {
        // Main vine
        cylinder(h=length, r=thickness/2, center=false);

        // Leaves along vine
        for(i=[1:3:length]) {
            translate([0,0,i]) {
                rotate([90,0,0]) {
                    // Leaf shape
                    scale([1,2,0.5]) {
                        sphere(thickness);
                    }
                }
            }
        }

        // End bulb
        translate([0,0,length]) {
            sphere(thickness);
        }
    }
}

// Generate model
scale(1.5) tangrowth();

// Electivire - Realistic Style, Medium Complexity
// Overall height ~80mm, 3D printable

// Color definitions
color_body = [0.2, 0.2, 0.2];
color_yellow = [1, 0.8, 0];
color_red = [0.8, 0, 0];
color_black = [0.1, 0.1, 0.1];
color_white = [1, 1, 1];

// Main body module
module electivire() {
    // Body
    color(color_body)
    translate([0, 0, 30])
    scale([1, 0.8, 1.2])
    sphere(d=30, $fn=32);

    // Chest pattern
    color(color_yellow)
    translate([0, -11, 20])
    rotate([90, 0, 0])
    cylinder(h=3, d1=20, d2=15, $fn=6);

    // Head
    color(color_body)
    translate([0, 0, 55])
    scale([0.9, 0.8, 1])
    sphere(d=25, $fn=32);

    // Muzzle
    color(color_body)
    translate([0, -10, 50])
    rotate([30, 0, 0])
    cylinder(h=10, d1=12, d2=8, $fn=16);

    // Eyes
    color(color_red)
    for(x = [-5, 5])
    translate([x, -8, 58])
    rotate([20, 0, 0])
    scale([1, 1.2, 0.5])
    sphere(d=4, $fn=16);

    // Eye pupils
    color(color_black)
    for(x = [-5, 5])
    translate([x, -8.5, 58.5])
    rotate([20, 0, 0])
    scale([0.5, 0.8, 0.3])
    sphere(d=3, $fn=8);

    // Ears
    module ear(x) {
        color(color_body)
        translate([x, 5, 65])
        rotate([0, 40 * (x > 0 ? 1 : -1), 0])
        scale([0.8, 0.3, 2])
        sphere(d=8, $fn=16);

        color(color_yellow)
        translate([x * 1.2, 4, 65])
        rotate([0, 40 * (x > 0 ? 1 : -1), 0])
        scale([0.5, 0.2, 1.5])
        sphere(d=6, $fn=8);
    }

    ear(8);
    ear(-8);

    // Arms
    module arm(x) {
        // Upper arm
        color(color_body)
        translate([x * 12, 0, 45])
        rotate([0, 0, 30 * (x > 0 ? 1 : -1)])
        cylinder(h=15, d=8, $fn=16);

        // Lower arm
        color(color_body)
        translate([x * 22, -5, 35])
        rotate([0, 0, 60 * (x > 0 ? 1 : -1)])
        cylinder(h=12, d=7, $fn=16);

        // Hand
        color(color_body)
        translate([x * 30, -8, 30])
        sphere(d=10, $fn=16);

        // Cable on arm
        color(color_yellow)
        translate([x * 18, 0, 40])
        rotate([90, 0, 0])
        torus(2, 6, 8);
    }

    arm(1);
    arm(-1);

    // Legs
    module leg(x) {
        color(color_body)
        translate([x * 8, 0, 15])
        cylinder(h=20, d=10, $fn=16);

        color(color_body)
        translate([x * 8, 0, 5])
        sphere(d=12, $fn=16);
    }

    leg(5);
    leg(-5);

    // Tail
    color(color_body)
    translate([0, 12, 35])
    rotate([0, 20, 0])
    scale([1, 0.8, 3])
    sphere(d=8, $fn=16);

    // Tail cables
    color(color_yellow)
    translate([0, 18, 30])
    rotate([0, 20, 0])
    cylinder(h=15, d=3, $fn=8);

    color(color_yellow)
    translate([2, 18, 30])
    rotate([0, 20, 5])
    cylinder(h=15, d=2, $fn=8);

    color(color_yellow)
    translate([-2, 18, 30])
    rotate([0, 20, -5])
    cylinder(h=15, d=2, $fn=8);

    // Back pattern
    color(color_yellow)
    translate([0, 10, 45])
    rotate([90, 0, 0])
    cylinder(h=2, d1=15, d2=10, $fn=6);

    // Mouth
    color(color_black)
    translate([0, -13, 48])
    rotate([30, 0, 0])
    scale([3, 1, 0.5])
    sphere(d=3, $fn=16);
}

// Helper module for torus shape
module torus(r1, r2, fn) {
    rotate_extrude($fn=fn)
    translate([r2, 0, 0])
    circle(r=r1, $fn=fn);
}

// Render the model
electivire();

