// Togekiss OpenSCAD Model - Realistic Style, Medium Complexity

// Parameters for sizing
body_r = 20;
head_r = 15;
wing_w = 25;
wing_h = 20;
tail_len = 30;
scale = 2;

// Main colors
color_body = [1, 0.95, 0.9];      // Off-white
color_accents = [0.95, 0.2, 0.4]; // Red triangles
color_details = [0.1, 0.1, 0.1];  // Black eyes

module togekiss() {
    // Main body - egg shaped
    color(color_body) {
        scale([1, 1.2, 1.3]) sphere(r=body_r, $fn=50);
    }

    // Head section
    translate([0, 0, body_r*1.2]) {
        color(color_body) {
            sphere(r=head_r, $fn=40);
        }

        // Eyes
        for(side = [-1, 1]) {
            translate([side * head_r * 0.5, -head_r * 0.7, head_r * 0.3]) {
                color(color_details) {
                    scale([1.2, 1, 1]) sphere(r=3, $fn=20);
                }
            }
        }

        // Beak
        translate([0, -head_r * 0.9, -head_r * 0.2]) {
            rotate([30, 0, 0]) {
                color([0.9, 0.7, 0.5]) {
                    cylinder(h=5, r1=3, r2=1, $fn=8);
                }
            }
        }
    }

    // Wings
    for(side = [-1, 1]) {
        translate([side * body_r * 0.8, 0, body_r * 0.5]) {
            rotate([0, 0, side * 20]) {
                rotate([0, 45, 0]) {
                    color(color_body) {
                        scale([1, 0.3, 1]) sphere(r=wing_w, $fn=20);
                    }

                    // Wing triangles pattern
                    for(i = [0:2]) {
                        translate([i * 5 - 5, -wing_w * 0.3, wing_h * 0.2]) {
                            rotate([90, 0, 90]) {
                                color(color_accents) {
                                    scale([0.3, 0.3, 1]) cylinder(h=8, r1=4, r2=0, $fn=3);
                                }
                            }
                        }
                    }
                }
            }
        }
    }

    // Triangular tail pattern
    for(i = [0:2]) {
        angle = i * 120;
        rotate([0, 0, angle]) {
            translate([0, -body_r * 1.5, -body_r * 0.5]) {
                color(color_accents) {
                    rotate([0, 0, 180]) {
                        scale([1, 1, 0.5]) cylinder(h=tail_len, r1=10, r2=0, $fn=3);
                    }
                }
            }
        }
    }

    // Feet
    for(side = [-1, 1]) {
        translate([side * body_r * 0.4, body_r * 0.8, -body_r * 1.2]) {
            color(color_body) {
                scale([0.5, 0.8, 2]) sphere(r=3, $fn=10);
            }
        }
    }
}

// Main assembly
scale([scale, scale, scale]) {
    togekiss();
}

// Yanmega Pokemon - Realistic Style, Medium Complexity
// Approx 80mm wingspan, 65mm body length

// Colors
color_body = [0.1, 0.3, 0.2];      // Dark green
color_wing = [0.7, 0.8, 0.9];      // Light blue-gray
color_eye = [0.9, 0.9, 0.1];       // Yellow
color_detail = [0.8, 0.2, 0.2];    // Red
color_black = [0.1, 0.1, 0.1];

// Main body module
module yanmega_body() {
    color(color_body)
    union() {
        // Main thorax segment
        translate([0, 0, 10])
            scale([1.2, 0.8, 1])
            sphere(d=25, $fn=32);

        // Abdomen
        translate([0, -20, 5])
            scale([0.8, 1.5, 0.8])
            sphere(d=30, $fn=32);

        // Head segment
        translate([0, 15, 15])
            scale([1.1, 0.7, 0.8])
            sphere(d=20, $fn=32);
    }
}

// Wing module
module wing(left_right = 1) {
    color(color_wing)
    mirror([left_right, 0, 0])
    union() {
        // Main wing blade
        translate([0, 0, 30])
            rotate([0, 0, -30])
            linear_extrude(height=1.5)
                polygon(points=[
                    [0, 0], [35, -15], [25, 5], [30, 25], [15, 35], [5, 30], [0, 20]
                ]);

        // Secondary wing
        translate([5, 10, 29])
            rotate([0, 0, -45])
            linear_extrude(height=1.2)
                polygon(points=[
                    [0, 0], [20, -8], [15, 5], [18, 15], [8, 20], [2, 15]
                ]);
    }
}

// Eye module
module eye() {
    color(color_eye)
    union() {
        // Eye spheres
        translate([8, 18, 20])
            sphere(d=6, $fn=16);
        translate([-8, 18, 20])
            sphere(d=6, $fn=16);

        // Eye stalks
        translate([8, 18, 20])
            rotate([0, -30, 0])
            cylinder(h=8, d1=4, d2=6, $fn=16);
        translate([-8, 18, 20])
            rotate([0, -30, 0])
            cylinder(h=8, d1=4, d2=6, $fn=16);
    }

    // Pupils
    color(color_black)
    union() {
        translate([8, 20, 22])
            sphere(d=2.5, $fn=12);
        translate([-8, 20, 22])
            sphere(d=2.5, $fn=12);
    }
}

// Leg modules
module legs() {
    color(color_body)
    for(side = [-1, 1]) {
        // Front legs
        translate([side * 12, 10, 8])
            rotate([0, 90, 0])
            cylinder(h=15, d1=3, d2=2, $fn=8);

        // Middle legs
        translate([side * 15, -5, 8])
            rotate([0, 90, 0])
            cylinder(h=18, d1=3, d2=2, $fn=8);

        // Rear legs
        translate([side * 12, -25, 8])
            rotate([0, 90, 0])
            cylinder(h=20, d1=3, d2=2, $fn=8);
    }
}

// Tail details
module tail_details() {
    color(color_detail)
    for(i = [1:3]) {
        translate([0, -35 + i*8, 8])
            scale([1.2 - i*0.3, 0.3, 0.8 - i*0.2])
            sphere(d=15, $fn=16);
    }
}

// Markings on back
module back_markings() {
    color(color_detail)
    union() {
        // Central stripe
        translate([0, 0, 25])
            scale([0.2, 1.5, 0.1])
            cube([10, 30, 3], center=true);

        // Side markings
        for(side = [-1, 1]) {
            translate([side * 10, -5, 25])
                scale([0.3, 0.8, 0.1])
                cube([8, 15, 3], center=true);
        }
    }
}

// Assembly
union() {
    yanmega_body();
    wing(1);    // Left wing
    wing(-1);   // Right wing
    eye();
    legs();
    tail_details();
    back_markings();
}

// Leafeon OpenSCAD Model - Realistic Style, Medium Complexity
