    color(color_body) {
        // Torso
        translate([0, 0, 20]) {
            scale([1.2, 0.8, 1.5])
                sphere(d=30);
        }

        // Chest
        translate([0, -8, 10]) {
            scale([0.8, 0.5, 1])
                sphere(d=25);
        }

        // Rump
        translate([0, 12, 18]) {
            scale([1, 0.9, 1.1])
                sphere(d=28);
        }
    }
}

// Head module
module head() {
    color(color_body) {
        // Main head
        translate([0, -15, 38]) {
            scale([1.1, 1.3, 1])
                sphere(d=28);
        }

        // Snout
        translate([0, -25, 35]) {
            scale([0.6, 0.8, 0.5])
                sphere(d=20);
        }
    }
}

// Ears module
module ears() {
    for(side = [-1, 1]) {
        color(color_body) {
            translate([side * 12, -18, 48]) {
                rotate([0, side * 15, 0]) {
                    // Ear base
                    cylinder(h=15, d1=8, d2=12);

                    // Ear tip
                    translate([0, 0, 15]) {
                        rotate([0, 20, 0]) {
                            scale([0.3, 1, 1])
                                cylinder(h=12, d1=10, d2=0);
                        }
                    }
                }
            }
        }

        // Inner ear
        color(color_markings) {
            translate([side * 12, -18, 48]) {
                rotate([0, side * 15, 0]) {
                    translate([0, 0, 0.5]) {
                        scale([0.6, 0.8, 0.8])
                            cylinder(h=14, d1=6, d2=8);
                    }
                }
            }
        }
    }
}

// Eyes module
module eyes() {
    for(side = [-1, 1]) {
        color(color_eyes) {
            translate([side * 7, -22, 38]) {
                scale([1, 0.6, 0.5])
                    sphere(d=6);
            }
        }

        // Eye highlight
        color("white") {
            translate([side * 7.5, -21.5, 39]) {
                sphere(d=2);
            }
        }
    }
}

// Nose module
module nose() {
    color(color_nose) {
        translate([0, -28, 34]) {
            sphere(d=3);
        }
    }
}

// Leaf tail
module tail() {
    color(color_accents) {
        translate([0, 22, 25]) {
            rotate([0, 0, 10]) {
                // Tail base
                cylinder(h=25, d1=10, d2=5);

                // Leaf blades
                for(i = [0:3]) {
                    rotate([0, 0, i * 90]) {
                        translate([0, 0, 22]) {
                            rotate([20, 0, 0]) {
                                scale([0.2, 1, 2])
                                    cylinder(h=15, d1=8, d2=0);
                            }
                        }
                    }
                }
            }
        }
    }
}

// Legs module
module legs() {
    for(side = [-1, 1]) {
        // Front legs
        color(color_body) {
            translate([side * 8, -8, 10]) {
                cylinder(h=20, d=8);

                // Paw
                translate([0, 0, -10]) {
                    sphere(d=10);

                    // Toes
                    for(toe = [0:2]) {
                        rotate([0, 0, toe * 60 - 60]) {
                            translate([0, 5, 0]) {
                                sphere(d=4);
                            }
                        }
                    }
                }
            }

            // Back legs
            translate([side * 8, 12, 12]) {
                cylinder(h=22, d=9);

                // Paw
                translate([0, 0, -11]) {
                    sphere(d=11);

                    // Toes
                    for(toe = [0:2]) {
                        rotate([0, 0, toe * 60 - 60]) {
                            translate([0, 5.5, 0]) {
                                sphere(d=4.5);
                            }
                        }
                    }
                }
            }
        }
    }
}

// Leaf details on body
