}

// Glaceon - Realistic Medium Complexity OpenSCAD Model
// Overall height: 80mm

// Colors
color_cyan = [0.2,0.7,0.9];
color_dark_cyan = [0.1,0.5,0.7];
color_light_blue = [0.7,0.9,1];
color_white = [1,1,1];
color_black = [0.1,0.1,0.1];

module glaceon() {
    // Body
    color(color_cyan) {
        union() {
            // Main body
            translate([0,0,25]) scale([1.2,1,1.5]) sphere(r=15, $fn=32);

            // Chest fluff
            color(color_white) {
                translate([0,-14,15]) scale([0.8,0.3,1.2]) sphere(r=12, $fn=24);
            }

            // Legs
            for(x=[-10,10]) {
                // Front legs
                translate([x,-8,5]) rotate([0,15,0]) {
                    color(color_dark_cyan) cylinder(h=20, r1=4, r2=3, $fn=16);
                    translate([0,0,20]) sphere(r=4, $fn=16);
                }

                // Back legs
                translate([x*0.8,12,8]) rotate([0,-15,0]) {
                    color(color_dark_cyan) cylinder(h=22, r1=4.5, r2=3.5, $fn=16);
                    translate([0,0,22]) sphere(r=4.5, $fn=16);
                }
            }
        }
    }

    // Head
    color(color_cyan) {
        translate([0,-25,45]) scale([0.9,0.8,1.1]) sphere(r=12, $fn=32);
    }

    // Ears
    for(x=[-8,8]) {
        color(color_cyan) {
            translate([x,-30,55]) rotate([15,0,x*-5]) {
                difference() {
                    scale([0.4,0.2,1.5]) sphere(r=10, $fn=16);
                    translate([0,0,-5]) cube([20,20,20], center=true);
                }
            }
        }

        // Inner ear
        color(color_light_blue) {
            translate([x,-29,54]) rotate([15,0,x*-5]) {
                scale([0.2,0.1,1.1]) sphere(r=8, $fn=16);
            }
        }
    }

    // Tail
    color(color_cyan) {
        translate([0,15,35]) rotate([30,0,0]) {
            hull() {
                sphere(r=6, $fn=16);
                translate([0,0,20]) scale([0.7,0.5,1.5]) sphere(r=6, $fn=16);
                translate([0,0,40]) scale([0.5,0.3,1.2]) sphere(r=6, $fn=16);
            }
        }
    }

    // Diamond patterns on head
    color(color_light_blue) {
        translate([0,-32,50]) rotate([0,0,45]) cube([4,4,1], center=true);
        translate([0,-34,48]) rotate([0,0,45]) cube([3,3,1], center=true);
    }

    // Eyes
    for(x=[-6,6]) {
        color(color_white) {
            translate([x,-30,47]) scale([1,1.2,0.5]) sphere(r=3, $fn=16);
        }

        color(color_black) {
            translate([x,-31,47.5]) sphere(r=1.5, $fn=8);
        }

        color(color_light_blue) {
            translate([x-1,-30.5,48]) sphere(r=0.5, $fn=8);
        }
    }

    // Nose
    color(color_black) {
        translate([0,-32,45]) sphere(r=1, $fn=8);
    }

    // Mouth
    color(color_black) {
        translate([0,-33,43]) rotate([90,0,0]) cylinder(h=0.5, r1=1.5, r2=1.5, $fn=8);
    }

    // Neck diamond
    color(color_light_blue) {
        translate([0,-18,40]) rotate([0,0,45]) cube([5,5,1.5], center=true);
    }

    // Back diamonds
    for(i=[-1,0,1]) {
        color(color_light_blue) {
            translate([0,i*8+5,35+i*5]) rotate([0,0,45]) cube([4,4,1], center=true);
        }
    }

    // Leg diamonds
    for(x=[-10,10]) {
        color(color_light_blue) {
            translate([x,-5,15]) rotate([0,0,45]) cube([2.5,2.5,0.5], center=true);
            translate([x*0.8,12,18]) rotate([0,0,45]) cube([3,3,0.5], center=true);
        }
    }
}

// Render the model
glaceon();

// Gliscor - Realistic Style, Medium Complexity
// Size: ~75mm wingspan, 3D printable

// Main colors
color_body = "#4A3B31";
color_wings = "#6B5B47";
color_accents = "#8B7355";
color_eyes = "#FF4500";
color_markings = "#E6D3A3";

// Overall scale
scale_factor = 0.4;

// Main body module
module body() {
    color(color_body)
    union() {
        // Torso
        translate([0, 0, 10])
            scale([1.2, 0.8, 1])
            sphere(d=30);

        // Abdomen
        translate([0, -15, 5])
            scale([1, 1.2, 0.8])
            sphere(d=25);

        // Head base
        translate([0, 20, 15])
            scale([0.9, 0.7, 0.8])
            sphere(d=25);
    }
}

// Pincers module
module pincers() {
    color(color_body)
    union() {
        // Left pincer
        translate([-15, 25, 15])
            rotate([0, 0, -30])
            union() {
                cylinder(h=15, d1=8, d2=5);
                translate([0, 7, 0])
                    rotate([90, 0, 0])
                    cylinder(h=12, d=6);
            }

        // Right pincer
        mirror([1, 0, 0])
            translate([-15, 25, 15])
            rotate([0, 0, 30])
            union() {
                cylinder(h=15, d1=8, d2=5);
                translate([0, 7, 0])
                    rotate([90, 0, 0])
                    cylinder(h=12, d=6);
            }
    }
}

// Ears module
module ears() {
    color(color_body)
    union() {
        // Left ear
        translate([-8, 30, 25])
            rotate([0, 30, -20])
            scale([0.5, 2, 0.3])
            sphere(d=10);

        // Right ear
        mirror([1, 0, 0])
            translate([-8, 30, 25])
            rotate([0, 30, 20])
            scale([0.5, 2, 0.3])
            sphere(d=10);
    }
}

// Eyes module
module eyes() {
    color(color_eyes)
    union() {
        // Left eye
        translate([-6, 22, 22])
            rotate([0, 0, 15])
            scale([1, 1.2, 0.5])
            sphere(d=5);

        // Right eye
        mirror([1, 0, 0])
            translate([-6, 22, 22])
            rotate([0, 0, -15])
            scale([1, 1.2, 0.5])
            sphere(d=5);

        // Eye markings
        color(color_markings)
        union() {
            translate([-10, 20, 20])
                cube([2, 8, 1]);
            mirror([1, 0, 0])
                translate([-10, 20, 20])
                cube([2, 8, 1]);
        }
    }
}

// Legs module
module legs() {
    color(color_body)
    union() {
        // Front legs
        for(side = [-1, 1]) {
            translate([side * 12, 10, 5])
                rotate([0, 0, side * 20])
                union() {
                    cylinder(h=15, d=4);
                    translate([0, 0, -8])
                        sphere(d=6);
                }
        }

        // Back legs
        for(side = [-1, 1]) {
            translate([side * 10, -15, 5])
                rotate([0, 0, side * -30])
                union() {
                    cylinder(h=18, d=4);
                    translate([0, 0, -10])
                        sphere(d=6);
                }
        }
    }
}

// Wings module
module wings() {
    color(color_wings)
    union() {
        // Left wing
        translate([-20, 0, 15])
            rotate([0, 0, -30])
            union() {
                // Main wing
                scale([2, 1, 0.1])
                sphere(d=30);

                // Wing segments
                for(i = [1:3]) {
                    translate([-i * 8, i * 3, 0])
                        scale([0.8, 0.5, 0.1])
                        sphere(d=15);
                }
            }

        // Right wing
        mirror([1, 0, 0])
            translate([-20, 0, 15])
            rotate([0, 0, 30])
            union() {
                // Main wing
                scale([2, 1, 0.1])
                sphere(d=30);

                // Wing segments
                for(i = [1:3]) {
                    translate([-i * 8, i * 3, 0])
                        scale([0.8, 0.5, 0.1])
                        sphere(d=15);
                }
            }
    }
}

// Tail module
module tail() {
    color(color_body)
    union() {
        // Main tail
        translate([0, -25, 12])
            rotate([0, 0, 180])
            cylinder(h1=15, h2=0, r1=6, r2=2);

        // Tail segments
        for(i = [1:3]) {
            translate([0, -25 - i * 5, 12 - i * 2])
                rotate([0, 0, 180])
                cylinder(h1=5, h2=0, r1=5 - i, r2=3 - i * 0.5);
        }

        // Tail stinger
        color(color_accents)
        translate([0, -45, 5])
            rotate([0, 0, 180])
            cylinder(h1=8, h2=0, r1=3, r2=0.5);
    }
}

// Body markings
module markings() {
    color(color_markings)
    union() {
        // Back markings
        translate([0, 0, 25])
            scale([1, 1.5, 0.1])
            sphere(d=20);

        // Wing markings
        for(side = [-1, 1]) {
            translate([side * 30, 5, 15])
                scale([0.3, 0.3, 0.1])
                sphere(d=15);
            translate([side * 35, 10, 15])
                scale([0.2, 0.2, 0.1])
                sphere(d=10);
        }
    }
}

// Assembly
scale([scale_factor, scale_factor, scale_factor])
union() {
    body();
    pincers();
    ears();
    eyes();
    legs();
    wings();
    tail();
    markings();
}

// Mamoswine Realistic Style - Medium Complexity
// 3D printable model using basic OpenSCAD primitives

// Main color scheme
body_color = [0.8, 0.7, 0.6];  // Brown fur
tusk_color = [0.9, 0.9, 0.85]; // Ivory tusks
blue_color = [0.2, 0.4, 0.6];  // Blue accents
eye_color = [0.1, 0.1, 0.1];   // Black eyes

// Body dimensions
body_length = 40;
body_height = 25;
body_width = 30;

// Create the main body
module body() {
    color(body_color)
    union() {
        // Main torso
        translate([0, 0, body_height/2])
            scale([1.2, 1, 0.8])
            sphere(d=body_width);

        // Lower body extension
        translate([0, -10, 5])
            cube([body_width*0.8, body_length*0.6, body_height*0.4], center=true);

        // Rear hump
        translate([-body_length/4, -body_length/3, body_height*0.7])
            sphere(d=body_width*0.7);
    }
}

// Head with snout
module head() {
    color(body_color)
    union() {
        // Main head
        translate([0, body_length/2 + 8, body_height*0.8])
            sphere(d=body_width*0.7);

        // Snout
        translate([0, body_length/2 + 18, body_height*0.7])
            scale([0.8, 1.5, 0.6])
            sphere(d=body_width*0.5);

        // Nose
