// Probopass realistic style - OpenSCAD code
// 3D printable model with medium complexity

// Constants
$fn = 32;
body_size = 40;
nose_length = 20;
mustache_count = 3;
hat_height = 15;
eye_radius = 4;
mini_nose_size = 8;

// Main module
module probopass() {
    union() {
        // Main body (blue-gray rock)
        color([0.4, 0.5, 0.6])
        difference() {
            sphere(d = body_size);
            // Eye sockets
            translate([10, 12, 5]) rotate([0, 90, 0]) cylinder(h = 15, r = eye_radius, center = true);
            translate([10, -12, 5]) rotate([0, 90, 0]) cylinder(h = 15, r = eye_radius, center = true);
            // Nose hole
            translate([body_size/2, 0, 0]) rotate([0, 90, 0]) cylinder(h = 25, r = 6, center = true);
        }

        // Eyes (white with black pupils)
        color([1, 1, 1])
        translate([body_size/2 - 2, 12, 5]) sphere(r = eye_radius - 1);
        translate([body_size/2 - 2, -12, 5]) sphere(r = eye_radius - 1);

        color([0, 0, 0])
        translate([body_size/2, 12, 5]) sphere(r = 2);
        translate([body_size/2, -12, 5]) sphere(r = 2);

        // Large red nose
        color([0.8, 0.2, 0.2])
        translate([body_size/2 + nose_length/2, 0, 0]) 
            rotate([0, 90, 0]) cylinder(h = nose_length, r1 = 10, r2 = 8);

        // Mini noses (3 per side)
        for (i = [-1:1]) {
            // Left side mini noses
            color([0.7, 0.3, 0.3])
            translate([-body_size/4 - 5, i * 15, 5 + i * 5]) 
                rotate([0, -30, 0]) cylinder(h = mini_nose_size, r1 = 4, r2 = 3);

            // Right side mini noses
            color([0.7, 0.3, 0.3])
            translate([-body_size/4 - 5, i * 15, -5 - i * 5]) 
                rotate([0, -30, 0]) cylinder(h = mini_nose_size, r1 = 4, r2 = 3);
        }

        // Magnetic mustache (gray steel)
        color([0.5, 0.5, 0.5])
        for (i = [0:mustache_count-1]) {
            angle = i * 30 - 30;
            // Top mustache
            translate([body_size/4, 0, body_size/4]) 
                rotate([0, angle, 0]) 
                translate([0, 0, body_size/4]) 
                cube([body_size/3, 3, 3], center = true);

            // Bottom mustache
            translate([body_size/4, 0, -body_size/4]) 
                rotate([0, -angle, 0]) 
                translate([0, 0, body_size/4]) 
                cube([body_size/3, 3, 3], center = true);
        }

        // Mini noses on mustache tips
        for (i = [0:mustache_count-1]) {
            angle = i * 30 - 30;
            color([0.6, 0.2, 0.2])
            translate([body_size/2 + 5, 0, body_size/3]) 
                rotate([0, angle, 0]) 
                translate([0, 0, body_size/3]) 
                sphere(r = 2);

            translate([body_size/2 + 5, 0, -body_size/3]) 
                rotate([0, -angle, 0]) 
                translate([0, 0, body_size/3]) 
                sphere(r = 2);
        }

        // Steel hat/cap
        color([0.6, 0.6, 0.7])
        difference() {
            translate([0, 0, body_size/2 + hat_height/2]) 
                cylinder(h = hat_height, r1 = body_size/2 - 2, r2 = body_size/2 - 5);
            translate([0, 0, body_size/2 + hat_height]) 
                cylinder(h = 5, r = body_size/2 - 8);
        }

        // Red gem on hat
        color([0.9, 0.1, 0.1])
        translate([0, 0, body_size/2 + hat_height + 2]) 
            sphere(r = 3);

        // Rock texture details
        for (i = [0:5]) {
            color([0.3, 0.4, 0.5])
            rotate([0, 0, i * 60]) 
                translate([body_size/4, 0, 0]) 
                scale([1, 0.8, 0.8]) 
                sphere(d = 8);
        }
    }
}

