        difference() {
            scale([1, 0.8, 1])
            sphere(d=body_height);

            // Chest cavity
            translate([0, -body_width/2, 0])
            cube([body_width/2, body_width, body_height], center=true);
        }

        // Back armor plates
        translate([0, body_width/3, leg_height + 10])
        rotate([30, 0, 0])
        cube([body_width*1.2, body_depth/2, 8], center=true);

        // Side vents
        for(side = [-1, 1]) {
            translate([side * body_width/2, 0, leg_height + 5])
            rotate([0, 90, 0])
            cylinder(h=body_depth/3, d=8);
        }
