    color([0.5, 0.25, 0.1])
    union() {
        // Main head
        translate([0, -body_width/2 - 10, leg_height + body_height/2])
        sphere(d=head_size);

        // Snout
        translate([0, -body_width/2 - 20, leg_height + body_height/2 - 5])
        scale([1.2, 1.5, 0.8])
        sphere(d=15);

