            rotate([0, 0, angle])
            rotate([30, 0, 0])
            cylinder(h=12, d1=8, d2=4);
        }

        // Eyes (lava glow)
        color([1, 0.3, 0])
        for(side = [-1, 1]) {
            translate([side * 8, -body_width/2 - 25, leg_height + body_height/2])
            sphere(d=4);
        }
    }
}

